import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  Container, 
  Card, 
  CardContent, 
  CardActions, 
  Button, 
  Chip, 
  Stack, 
  Skeleton,
  IconButton,
  Tooltip,
  Alert
} from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import FavoriteIcon from '@mui/icons-material/Favorite';
import FavoriteBorderIcon from '@mui/icons-material/FavoriteBorder';
import { getUserFavorites, removeFromFavorites } from '../api';
import { LoginContext } from '../App';

const categoryColors = {
  Roman: '#3a5ba0',
  Klasik: '#e07a5f',
  Psikoloji: '#43aa8b',
  Aşk: '#f76d6d',
  Distopya: '#6c63ff',
  'Bilim Kurgu' : '#ffd166',
  Çocuk: '#f9c74f',
  Tarih: '#b5838d',
  Polisiye: '#22223b',
  Felsefe: '#3d405b',
  Macera: '#f3722c',
  Biyografi: '#277da1',
  Şiir: '#9d4edd',
  Mitoloji: '#ffb4a2',
  Korku: '#720026',
  Mizah: '#f9844a',
  Sanat: '#577590',
  Din: '#b5ead7',
  Bilim: '#00b4d8',
  Eğitim: '#f7b801',
  Sağlık: '#43bccd',
  Spor: '#f94144',
  Gezi: '#90be6d',
  Yemek: '#f8961e',
  Diğer: '#adb5bd',
};

const Favorites = () => {
  const { isLoggedIn } = useContext(LoginContext);
  const [favoriteBooks, setFavoriteBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!isLoggedIn) {
      setError('Favorilerinizi görmek için giriş yapmalısınız.');
      setLoading(false);
      return;
    }

    const fetchFavorites = async () => {
      try {
        const user = JSON.parse(localStorage.getItem('user') || '{}');
        if (!user.id) {
          setError('Kullanıcı bilgileri bulunamadı.');
          setLoading(false);
          return;
        }

        const response = await getUserFavorites(user.id);
        setFavoriteBooks(response.data);
      } catch (error) {
        console.error('Favoriler yüklenirken hata:', error);
        setError('Favorileriniz yüklenirken bir hata oluştu.');
      } finally {
        setLoading(false);
      }
    };

    fetchFavorites();
  }, [isLoggedIn]);

  const handleRemoveFavorite = async (bookId) => {
    try {
      const user = JSON.parse(localStorage.getItem('user') || '{}');
      await removeFromFavorites({ UserID: user.id, BookID: bookId });
      
      // Favoriler listesinden kaldır
      setFavoriteBooks(prev => prev.filter(book => book.id !== bookId));
    } catch (error) {
      console.error('Favori kaldırılırken hata:', error);
      alert('Favori kaldırılırken bir hata oluştu.');
    }
  };

  if (!isLoggedIn) {
    return (
      <Box
        sx={{
          minHeight: '100vh',
          background: "linear-gradient(135deg, rgba(30,40,60,0.85) 0%, rgba(78,205,196,0.15) 100%), url('/books-bg.jpg') center center/cover no-repeat fixed",
          color: '#f7f4ed',
          py: 6,
        }}
      >
        <Container maxWidth="lg">
          <Alert severity="warning" sx={{ mt: 4 }}>
            Favorilerinizi görmek için giriş yapmalısınız.
          </Alert>
        </Container>
      </Box>
    );
  }

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: "linear-gradient(135deg, rgba(30,40,60,0.85) 0%, rgba(78,205,196,0.15) 100%), url('/books-bg.jpg') center center/cover no-repeat fixed",
        color: '#f7f4ed',
        py: 6,
      }}
    >
      <Container maxWidth="lg">
        <Typography 
          variant="h3" 
          align="center" 
          gutterBottom
          sx={{
            background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 6,
            textShadow: '0 4px 20px rgba(0,0,0,0.3)',
          }}
        >
          Favori Kitaplarım
        </Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 4 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 3 }}>
            {[1, 2, 3].map((item) => (
              <Card key={item} sx={{ bgcolor: 'rgba(255,255,255,0.95)', backdropFilter: 'blur(20px)' }}>
                <CardContent>
                  <Skeleton variant="text" width="80%" height={32} />
                  <Skeleton variant="text" width="60%" height={24} />
                  <Skeleton variant="text" width="100%" height={60} />
                </CardContent>
              </Card>
            ))}
          </Box>
        ) : favoriteBooks.length === 0 ? (
          <Box sx={{ textAlign: 'center', py: 8 }}>
            <MenuBookIcon sx={{ fontSize: 80, color: '#666', mb: 2 }} />
            <Typography variant="h5" color="text.secondary" gutterBottom>
              Henüz favori kitabınız yok
            </Typography>
            <Typography variant="body1" color="text.secondary">
              Kitaplar sayfasından beğendiğiniz kitapları favorilere ekleyebilirsiniz.
            </Typography>
          </Box>
        ) : (
          <Box sx={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fill, minmax(300px, 1fr))', gap: 3 }}>
            {favoriteBooks.map(book => (
              <Card key={book.id} sx={{ 
                bgcolor: 'rgba(255,255,255,0.95)', 
                backdropFilter: 'blur(20px)',
                borderRadius: 4,
                boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
                transition: 'transform 0.2s ease-in-out',
                '&:hover': {
                  transform: 'translateY(-4px)',
                }
              }}>
                <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', gap: 1 }}>
                  <Typography variant="h6" gutterBottom sx={{ fontWeight: 700, color: '#333' }}>
                    {book.title}
                  </Typography>
                  
                  <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                    {book.categories && book.categories.map(catName => (
                      <Chip key={catName} label={catName} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                    ))}
                  </Stack>
                  
                  <Typography variant="subtitle2" color="text.secondary" gutterBottom>
                    {book.author}
                  </Typography>
                  
                  <Typography variant="body2" color="text.secondary" sx={{ 
                    overflow: 'hidden',
                    textOverflow: 'ellipsis',
                    display: '-webkit-box',
                    WebkitLineClamp: 3,
                    WebkitBoxOrient: 'vertical',
                  }}>
                    {book.summary}
                  </Typography>
                </CardContent>
                
                <CardActions sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', p: 2 }}>
                  <Button size="small" color="primary" sx={{ fontWeight: 700, borderRadius: 2 }}>
                    Detay
                  </Button>
                  
                  <Tooltip title="Favorilerden çıkar">
                    <IconButton
                      onClick={() => handleRemoveFavorite(book.id)}
                      sx={{
                        color: '#e91e63',
                        '&:hover': {
                          color: '#c2185b',
                        }
                      }}
                    >
                      <FavoriteIcon />
                    </IconButton>
                  </Tooltip>
                </CardActions>
              </Card>
            ))}
          </Box>
        )}
      </Container>
    </Box>
  );
};

export default Favorites;
