import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Paper, MenuItem, Alert } from '@mui/material';

const Register = () => {
  const [userType, setUserType] = useState('member');
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Dummy kayıt işlemi
    setSuccess('Kayıt başarılı! Giriş yapabilirsiniz.');
  };

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '60vh' }}>
      <Paper sx={{ p: 4, minWidth: 320 }}>
        <Typography variant="h5" gutterBottom>Kayıt Ol</Typography>
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
        <form onSubmit={handleSubmit}>
          <TextField
            select
            label="Kullanıcı Türü"
            value={userType}
            onChange={e => setUserType(e.target.value)}
            fullWidth
            margin="normal"
          >
            <MenuItem value="member">Üye</MenuItem>
            <MenuItem value="publisher">Yayınevi</MenuItem>
          </TextField>
          <TextField
            label="Kullanıcı Adı"
            variant="outlined"
            fullWidth
            margin="normal"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />
          <TextField
            label="Şifre"
            type="password"
            variant="outlined"
            fullWidth
            margin="normal"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            Kayıt Ol
          </Button>
        </form>
      </Paper>
    </Box>
  );
};

export default Register;
