import React, { useState } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'Aşk', 'Distopya', 'Bilim Kurgu', 'Çocuk', 'Tarih', 'Polisiye'];

const dummyBooks = [
  { id: 1, title: 'Sefiller', author: 'Victor Hugo', year: 1862, pages: 1463, isbn: '9789750738606', publisher: 'Can Yayınları', language: 'Fransızca', categories: ['Roman', 'Klasik'] },
  { id: 2, title: 'Suç ve Ceza', author: 'Fyodor Dostoyevski', year: 1866, pages: 687, isbn: '9786053609396', publisher: 'İş Bankası Kültür Yayınları', language: 'Rusça', categories: ['Roman', 'Psikoloji'] },
  { id: 3, title: 'Kürk Mantolu Madonna', author: 'Sabahattin Ali', year: 1943, pages: 160, isbn: '9789753638026', publisher: 'Yapı Kredi Yayınları', language: 'Türkçe', categories: ['Roman', 'Aşk'] },
];
const dummyUsers = [
  { id: 1, type: 'Üye', name: 'Ali', surname: 'Veli', username: 'aliveli', email: 'ali@site.com', password: '1234' },
  { id: 2, type: 'Yayınevi', publisherName: 'Can Yayınları', publisherContact: 'Ayşe Yetkili', username: 'canyay', email: 'info@can.com', password: '1234', phone: '0212 123 45 67' },
];

const emptyBook = { title: '', author: '', year: '', pages: '', isbn: '', publisher: '', language: '', categories: [] };
const emptyUser = { type: 'Üye', name: '', surname: '', username: '', email: '', password: '', publisherName: '', publisherContact: '', phone: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState(() => {
    const stored = localStorage.getItem('books');
    return stored ? JSON.parse(stored) : dummyBooks;
  });
  const [users, setUsers] = useState(dummyUsers);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);

  // Kitap ekle/güncelle (localStorage ile kalıcı)
  const handleBookSave = () => {
    let newBooks;
    if (editBook) {
      newBooks = books.map(b => b.id === editBook.id ? { ...editBook, ...bookForm } : b);
    } else {
      newBooks = [...books, { id: Date.now(), ...bookForm }];
    }
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
    setOpenBookDialog(false);
    setEditBook(null);
    setBookForm(emptyBook);
  };
  // Kitap sil (localStorage ile kalıcı)
  const handleBookDelete = (id) => {
    const newBooks = books.filter(b => b.id !== id);
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
  };
  // Kitap düzenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      year: book.year,
      pages: book.pages,
      isbn: book.isbn,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
    });
    setOpenBookDialog(true);
  };

  // Kullanıcı ekle/güncelle
  const handleUserSave = () => {
    if (editUser) {
      setUsers(users.map(u => u.id === editUser.id ? { ...editUser, ...userForm } : u));
    } else {
      setUsers([...users, { id: Date.now(), ...userForm }]);
    }
    setOpenUserDialog(false);
    setEditUser(null);
    setUserForm(emptyUser);
  };
  // Kullanıcı sil
  const handleUserDelete = (id) => {
    setUsers(users.filter(u => u.id !== id));
  };
  // Kullanıcı düzenle
  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({ ...user });
    setOpenUserDialog(true);
  };

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>
        <Tabs value={tab} onChange={(_, v) => setTab(v)} centered sx={{ mb: 3 }}>
          <Tab label="Kitaplar" />
          <Tab label="Kullanıcılar" />
        </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button variant="contained" startIcon={<AddIcon />} onClick={() => { setEditBook(null); setBookForm(emptyBook); setOpenBookDialog(true); }}>
                Kitap Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Başlık</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>Yıl</TableCell>
                    <TableCell>Sayfa</TableCell>
                    <TableCell>ISBN</TableCell>
                    <TableCell>Yayınevi</TableCell>
                    <TableCell>Dil</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {books.map(book => (
                    <TableRow key={book.id}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{book.year}</TableCell>
                      <TableCell>{book.pages}</TableCell>
                      <TableCell>{book.isbn}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>{book.language}</TableCell>
                      <TableCell>
                        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map(cat => (
                            <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                          ))}
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleBookEdit(book)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleBookDelete(book.id)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kitap Ekle/Güncelle Dialog */}
            <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)}>
              <DialogTitle>{editBook ? 'Kitap Güncelle' : 'Kitap Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <TextField label="Başlık" value={bookForm.title} onChange={e => setBookForm(f => ({ ...f, title: e.target.value }))} fullWidth required />
                  <TextField label="Yazar" value={bookForm.author} onChange={e => setBookForm(f => ({ ...f, author: e.target.value }))} fullWidth required />
                  <TextField label="Yıl" value={bookForm.year} onChange={e => setBookForm(f => ({ ...f, year: e.target.value }))} fullWidth required />
                  <TextField label="Sayfa" value={bookForm.pages} onChange={e => setBookForm(f => ({ ...f, pages: e.target.value }))} fullWidth required />
                  <TextField label="ISBN" value={bookForm.isbn} onChange={e => setBookForm(f => ({ ...f, isbn: e.target.value }))} fullWidth required />
                  <TextField label="Yayınevi" value={bookForm.publisher} onChange={e => setBookForm(f => ({ ...f, publisher: e.target.value }))} fullWidth required />
                  <TextField label="Dil" value={bookForm.language} onChange={e => setBookForm(f => ({ ...f, language: e.target.value }))} fullWidth required />
                  <Autocomplete
                    multiple
                    options={allCategories}
                    value={bookForm.categories}
                    onChange={(_, newValue) => setBookForm(f => ({ ...f, categories: newValue }))}
                    renderInput={(params) => <TextField {...params} label="Kategoriler" placeholder="Kategori seç" />}
                    fullWidth
                  />
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenBookDialog(false)}>İptal</Button>
                <Button onClick={handleBookSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
        {/* Kullanıcılar Sekmesi */}
        {tab === 1 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button variant="contained" startIcon={<AddIcon />} onClick={() => { setEditUser(null); setUserForm(emptyUser); setOpenUserDialog(true); }}>
                Kullanıcı Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Tür</TableCell>
                    <TableCell>Ad</TableCell>
                    <TableCell>Soyad</TableCell>
                    <TableCell>Yayınevi Adı</TableCell>
                    <TableCell>Yetkili Kişi</TableCell>
                    <TableCell>Kullanıcı Adı</TableCell>
                    <TableCell>E-posta</TableCell>
                    <TableCell>Telefon</TableCell>
                    <TableCell>Şifre</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map(user => (
                    <TableRow key={user.id}>
                      <TableCell>{user.type}</TableCell>
                      <TableCell>{user.type === 'Üye' ? user.name : ''}</TableCell>
                      <TableCell>{user.type === 'Üye' ? user.surname : ''}</TableCell>
                      <TableCell>{user.type === 'Yayınevi' ? user.publisherName : ''}</TableCell>
                      <TableCell>{user.type === 'Yayınevi' ? user.publisherContact : ''}</TableCell>
                      <TableCell>{user.username}</TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell>{user.type === 'Yayınevi' ? user.phone : ''}</TableCell>
                      <TableCell>{user.password}</TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleUserEdit(user)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleUserDelete(user.id)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kullanıcı Ekle/Güncelle Dialog */}
            <Dialog open={openUserDialog} onClose={() => setOpenUserDialog(false)}>
              <DialogTitle>{editUser ? 'Kullanıcı Güncelle' : 'Kullanıcı Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <TextField
                    select
                    label="Kullanıcı Türü"
                    value={userForm.type}
                    onChange={e => setUserForm(f => ({ ...f, type: e.target.value }))}
                    fullWidth
                  >
                    <MenuItem value="Üye">Üye</MenuItem>
                    <MenuItem value="Yayınevi">Yayınevi</MenuItem>
                  </TextField>
                  {userForm.type === 'Üye' && (
                    <>
                      <TextField label="Ad" value={userForm.name} onChange={e => setUserForm(f => ({ ...f, name: e.target.value }))} fullWidth required />
                      <TextField label="Soyad" value={userForm.surname} onChange={e => setUserForm(f => ({ ...f, surname: e.target.value }))} fullWidth required />
                    </>
                  )}
                  {userForm.type === 'Yayınevi' && (
                    <>
                      <TextField label="Yayınevi Adı" value={userForm.publisherName} onChange={e => setUserForm(f => ({ ...f, publisherName: e.target.value }))} fullWidth required />
                      <TextField label="Yetkili Kişi" value={userForm.publisherContact} onChange={e => setUserForm(f => ({ ...f, publisherContact: e.target.value }))} fullWidth required />
                      <TextField label="Telefon" value={userForm.phone} onChange={e => setUserForm(f => ({ ...f, phone: e.target.value }))} fullWidth />
                    </>
                  )}
                  <TextField label="Kullanıcı Adı" value={userForm.username} onChange={e => setUserForm(f => ({ ...f, username: e.target.value }))} fullWidth required />
                  <TextField label="E-posta" value={userForm.email} onChange={e => setUserForm(f => ({ ...f, email: e.target.value }))} fullWidth required />
                  <TextField label="Şifre" value={userForm.password} onChange={e => setUserForm(f => ({ ...f, password: e.target.value }))} fullWidth required />
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenUserDialog(false)}>İptal</Button>
                <Button onClick={handleUserSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
      </Paper>
    </Box>
  );
};

export default AdminPanel;
