                    value={borrowStartDate}
                    onChange={(e) => setBorrowStartDate(e.target.value)}
                    fullWidth
                    required
                    InputLabelProps={{ shrink: true }}
                    inputProps={{ min: new Date().toISOString().split('T')[0] }}
                    sx={{
                      '& .MuiOutlinedInput-root': {
