import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Paper } from '@mui/material';

const Login = ({ userType }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    // Burada API'ye giriş isteği atılabilir
    alert(`${userType} olarak giriş yapıldı!\nKullanıcı adı: ${username}`);
  };

  const userTypeLabel = userType === 'member' ? 'Üye' : userType === 'publisher' ? 'Yayınevi' : 'Admin';

  return (
    <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', minHeight: '60vh' }}>
      <Paper sx={{ p: 4, minWidth: 320 }}>
        <Typography variant="h5" gutterBottom>{userTypeLabel} Girişi</Typography>
        <form onSubmit={handleSubmit}>
          <TextField
            label="Kullanıcı Adı"
            variant="outlined"
            fullWidth
            margin="normal"
            value={username}
            onChange={e => setUsername(e.target.value)}
            required
          />
          <TextField
            label="Şifre"
            type="password"
            variant="outlined"
            fullWidth
            margin="normal"
            value={password}
            onChange={e => setPassword(e.target.value)}
            required
          />
          <Button type="submit" variant="contained" color="primary" fullWidth sx={{ mt: 2 }}>
            Giriş Yap
          </Button>
        </form>
      </Paper>
    </Box>
  );
};

export default Login;
