import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Paper, MenuItem, Alert } from '@mui/material';

const Register = () => {
  const [userType, setUserType] = useState('member');
  const [form, setForm] = useState({
    name: '',
    surname: '',
    username: '',
    email: '',
    password: '',
    publisherName: '',
    publisherContact: '',
    publisherEmail: '',
    publisherUsername: '',
    publisherPassword: '',
  });
  const [success, setSuccess] = useState('');

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSuccess('Kayıt başarılı! Giriş yapabilirsiniz.');
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: "linear-gradient(rgba(30,40,60,0.72), rgba(30,40,60,0.62)), url('/register-bg.jpg') center center/cover no-repeat fixed",
        color: '#f7f4ed',
        py: 6,
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
      }}
    >
      <Paper
        sx={{
          p: { xs: 2, sm: 4 },
          width: '100%',
          maxWidth: 400,
          borderRadius: 4,
          boxShadow: 6,
          bgcolor: 'rgba(255,255,255,0.82)',
          border: '1.5px solid #e0d7c6',
          backdropFilter: 'blur(8px)',
          mx: 'auto',
        }}
      >
        <Typography variant="h5" gutterBottom align="center"
          sx={{
            background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 800,
            letterSpacing: '0.04em',
          }}
        >Kayıt Ol</Typography>
        {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
        <form onSubmit={handleSubmit} style={{ display: 'flex', flexDirection: 'column', gap: 10 }}>
          <TextField
            select
            label="Kullanıcı Türü"
            value={userType}
            onChange={e => setUserType(e.target.value)}
            fullWidth
            margin="dense"
            sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
          >
            <MenuItem value="member">Üye</MenuItem>
            <MenuItem value="publisher">Yayınevi</MenuItem>
          </TextField>
          {userType === 'member' && (
            <>
              <TextField
                label="Ad"
                name="name"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.name}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Soyad"
                name="surname"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.surname}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Kullanıcı Adı"
                name="username"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.username}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="E-posta"
                name="email"
                type="email"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.email}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Şifre"
                name="password"
                type="password"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.password}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
            </>
          )}
          {userType === 'publisher' && (
            <>
              <TextField
                label="Yayınevi Adı"
                name="publisherName"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.publisherName}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Yetkili Kişi"
                name="publisherContact"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.publisherContact}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="E-posta"
                name="publisherEmail"
                type="email"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.publisherEmail}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Kullanıcı Adı"
                name="publisherUsername"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.publisherUsername}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
              <TextField
                label="Şifre"
                name="publisherPassword"
                type="password"
                variant="outlined"
                fullWidth
                margin="dense"
                value={form.publisherPassword}
                onChange={handleChange}
                required
                sx={{ bgcolor: 'rgba(255,255,255,0.7)', borderRadius: 2 }}
              />
            </>
          )}
          <Button
            type="submit"
            variant="contained"
            color="primary"
            fullWidth
            sx={{
              py: 1.3,
              fontWeight: 700,
              fontSize: 17,
              borderRadius: 3,
              boxShadow: '0 2px 12px 0 #4ecdc422',
              background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
              backgroundSize: '200% 100%',
              backgroundPosition: 'left',
              transition: 'background-position 0.3s',
              '&:hover': {
                backgroundPosition: 'right',
                background: 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)',
              },
            }}
          >
            Kayıt Ol
          </Button>
        </form>
      </Paper>
    </Box>
  );
};

export default Register;
