import React, { useState, useEffect } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem, Alert, CircularProgress, Grid, InputAdornment } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import ClearIcon from '@mui/icons-material/Clear';
import { fetchBooks, getAllUsers, updateUser, deleteUser, registerUser, createBook, updateBook, deleteBook } from '../api';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'A≈ük', 'Distopya', 'Bilim Kurgu', '√áocuk', 'Tarih', 'Polisiye'];

const emptyBook = { title: '', author: '', publishDate: '', pageCount: '', isbn: '', publisher: '', language: '', categories: [], cover: '' };
const emptyUser = { firstName: '', lastName: '', username: '', email: '', role: 'member', birthDate: '', address: '', publisherName: '', website: '', password: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);

  // Verileri y√ºkle
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [booksResponse, usersResponse] = await Promise.all([
          fetchBooks(),
          getAllUsers()
        ]);
        setBooks(booksResponse.data);
        setUsers(usersResponse.data);
        setError(null);
      } catch (err) {
        setError('Veriler y√ºklenirken hata olu≈ütu: ' + err.message);
        console.error('Veri y√ºkleme hatasƒ±:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Kitap ekle/g√ºncelle (API ile)
  const handleBookSave = async () => {
    try {
      if (editBook) {
        // Kitap g√ºncelleme
        await updateBook(editBook.ID, bookForm);
        const updatedBooks = books.map(b => b.ID === editBook.ID ? { ...b, ...bookForm } : b);
        setBooks(updatedBooks);
      } else {
        // Yeni kitap ekleme
        const response = await createBook(bookForm);
        const newBook = response.data;
        setBooks([...books, newBook]);
      }
      setOpenBookDialog(false);
      setEditBook(null);
      setBookForm(emptyBook);
    } catch (err) {
      setError('Kitap i≈ülemi sƒ±rasƒ±nda hata olu≈ütu: ' + err.message);
      console.error('Kitap i≈ülemi hatasƒ±:', err);
    }
  };

  // Kitap sil (API ile)
  const handleBookDelete = async (id) => {
    try {
      await deleteBook(id);
      setBooks(books.filter(b => b.ID !== id));
    } catch (err) {
      setError('Kitap silinirken hata olu≈ütu: ' + err.message);
      console.error('Kitap silme hatasƒ±:', err);
    }
  };
  // Kitap d√ºzenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      publishDate: book.publishDate,
      pageCount: book.pageCount,
      isbn: book.ISBN,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
      cover: book.cover || '',
    });
    setOpenBookDialog(true);
  };

  // Kullanƒ±cƒ± ekle/g√ºncelle (API ile)
  const handleUserSave = async () => {
    try {
      if (editUser) {
        // Kullanƒ±cƒ± g√ºncelleme
        await updateUser(editUser.id, userForm);
        const updatedUsers = users.map(u => u.id === editUser.id ? { ...u, ...userForm } : u);
        setUsers(updatedUsers);
      } else {
        // Yeni kullanƒ±cƒ± ekleme
        const response = await registerUser(userForm);
        const newUser = response.data;
        setUsers([...users, newUser]);
      }
      setOpenUserDialog(false);
      setEditUser(null);
      setUserForm(emptyUser);
    } catch (err) {
      setError('Kullanƒ±cƒ± i≈ülemi sƒ±rasƒ±nda hata olu≈ütu: ' + err.message);
      console.error('Kullanƒ±cƒ± i≈ülemi hatasƒ±:', err);
    }
  };

  // Kullanƒ±cƒ± sil (API ile)
  const handleUserDelete = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter(u => u.id !== id));
    } catch (err) {
      setError('Kullanƒ±cƒ± silinirken hata olu≈ütu: ' + err.message);
      console.error('Kullanƒ±cƒ± silme hatasƒ±:', err);
    }
  };
  // Kullanƒ±cƒ± d√ºzenle
  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      username: user.username || '',
      email: user.email || '',
      role: user.role || 'member',
      birthDate: user.birthDate || '',
      address: user.address || '',
      publisherName: user.publisherName || '',
      website: user.website || '',
      password: '' // ≈ûifre g√ºvenlik nedeniyle bo≈ü bƒ±rakƒ±lƒ±r
    });
    setOpenUserDialog(true);
  };

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <Tabs value={tab} onChange={(_, v) => setTab(v)} centered sx={{ mb: 3 }}>
              <Tab label="Kitaplar" />
              <Tab label="Kullanƒ±cƒ±lar" />
            </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 3 }}>
              <Button 
                variant="contained" 
                startIcon={<AddIcon />} 
                onClick={() => { setEditBook(null); setBookForm(emptyBook); setOpenBookDialog(true); }}
                sx={{ 
                  backgroundColor: '#2e7d32',
                  borderRadius: 3,
                  px: 4,
                  py: 1.5,
                  fontSize: 16,
                  fontWeight: 600,
                  textTransform: 'none',
                  boxShadow: '0 4px 12px rgba(46, 125, 50, 0.3)',
                  '&:hover': { 
                    backgroundColor: '#1b5e20',
                    boxShadow: '0 6px 16px rgba(46, 125, 50, 0.4)',
                    transform: 'translateY(-2px)'
                  },
                  transition: 'all 0.3s ease'
                }}
              >
                üìö Kitap Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Ba≈ülƒ±k</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>Yayƒ±n Tarihi</TableCell>
                    <TableCell>Sayfa</TableCell>
                    <TableCell>ISBN</TableCell>
                    <TableCell>Yayƒ±nevi</TableCell>
                    <TableCell>Dil</TableCell>
                    <TableCell>Kapak</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell align="right">ƒ∞≈ülemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {books.map(book => (
                    <TableRow key={book.ID}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{new Date(book.publishDate).toLocaleDateString('tr-TR')}</TableCell>
                      <TableCell>{book.pageCount}</TableCell>
                      <TableCell>{book.isbn || book.ISBN}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>{book.language}</TableCell>
                      <TableCell>
                        {book.cover && (
                          <img src={book.cover} alt="kapak" style={{ width: 40, height: 56, objectFit: 'contain', borderRadius: 4, boxShadow: '0 2px 8px #3a5ba022' }} />
                        )}
                      </TableCell>
                      <TableCell>
                        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map(cat => (
                            <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                          ))}
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleBookEdit(book)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleBookDelete(book.ID)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kitap Ekle/G√ºncelle Dialog */}
            <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)}>
