import React, { useState, useEffect } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem, Alert, CircularProgress, Grid, InputAdornment } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import ClearIcon from '@mui/icons-material/Clear';
import { fetchBooks, getAllUsers, updateUser, deleteUser, registerUser, createBook, updateBook, deleteBook, fetchCategories } from '../api';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'Aşk', 'Distopya', 'Bilim Kurgu', 'Çocuk', 'Tarih', 'Polisiye'];

const emptyBook = { title: '', author: '', publishDate: '', pageCount: '', isbn: '', publisher: '', language: '', categories: [], cover: '' };
const emptyUser = { firstName: '', lastName: '', username: '', email: '', role: 'member', birthDate: '', address: '', publisherName: '', website: '', password: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [openCategoryDialog, setOpenCategoryDialog] = useState(false);
  const [editCategory, setEditCategory] = useState(null);
  const [categoryForm, setCategoryForm] = useState({ name: '' });

  // Arama fonksiyonu
  const handleSearch = (searchValue) => {
    setSearchTerm(searchValue);

    if (!searchValue.trim()) {
      setFilteredUsers(users);
      setFilteredBooks(books);
      setFilteredCategories(categories);
      return;
    }

    const lowerSearch = searchValue.toLowerCase();

    // Kullanıcı arama
    const filteredUsersResult = users.filter(user => {
      const fullName = `${user.firstName} ${user.lastName}`.toLowerCase();
      const username = user.username.toLowerCase();
      const email = user.email.toLowerCase();
      const publisherName = (user.publisherName || '').toLowerCase();
      const website = (user.website || '').toLowerCase();

      return fullName.includes(lowerSearch) || 
             username.includes(lowerSearch) || 
             email.includes(lowerSearch) ||
             publisherName.includes(lowerSearch) ||
             website.includes(lowerSearch);
    });

    // Kitap arama
    const filteredBooksResult = books.filter(book => {
      const title = book.title.toLowerCase();
      const author = book.author.toLowerCase();
      const publisher = (book.publisher || '').toLowerCase();
      const isbn = (book.isbn || book.ISBN || '').toLowerCase();

      return title.includes(lowerSearch) || 
             author.includes(lowerSearch) || 
             publisher.includes(lowerSearch) ||
             isbn.includes(lowerSearch);
    });

    // Kategori arama
    const filteredCategoriesResult = categories.filter(category => {
      const name = category.name.toLowerCase();

      return name.includes(lowerSearch);
    });

    setFilteredUsers(filteredUsersResult);
    setFilteredBooks(filteredBooksResult);
    setFilteredCategories(filteredCategoriesResult);
  };

  // Arama temizleme
  const clearSearch = () => {
    setSearchTerm('');
    setFilteredUsers(users);
    setFilteredBooks(books);
    setFilteredCategories(categories);
  };

  // Verileri yükle
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [booksResponse, usersResponse, categoriesResponse] = await Promise.all([
          fetchBooks(),
          getAllUsers(),
          fetchCategories()
        ]);
        setBooks(booksResponse.data);
        setUsers(usersResponse.data);
        setCategories(categoriesResponse.data);
        setFilteredUsers(usersResponse.data);
        setFilteredBooks(booksResponse.data);
        setFilteredCategories(categoriesResponse.data);
        setError(null);
      } catch (err) {
        setError('Veriler yüklenirken hata oluştu: ' + err.message);
        console.error('Veri yükleme hatası:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Kitap ekle/güncelle (API ile)
  const handleBookSave = async () => {
    try {
      if (editBook) {
        // Kitap güncelleme
        await updateBook(editBook.ID, bookForm);
        const updatedBooks = books.map(b => b.ID === editBook.ID ? { ...b, ...bookForm } : b);
        setBooks(updatedBooks);
      } else {
        // Yeni kitap ekleme
        const response = await createBook(bookForm);
        const newBook = response.data;
        setBooks([...books, newBook]);
      }
      setOpenBookDialog(false);
      setEditBook(null);
      setBookForm(emptyBook);
    } catch (err) {
      setError('Kitap işlemi sırasında hata oluştu: ' + err.message);
      console.error('Kitap işlemi hatası:', err);
    }
  };

  // Kitap sil (API ile)
  const handleBookDelete = async (id) => {
    try {
      await deleteBook(id);
      setBooks(books.filter(b => b.ID !== id));
    } catch (err) {
      setError('Kitap silinirken hata oluştu: ' + err.message);
      console.error('Kitap silme hatası:', err);
    }
  };
  // Kitap düzenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      publishDate: book.publishDate,
      pageCount: book.pageCount,
      isbn: book.ISBN,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
      cover: book.cover || '',
    });
    setOpenBookDialog(true);
  };

  // Kullanıcı ekle/güncelle (API ile)
  const handleUserSave = async () => {
    try {
      if (editUser) {
        // Kullanıcı güncelleme
        await updateUser(editUser.id, userForm);
        const updatedUsers = users.map(u => u.id === editUser.id ? { ...u, ...userForm } : u);
        setUsers(updatedUsers);
      } else {
        // Yeni kullanıcı ekleme
        const response = await registerUser(userForm);
        const newUser = response.data;
        setUsers([...users, newUser]);
      }
      setOpenUserDialog(false);
      setEditUser(null);
      setUserForm(emptyUser);
    } catch (err) {
      setError('Kullanıcı işlemi sırasında hata oluştu: ' + err.message);
      console.error('Kullanıcı işlemi hatası:', err);
    }
  };

  // Kullanıcı sil (API ile)
  const handleUserDelete = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter(u => u.id !== id));
    } catch (err) {
      setError('Kullanıcı silinirken hata oluştu: ' + err.message);
      console.error('Kullanıcı silme hatası:', err);
    }
  };
  // Kullanıcı düzenle
  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      username: user.username || '',
      email: user.email || '',
      role: user.role || 'member',
      birthDate: user.birthDate || '',
      address: user.address || '',
      publisherName: user.publisherName || '',
      website: user.website || '',
      password: '' // Şifre güvenlik nedeniyle boş bırakılır
    });
    setOpenUserDialog(true);
  };

  // Kategori ekle/güncelle
  const handleCategorySave = async () => {
    try {
      if (editCategory) {
        // Kategori güncelleme (şimdilik localStorage kullanıyoruz)
        const updatedCategories = categories.map(c => 
          c.id === editCategory.id ? { ...c, ...categoryForm } : c
        );
        setCategories(updatedCategories);
        setFilteredCategories(updatedCategories);
      } else {
        // Yeni kategori ekleme
        const newCategory = {
          id: Date.now(),
          name: categoryForm.name,
          description: categoryForm.description
        };
        const updatedCategories = [...categories, newCategory];
        setCategories(updatedCategories);
        setFilteredCategories(updatedCategories);
      }
      setOpenCategoryDialog(false);
      setEditCategory(null);
      setCategoryForm({ name: '', description: '' });
    } catch (err) {
      setError('Kategori işlemi sırasında hata oluştu: ' + err.message);
      console.error('Kategori işlemi hatası:', err);
    }
  };

  // Kategori sil
  const handleCategoryDelete = async (id) => {
    try {
      const updatedCategories = categories.filter(c => c.id !== id);
      setCategories(updatedCategories);
      setFilteredCategories(updatedCategories);
    } catch (err) {
      setError('Kategori silinirken hata oluştu: ' + err.message);
      console.error('Kategori silme hatası:', err);
    }
  };

  // Kategori düzenle
  const handleCategoryEdit = (category) => {
    setEditCategory(category);
    setCategoryForm({
      name: category.name || '',
      description: category.description || ''
    });
    setOpenCategoryDialog(true);
  };

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <Tabs value={tab} onChange={(_, v) => { setTab(v); clearSearch(); }} centered sx={{ mb: 3 }}>
              <Tab label="Kitaplar" />
              <Tab label="Kullanıcılar" />
              <Tab label="Kategoriler" />
            </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            {/* Kitaplar Arama Kutusu */}
            <Box sx={{ mb: 3, display: 'flex', alignItems: 'center', gap: 2, justifyContent: 'space-between' }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2, flexGrow: 1, maxWidth: 400 }}>
                <TextField
                  placeholder="Kitap başlığı, yazar veya yayınevi ara..."
                  value={searchTerm}
                  onChange={(e) => handleSearch(e.target.value)}
                  sx={{
                    flexGrow: 1,
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: '#ffffff',
                      boxShadow: 'none',
                      border: '1px solid #e0e0e0',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: '#ffffff',
                        border: '1px solid #d0d0d0',
                        boxShadow: '0 1px 3px rgba(0, 0, 0, 0.05)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: '#ffffff',
                        border: '1px solid #2e7d32',
                        boxShadow: '0 1px 3px rgba(46, 125, 50, 0.1)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 400,
                      color: '#666'
                    }
                  }}
                  InputProps={{
                    startAdornment: (
                      <InputAdornment position="start">
                        <SearchIcon sx={{ color: '#777' }} />
                      </InputAdornment>
                    ),
                    endAdornment: searchTerm && (
                      <InputAdornment position="end">
                        <IconButton
                          size="small"
                          onClick={clearSearch}
                          sx={{ color: '#777' }}
                        >
                          <ClearIcon />
