import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Grid, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Autocomplete, Paper, Alert, Stack, Chip } from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';

const featuredBooks = [
  {
    id: 1,
    title: 'Sefiller',
    author: 'Victor Hugo',
    year: 1862,
    pages: 1463,
    isbn: '9789750738606',
    publisher: 'Can Yayınları',
    language: 'Fransızca',
    categories: ['Roman', 'Klasik'],
  },
  {
    id: 2,
    title: 'Suç ve Ceza',
    author: 'Fyodor Dostoyevski',
    year: 1866,
    pages: 687,
    isbn: '9786053609396',
    publisher: 'İş Bankası Kültür Yayınları',
    language: 'Rusça',
    categories: ['Roman', 'Psikoloji'],
  },
  {
    id: 3,
    title: 'Kürk Mantolu Madonna',
    author: 'Sabahattin Ali',
    year: 1943,
    pages: 160,
    isbn: '9789753638026',
    publisher: 'Yapı Kredi Yayınları',
    language: 'Türkçe',
    categories: ['Roman', 'Aşk'],
  },
];

const allBooks = [
  ...featuredBooks,
  // Buraya daha fazla kitap eklenebilir
];

const Home = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [notFound, setNotFound] = useState(false);

  // Arama fonksiyonu
  const handleSearch = () => {
    const found = allBooks.find(b => b.title.toLowerCase() === (searchValue || inputValue).toLowerCase());
    if (found) {
      setSelectedBook(found);
      setNotFound(false);
    } else {
      setSelectedBook(null);
      setNotFound(true);
    }
  };

  return (
    <Box>
      {/* Hero Section */}
      <Box sx={{
        width: '100%',
        minHeight: 360,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'repeating-linear-gradient(135deg, #f7f4ed 0px, #f7f4ed 40px, #f0ede6 40px, #f0ede6 80px)',
        position: 'relative',
        overflow: 'hidden',
        borderRadius: 4,
        boxShadow: 3,
        mb: 6,
        py: { xs: 7, md: 11 },
        px: 2,
      }}>
        {/* Kitap rafı SVG veya ikon */}
        <AutoStoriesIcon sx={{ position: 'absolute', top: 32, left: 32, fontSize: 64, color: 'rgba(58,91,160,0.10)' }} />
        <MenuBookIcon sx={{ position: 'absolute', bottom: 32, right: 32, fontSize: 80, color: 'rgba(76,205,196,0.10)' }} />
        <Typography
          variant="h3"
          align="center"
          gutterBottom
          sx={{
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 2,
            color: '#3a5ba0',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 1.5,
          }}
        >
          <AutoStoriesIcon sx={{ fontSize: 44, color: '#4ecdc4', mb: '-4px' }} />
          Kent Kütüphanesi
        </Typography>
        <Typography variant="h6" align="center" gutterBottom sx={{ color: '#22335b', opacity: 0.92, mb: 3, fontWeight: 500 }}>
          Binlerce kitap, sıcak bir atmosferde sizi bekliyor.
        </Typography>
        <Paper elevation={8} sx={{
          mt: 2,
          width: '100%',
          maxWidth: 500,
          p: { xs: 2, sm: 3 },
          borderRadius: 3.5,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 4px 16px 0 #3a5ba022',
          backdropFilter: 'blur(6px)',
          background: 'rgba(255,255,255,0.85)',
          border: '1.5px solid #e0d7c6',
        }}>
          <Autocomplete
            freeSolo
            options={allBooks.map(b => b.title)}
            value={searchValue}
            onChange={(_, newValue) => setSearchValue(newValue || '')}
            inputValue={inputValue}
            onInputChange={(_, newInputValue) => setInputValue(newInputValue)}
            sx={{ width: '100%', mb: 2, '& .MuiAutocomplete-listbox': { borderRadius: 2, boxShadow: 3, bgcolor: '#fff' } }}
            renderInput={(params) => (
              <TextField {...params} label="Kitap ara..." variant="outlined" sx={{ bgcolor: 'rgba(255,255,255,0.95)', borderRadius: 2, width: '100%', border: '1.5px solid #e0d7c6' }} />
            )}
            ListboxProps={{ style: { boxShadow: '0 4px 16px 0 #e0d7c622', borderRadius: 12 } }}
          />
          <Button variant="contained" color="primary" sx={{ fontWeight: 700, px: 4, width: '100%', py: 1.2, fontSize: 17, borderRadius: 3, boxShadow: '0 2px 12px 0 #3a5ba022', bgcolor: '#3a5ba0' }} onClick={handleSearch}>
            Ara
          </Button>
          {notFound && (
            <Alert severity="warning" sx={{ mt: 2, width: '100%' }}>Aradığınız kitap bulunamadı.</Alert>
          )}
        </Paper>
      </Box>
      {/* Featured Books */}
      <Typography variant="h5" gutterBottom sx={{ mt: 4, mb: 2, fontWeight: 700, color: 'primary.dark', letterSpacing: '0.03em' }}>Öne Çıkan Kitaplar</Typography>
      <Grid container spacing={3}>
        {featuredBooks.map(book => (
          <Grid item xs={12} sm={4} key={book.id}>
            <Paper
              elevation={6}
              sx={{
                p: 3,
                borderRadius: 5,
                bgcolor: 'rgba(255,255,255,0.85)',
                boxShadow: '0 8px 32px 0 #3a5ba022',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'flex-start',
                gap: 1,
                transition: 'transform 0.18s, box-shadow 0.18s',
                cursor: 'pointer',
                '&:hover': {
                  transform: 'scale(1.045)',
                  boxShadow: '0 16px 48px 0 #4ecdc422',
                  bgcolor: 'rgba(255,255,255,0.97)',
                },
              }}
              onClick={() => setSelectedBook(book)}
            >
              <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                {book.categories && book.categories.map(cat => (
                  <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                ))}
              </Stack>
              <Typography variant="h6" sx={{ fontWeight: 700 }}>{book.title}</Typography>
              <Typography variant="subtitle2" color="text.secondary" gutterBottom>{book.author}</Typography>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <Stack direction="row" spacing={1} sx={{ mb: 2, flexWrap: 'wrap' }}>
            {selectedBook?.categories?.map(cat => (
              <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
            ))}
          </Stack>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Home;
