import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardActions, Button, Box } from '@mui/material';

const dummyBooks = [
  { id: 1, title: 'Sefiller', author: 'Victor Hugo' },
  { id: 2, title: 'Suç ve Ceza', author: 'Fyodor Dostoyevski' },
  { id: 3, title: 'Kürk Mantolu Madonna', author: 'Sabahattin Ali' },
  { id: 4, title: '1984', author: 'George Orwell' },
];

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setBooks(dummyBooks);
      setLoading(false);
    }, 500);
  }, []);

  if (loading) return <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><span>Yükleniyor...</span></Box>;

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Kitaplar</Typography>
      <Grid container spacing={3}>
        {books.map(book => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>{book.title}</Typography>
                <Typography variant="body2" color="text.secondary">{book.author}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary">Detay</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default BookList;
