import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Menu, MenuItem } from '@mui/material';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import { Link, useLocation } from 'react-router-dom';

const navLinks = [
  { label: 'Ana Sayfa', to: '/' },
  { label: 'Kitaplar', to: '/books' },
  { label: 'Kayıt Ol', to: '/register' },
];

const Navbar = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const handleMenuOpen = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);
  const location = useLocation();

  return (
    <AppBar position="static" elevation={0} sx={{
      background: 'rgba(30,40,60,0.55)',
      backdropFilter: 'blur(12px)',
      boxShadow: '0 4px 24px 0 rgba(30,40,60,0.12)',
      borderBottom: '1.5px solid rgba(255,255,255,0.13)',
      minWidth: '100vw',
      left: 0,
      top: 0,
      right: 0,
      borderRadius: 0,
      m: 0,
      p: 0,
    }}>
      <Toolbar sx={{ minHeight: 88, px: { xs: 1, sm: 4 } }}>
        <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
          <LibraryBooksIcon sx={{ mr: 2, fontSize: 44, color: '#fff', filter: 'drop-shadow(0 2px 8px #1e283c66)' }} />
          <Typography variant="h4" component={Link} to="/" sx={{ color: '#fff', textDecoration: 'none', fontWeight: 900, letterSpacing: '0.04em', fontSize: 32, textShadow: '0 2px 12px #1e283c88' }}>
            Kent Kütüphanesi
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 2 }}>
          {navLinks.map(link => (
            <Button
              key={link.to}
              color="inherit"
              component={Link}
              to={link.to}
              sx={{
                fontWeight: 700,
                fontSize: 18,
                px: 3,
                borderRadius: 2.5,
                position: 'relative',
                color: location.pathname === link.to ? '#4ecdc4' : '#fff',
                textShadow: '0 2px 8px #1e283c88',
                transition: 'color 0.2s',
                '&:after': {
                  content: '""',
                  display: 'block',
                  position: 'absolute',
                  left: 10,
                  right: 10,
                  bottom: 4,
                  height: 4,
                  borderRadius: 2,
                  background: location.pathname === link.to ? 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)' : 'transparent',
                  boxShadow: location.pathname === link.to ? '0 2px 8px 0 #4ecdc488' : 'none',
                  filter: location.pathname === link.to ? 'blur(0.5px)' : 'none',
                  transition: 'background 0.2s, box-shadow 0.2s',
                },
                '&:hover:after': {
                  background: 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)',
                  boxShadow: '0 2px 8px 0 #4ecdc488',
                  filter: 'blur(0.5px)',
                },
                '&:hover': {
                  color: '#4ecdc4',
                },
              }}
            >
              {link.label}
            </Button>
          ))}
          <Button color="inherit" onClick={handleMenuOpen} sx={{ fontWeight: 700, fontSize: 18, px: 3, borderRadius: 2.5, color: '#fff', textShadow: '0 2px 8px #1e283c88', '&:hover': { color: '#4ecdc4' } }}>Kullanıcı Girişi</Button>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/member">Üye Girişi</MenuItem>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/publisher">Yayınevi Girişi</MenuItem>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/admin">Admin Girişi</MenuItem>
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
