import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Grid, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Autocomplete } from '@mui/material';

const featuredBooks = [
  {
    id: 1,
    title: 'Sefiller',
    author: 'Victor Hugo',
    year: 1862,
    pages: 1463,
    isbn: '9789750738606',
    publisher: 'Can Yayınları',
    language: 'Fransızca',
  },
  {
    id: 2,
    title: 'Suç ve Ceza',
    author: 'Fyodor Dostoyevski',
    year: 1866,
    pages: 687,
    isbn: '9786053609396',
    publisher: 'İş Bankası Kültür Yayınları',
    language: 'Rusça',
  },
  {
    id: 3,
    title: 'Kürk Mantolu Madonna',
    author: 'Sabahattin Ali',
    year: 1943,
    pages: 160,
    isbn: '9789753638026',
    publisher: 'Yapı Kredi Yayınları',
    language: 'Türkçe',
  },
];

const allBooks = [
  ...featuredBooks,
  // Buraya daha fazla kitap eklenebilir
];

const Home = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');

  // Arama fonksiyonu
  const handleSearch = () => {
    const found = allBooks.find(b => b.title.toLowerCase() === (searchValue || inputValue).toLowerCase());
    if (found) setSelectedBook(found);
    else setSelectedBook(null);
  };

  return (
    <Box>
      {/* Hero Section */}
      <Box sx={{
        width: '100%',
        minHeight: 320,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'linear-gradient(90deg, #4b6cb7 0%, #182848 100%)',
        color: 'white',
        borderRadius: 4,
        boxShadow: 3,
        mb: 6,
        py: { xs: 6, md: 10 },
        px: 2,
      }}>
        <Typography variant="h3" align="center" gutterBottom sx={{ color: 'white', textShadow: '0 2px 16px rgba(0,0,0,0.12)' }}>
          Kütüphaneye Hoşgeldiniz
        </Typography>
        <Typography variant="h6" align="center" gutterBottom sx={{ color: 'white', opacity: 0.9 }}>
          Binlerce kitap, tek tıkla elinizin altında.
        </Typography>
        <Box sx={{ display: 'flex', flexDirection: 'column', alignItems: 'center', mt: 4, width: '100%', maxWidth: 500 }}>
          <Autocomplete
            freeSolo
            options={allBooks.map(b => b.title)}
            value={searchValue}
            onChange={(_, newValue) => setSearchValue(newValue || '')}
            inputValue={inputValue}
            onInputChange={(_, newInputValue) => setInputValue(newInputValue)}
            renderInput={(params) => (
              <TextField {...params} label="Kitap ara..." variant="outlined" sx={{ bgcolor: 'white', borderRadius: 2, width: '100%', mb: 2 }} />
            )}
          />
          <Button variant="contained" color="secondary" sx={{ fontWeight: 700, px: 4, width: '100%' }} onClick={handleSearch}>
            Ara
          </Button>
        </Box>
      </Box>
      {/* Featured Books */}
      <Typography variant="h5" gutterBottom>Öne Çıkan Kitaplar</Typography>
      <Grid container spacing={2}>
        {featuredBooks.map(book => (
          <Grid item xs={12} sm={4} key={book.id}>
            <Button
              fullWidth
              sx={{
                p: 3,
                fontSize: 18,
                fontWeight: 600,
                bgcolor: 'primary.main',
                color: 'white',
                borderRadius: 3,
                boxShadow: 2,
                transition: 'transform 0.2s',
                '&:hover': {
                  transform: 'scale(1.07)',
                  bgcolor: 'primary.dark',
                  boxShadow: 6,
                },
                textTransform: 'none',
                mb: 2,
              }}
              onClick={() => setSelectedBook(book)}
            >
              {book.title} <span style={{ fontWeight: 400, fontSize: 15, marginLeft: 8 }}>({book.author})</span>
            </Button>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Home;
