                                <Typography variant="body2">
                                  {formatDate(request.requestDate)}
                                </Typography>
                              </TableCell>
                              <TableCell>
                                <Box sx={{ display: 'flex', flexDirection: 'column', gap: 0.5 }}>
                                  {request.borrowStartDate && (
                                    <Typography variant="body2" color="text.secondary">
                                      <strong>Alış:</strong> {formatDate(request.borrowStartDate)}
                                    </Typography>
                                  )}
                                  {request.borrowEndDate && (
                                    <Typography variant="body2" color="text.secondary">
                                      <strong>İade:</strong> {formatDate(request.borrowEndDate)}
                                    </Typography>
                                  )}
                                  {!request.borrowStartDate && !request.borrowEndDate && (
                                    <Typography variant="body2" color="text.secondary">
                                      Tarih belirtilmemiş
                                    </Typography>
                                  )}
                                </Box>
                              </TableCell>
                              <TableCell>
                                <Chip
                                  label={getStatusText(request.status)}
                                  color={getStatusColor(request.status)}
                                  size="small"
                                  sx={{ fontWeight: 600 }}
                                />
                              </TableCell>
                              <TableCell>
                                {request.status === 'Pending' && (
                                  <Box sx={{ display: 'flex', gap: 1 }}>
                                    <IconButton
                                      size="small"
                                      onClick={() => handleApproveRequest(request.id)}
                                      sx={{ 
                                        color: '#27ae60',
                                        '&:hover': { bgcolor: 'rgba(39, 174, 96, 0.1)' }
                                      }}
                                    >
                                      <CheckIcon />
                                    </IconButton>
                                    <IconButton
                                      size="small"
                                      onClick={() => handleRejectRequest(request.id)}
                                      sx={{ 
                                        color: '#e74c3c',
                                        '&:hover': { bgcolor: 'rgba(231, 76, 60, 0.1)' }
                                      }}
                                    >
                                      <CancelIcon />
                                    </IconButton>
                                  </Box>
                                )}
                                {request.status !== 'Pending' && (
                                  <Typography variant="body2" color="text.secondary">
                                    {request.status === 'Approved' && formatDate(request.approvedDate)}
                                    {request.status === 'Rejected' && formatDate(request.rejectedDate)}
                                    {request.status === 'Returned' && formatDate(request.returnDate)}
                                  </Typography>
                                )}
                              </TableCell>
                            </TableRow>
                          ))}
                        </TableBody>
                      </Table>
                    </TableContainer>
                  )}
                </Box>
              )}
            </Box>
          </Paper>
        </Grid>
      </Grid>

      {/* Kitap Ekleme/Düzenleme Dialog */}
      <Dialog 
        open={openDialog} 
        onClose={() => setOpenDialog(false)} 
        maxWidth="md" 
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: 4,
            boxShadow: '0 24px 64px rgba(0,0,0,0.3)',
            background: 'linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%)',
            border: '1px solid rgba(102, 126, 234, 0.1)'
          }
        }}
      >
        <DialogTitle sx={{ 
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          borderRadius: '16px 16px 0 0',
          py: 4,
          px: 4,
          position: 'relative',
          overflow: 'hidden'
        }}>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 3,
            position: 'relative',
            zIndex: 2
          }}>
            <Box sx={{
              p: 1.5,
              borderRadius: 3,
              bgcolor: 'rgba(255,255,255,0.2)',
              backdropFilter: 'blur(10px)'
            }}>
              <BookIcon sx={{ fontSize: 28 }} />
            </Box>
            <Box>
              <Typography variant="h5" sx={{ 
                fontWeight: 800,
                mb: 0.5,
                textShadow: '0 2px 4px rgba(0,0,0,0.2)'
              }}>
                {editingBook ? '📝 Kitap Düzenle' : '📚 Yeni Kitap Ekle'}
              </Typography>
              <Typography variant="body2" sx={{ 
                opacity: 0.9,
                fontWeight: 300
              }}>
                {editingBook ? 'Kitap bilgilerini güncelleyin' : 'Yeni kitap bilgilerini girin'}
              </Typography>
            </Box>
          </Box>
          {/* Dekoratif arka plan elementleri */}
          <Box sx={{
            position: 'absolute',
            top: -20,
            right: -20,
            width: 100,
            height: 100,
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.1)',
            zIndex: 1
          }} />
          <Box sx={{
            position: 'absolute',
            bottom: -30,
            left: -30,
            width: 80,
            height: 80,
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.05)',
            zIndex: 1
          }} />
        </DialogTitle>

        <form onSubmit={handleSubmit}>
          <DialogContent sx={{ 
            pt: 4, 
            pb: 3,
            px: 4,
            background: 'linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%)'
          }}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <TextField
                  name="title"
                  label="📖 Kitap Adı"
                  value={form.title}
                  onChange={handleChange}
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="author"
                  label="✍️ Yazar"
                  value={form.author}
                  onChange={handleChange}
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="summary"
                  label="📝 Özet"
                  value={form.summary}
                  onChange={handleChange}
                  fullWidth
                  multiline
                  rows={4}
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="publishDate"
                  label="📅 Yayın Tarihi"
                  type="date"
                  value={form.publishDate}
                  onChange={handleChange}
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="pageCount"
                  label="📄 Sayfa Sayısı"
                  type="number"
                  value={form.pageCount}
                  onChange={handleChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="isbn"
                  label="🔢 ISBN"
                  value={form.isbn}
                  onChange={handleChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="language"
                  label="🌍 Dil"
                  value={form.language}
                  onChange={handleChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="categoryIDs"
                  label="🏷️ Kategoriler"
                  select
                  multiple
                  value={form.categoryIDs}
                  onChange={handleCategoryChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 2,
                      '&:hover fieldset': {
                        borderColor: '#667eea',
                      },
                      '&.Mui-focused fieldset': {
                        borderColor: '#667eea',
                      },
                    },
                  }}
                >
                  {categories.map((category) => (
                    <MenuItem key={category.id} value={category.id}>
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <Box sx={{
                          width: 12,
                          height: 12,
                          borderRadius: '50%',
                          bgcolor: '#667eea'
                        }} />
                        <Typography sx={{ 
                          fontSize: '0.9rem',
                          fontWeight: '500',
                          color: '#2c3e50',
                          whiteSpace: 'nowrap',
                          overflow: 'hidden',
                          textOverflow: 'ellipsis',
                          width: '100%'
                        }}>
                          {category.name}
                        </Typography>
                      </Box>
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
            </Grid>
          </DialogContent>

          <DialogActions sx={{ 
            p: 4, 
            pt: 2,
            background: 'linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%)',
            borderTop: '1px solid rgba(102, 126, 234, 0.1)'
          }}>
            <Button 
              onClick={() => setOpenDialog(false)}
              sx={{ 
                borderRadius: 3,
                px: 4,
                py: 1.5,
                fontWeight: 600,
                fontSize: '1rem',
                color: '#666',
                border: '2px solid #ddd',
                transition: 'all 0.3s ease',
                '&:hover': {
                  borderColor: '#999',
                  backgroundColor: 'rgba(0,0,0,0.05)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                }
              }}
            >
              ❌ İptal
            </Button>
            <Button
              type="submit"
              variant="contained"
              disabled={loading}
              sx={{ 
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                '&:hover': { 
                  background: 'linear-gradient(135deg, #5a6fd8 0%, #6a4c93 100%)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 8px 24px rgba(102, 126, 234, 0.4)'
                },
                '&:disabled': {
                  background: 'linear-gradient(135deg, #ccc 0%, #999 100%)',
                  transform: 'none',
                  boxShadow: 'none'
                },
                borderRadius: 3,
                px: 4,
                py: 1.5,
                fontWeight: 700,
                fontSize: '1rem',
                textTransform: 'none',
                boxShadow: '0 4px 12px rgba(102, 126, 234, 0.3)',
                transition: 'all 0.3s ease'
              }}
            >
              {loading ? (
                <CircularProgress size={24} sx={{ color: 'white' }} />
              ) : (
                editingBook ? '✅ Güncelle' : '📚 Ekle'
              )}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <Snackbar
        open={!!success || !!error}
        autoHideDuration={6000}
        onClose={() => { setSuccess(''); setError(''); }}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert 
          severity={success ? 'success' : 'error'} 
          onClose={() => { setSuccess(''); setError(''); }}
          sx={{ borderRadius: 2 }}
        >
          {success || error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default MyPublisher;
