import React, { useState, useEffect } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem, Alert, CircularProgress, Grid } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { fetchBooks, getAllUsers, updateUser, deleteUser } from '../api';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'Aşk', 'Distopya', 'Bilim Kurgu', 'Çocuk', 'Tarih', 'Polisiye'];

const emptyBook = { title: '', author: '', publishDate: '', pageCount: '', isbn: '', publisher: '', language: '', categories: [], cover: '' };
const emptyUser = { firstName: '', lastName: '', username: '', email: '', role: 'member', birthDate: '', address: '', publisherName: '', website: '', password: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);

  // Verileri yükle
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [booksResponse, usersResponse] = await Promise.all([
          fetchBooks(),
          getAllUsers()
        ]);
        setBooks(booksResponse.data);
        setUsers(usersResponse.data);
        setError(null);
      } catch (err) {
        setError('Veriler yüklenirken hata oluştu: ' + err.message);
        console.error('Veri yükleme hatası:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Kitap ekle/güncelle (API ile)
  const handleBookSave = () => {
    let newBooks;
    if (editBook) {
      newBooks = books.map(b => b.id === editBook.id ? { ...editBook, ...bookForm } : b);
    } else {
      newBooks = [...books, { id: Date.now(), ...bookForm }];
    }
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
    setOpenBookDialog(false);
    setEditBook(null);
    setBookForm(emptyBook);
  };
  // Kitap sil (localStorage ile kalıcı)
  const handleBookDelete = (id) => {
    const newBooks = books.filter(b => b.id !== id);
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
  };
  // Kitap düzenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      publishDate: book.publishDate,
      pageCount: book.pageCount,
      isbn: book.ISBN,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
      cover: book.cover || '',
    });
    setOpenBookDialog(true);
  };

  // Kullanıcı güncelle (API ile)
  const handleUserSave = async () => {
    try {
      if (editUser) {
        await updateUser(editUser.id, userForm);
        // Kullanıcı listesini güncelle
        const updatedUsers = users.map(u => u.id === editUser.id ? { ...u, ...userForm } : u);
        setUsers(updatedUsers);
      }
      setOpenUserDialog(false);
      setEditUser(null);
      setUserForm(emptyUser);
    } catch (err) {
      setError('Kullanıcı güncellenirken hata oluştu: ' + err.message);
      console.error('Kullanıcı güncelleme hatası:', err);
    }
  };

  // Kullanıcı sil (API ile)
  const handleUserDelete = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter(u => u.id !== id));
    } catch (err) {
      setError('Kullanıcı silinirken hata oluştu: ' + err.message);
      console.error('Kullanıcı silme hatası:', err);
    }
  };
  // Kullanıcı düzenle
  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      username: user.username || '',
      email: user.email || '',
      role: user.role || 'member',
      birthDate: user.birthDate || '',
      address: user.address || '',
      publisherName: user.publisherName || '',
      website: user.website || '',
      password: '' // Şifre güvenlik nedeniyle boş bırakılır
    });
    setOpenUserDialog(true);
  };

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <Tabs value={tab} onChange={(_, v) => setTab(v)} centered sx={{ mb: 3 }}>
              <Tab label="Kitaplar" />
              <Tab label="Kullanıcılar" />
            </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button variant="contained" startIcon={<AddIcon />} onClick={() => { setEditBook(null); setBookForm(emptyBook); setOpenBookDialog(true); }}>
                Kitap Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Başlık</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>Yayın Tarihi</TableCell>
                    <TableCell>Sayfa</TableCell>
                    <TableCell>ISBN</TableCell>
                    <TableCell>Yayınevi</TableCell>
                    <TableCell>Dil</TableCell>
                    <TableCell>Kapak</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {books.map(book => (
                    <TableRow key={book.ID}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{new Date(book.publishDate).toLocaleDateString('tr-TR')}</TableCell>
                      <TableCell>{book.pageCount}</TableCell>
                      <TableCell>{book.isbn || book.ISBN}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>{book.language}</TableCell>
                      <TableCell>
                        {book.cover && (
                          <img src={book.cover} alt="kapak" style={{ width: 40, height: 56, objectFit: 'contain', borderRadius: 4, boxShadow: '0 2px 8px #3a5ba022' }} />
                        )}
                      </TableCell>
                      <TableCell>
                        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map(cat => (
                            <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                          ))}
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleBookEdit(book)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleBookDelete(book.ID)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kitap Ekle/Güncelle Dialog */}
            <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)}>
              <DialogTitle>{editBook ? 'Kitap Güncelle' : 'Kitap Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <TextField label="Başlık" value={bookForm.title} onChange={e => setBookForm(f => ({ ...f, title: e.target.value }))} fullWidth required />
                  <TextField label="Yazar" value={bookForm.author} onChange={e => setBookForm(f => ({ ...f, author: e.target.value }))} fullWidth required />
                  <TextField 
                    label="Yayın Tarihi" 
                    type="date" 
                    value={bookForm.publishDate ? new Date(bookForm.publishDate).toISOString().split('T')[0] : ''} 
                    onChange={e => setBookForm(f => ({ ...f, publishDate: e.target.value }))} 
                    fullWidth 
                    required 
                    InputLabelProps={{ shrink: true }}
                  />
                  <TextField label="Sayfa Sayısı" type="number" value={bookForm.pageCount} onChange={e => setBookForm(f => ({ ...f, pageCount: parseInt(e.target.value) }))} fullWidth required />
                  <TextField label="ISBN" value={bookForm.isbn} onChange={e => setBookForm(f => ({ ...f, isbn: e.target.value }))} fullWidth required />
                  <TextField label="Yayınevi" value={bookForm.publisher} onChange={e => setBookForm(f => ({ ...f, publisher: e.target.value }))} fullWidth required />
                  <TextField label="Dil" value={bookForm.language} onChange={e => setBookForm(f => ({ ...f, language: e.target.value }))} fullWidth required />
                  <TextField label="Kapak URL" value={bookForm.cover || ''} onChange={e => setBookForm(f => ({ ...f, cover: e.target.value }))} fullWidth placeholder="https://..." />
                  <Autocomplete
                    multiple
                    options={allCategories}
                    value={bookForm.categories}
                    onChange={(_, newValue) => setBookForm(f => ({ ...f, categories: newValue }))}
                    renderInput={(params) => <TextField {...params} label="Kategoriler" placeholder="Kategori seç" />}
                    fullWidth
                  />
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenBookDialog(false)}>İptal</Button>
                <Button onClick={handleBookSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
        {/* Kullanıcılar Sekmesi */}
        {tab === 1 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button variant="contained" startIcon={<AddIcon />} onClick={() => { setEditUser(null); setUserForm(emptyUser); setOpenUserDialog(true); }}>
                Kullanıcı Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Rol</TableCell>
                    <TableCell>Ad</TableCell>
                    <TableCell>Soyad</TableCell>
                    <TableCell>Kullanıcı Adı</TableCell>
                    <TableCell>E-posta</TableCell>
                    <TableCell>Yayınevi Adı</TableCell>
                    <TableCell>Website</TableCell>
                    <TableCell>Kayıt Tarihi</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map(user => (
                    <TableRow key={user.id}>
                      <TableCell>
                        <Chip 
                          label={user.role === 'admin' ? 'Admin' : user.role === 'publisher' ? 'Yayınevi' : 'Üye'} 
                          color={user.role === 'admin' ? 'error' : user.role === 'publisher' ? 'warning' : 'info'}
                          size="small"
                        />
                      </TableCell>
                      <TableCell>{user.firstName}</TableCell>
                      <TableCell>{user.lastName}</TableCell>
                      <TableCell>{user.username}</TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell>{user.publisherName || '-'}</TableCell>
                      <TableCell>{user.website || '-'}</TableCell>
                      <TableCell>{new Date(user.createdAt).toLocaleDateString('tr-TR')}</TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleUserEdit(user)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleUserDelete(user.id)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kullanıcı Ekle/Güncelle Dialog */}
            <Dialog open={openUserDialog} onClose={() => setOpenUserDialog(false)} maxWidth="md" fullWidth>
              <DialogTitle>{editUser ? 'Kullanıcı Güncelle' : 'Kullanıcı Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <Grid container spacing={2}>
                    <Grid item xs={12} md={6}>
                      <TextField
                        select
                        label="Kullanıcı Rolü"
                        value={userForm.role}
                        onChange={e => setUserForm(f => ({ ...f, role: e.target.value }))}
                        fullWidth
                        required
                      >
                        <MenuItem value="member">Üye</MenuItem>
                        <MenuItem value="publisher">Yayınevi</MenuItem>
                        <MenuItem value="admin">Admin</MenuItem>
                      </TextField>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField 
                        label="Doğum Tarihi" 
                        type="date" 
                        value={userForm.birthDate ? new Date(userForm.birthDate).toISOString().split('T')[0] : ''} 
                        onChange={e => setUserForm(f => ({ ...f, birthDate: e.target.value }))} 
                        fullWidth 
                        required 
                        InputLabelProps={{ shrink: true }}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="Ad" value={userForm.firstName} onChange={e => setUserForm(f => ({ ...f, firstName: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="Soyad" value={userForm.lastName} onChange={e => setUserForm(f => ({ ...f, lastName: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="Kullanıcı Adı" value={userForm.username} onChange={e => setUserForm(f => ({ ...f, username: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="E-posta" value={userForm.email} onChange={e => setUserForm(f => ({ ...f, email: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField label="Adres" value={userForm.address} onChange={e => setUserForm(f => ({ ...f, address: e.target.value }))} fullWidth required multiline rows={2} />
                    </Grid>
                    {userForm.role === 'publisher' && (
                      <>
                        <Grid item xs={12} md={6}>
                          <TextField label="Yayınevi Adı" value={userForm.publisherName} onChange={e => setUserForm(f => ({ ...f, publisherName: e.target.value }))} fullWidth />
                        </Grid>
                        <Grid item xs={12} md={6}>
                          <TextField label="Website" value={userForm.website} onChange={e => setUserForm(f => ({ ...f, website: e.target.value }))} fullWidth />
                        </Grid>
                      </>
                    )}
                    {editUser && (
                      <Grid item xs={12}>
                        <TextField 
                          label="Yeni Şifre (Boş bırakılırsa değişmez)" 
                          type="password" 
                          value={userForm.password} 
                          onChange={e => setUserForm(f => ({ ...f, password: e.target.value }))} 
                          fullWidth 
                          helperText="Şifreyi değiştirmek istemiyorsanız boş bırakın"
                        />
                      </Grid>
                    )}
                  </Grid>
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenUserDialog(false)}>İptal</Button>
                <Button onClick={handleUserSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
          </>
        )}
      </Paper>
    </Box>
  );
};

export default AdminPanel;
