import React, { useState, useEffect } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem, Alert, CircularProgress, Grid } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import { fetchBooks, getAllUsers, updateUser, deleteUser, registerUser } from '../api';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'AÅŸk', 'Distopya', 'Bilim Kurgu', 'Ã‡ocuk', 'Tarih', 'Polisiye'];

const emptyBook = { title: '', author: '', publishDate: '', pageCount: '', isbn: '', publisher: '', language: '', categories: [], cover: '' };
const emptyUser = { firstName: '', lastName: '', username: '', email: '', role: 'member', birthDate: '', address: '', publisherName: '', website: '', password: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);

  // Verileri yÃ¼kle
  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [booksResponse, usersResponse] = await Promise.all([
          fetchBooks(),
          getAllUsers()
        ]);
        setBooks(booksResponse.data);
        setUsers(usersResponse.data);
        setError(null);
      } catch (err) {
        setError('Veriler yÃ¼klenirken hata oluÅŸtu: ' + err.message);
        console.error('Veri yÃ¼kleme hatasÄ±:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  // Kitap ekle/gÃ¼ncelle (API ile)
  const handleBookSave = () => {
    let newBooks;
    if (editBook) {
      newBooks = books.map(b => b.id === editBook.id ? { ...editBook, ...bookForm } : b);
    } else {
      newBooks = [...books, { id: Date.now(), ...bookForm }];
    }
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
    setOpenBookDialog(false);
    setEditBook(null);
    setBookForm(emptyBook);
  };
  // Kitap sil (localStorage ile kalÄ±cÄ±)
  const handleBookDelete = (id) => {
    const newBooks = books.filter(b => b.id !== id);
    setBooks(newBooks);
    localStorage.setItem('books', JSON.stringify(newBooks));
  };
  // Kitap dÃ¼zenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      publishDate: book.publishDate,
      pageCount: book.pageCount,
      isbn: book.ISBN,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
      cover: book.cover || '',
    });
    setOpenBookDialog(true);
  };

  // KullanÄ±cÄ± ekle/gÃ¼ncelle (API ile)
  const handleUserSave = async () => {
    try {
      if (editUser) {
        // KullanÄ±cÄ± gÃ¼ncelleme
        await updateUser(editUser.id, userForm);
        const updatedUsers = users.map(u => u.id === editUser.id ? { ...u, ...userForm } : u);
        setUsers(updatedUsers);
      } else {
        // Yeni kullanÄ±cÄ± ekleme
        const response = await registerUser(userForm);
        const newUser = response.data;
        setUsers([...users, newUser]);
      }
      setOpenUserDialog(false);
      setEditUser(null);
      setUserForm(emptyUser);
    } catch (err) {
      setError('KullanÄ±cÄ± iÅŸlemi sÄ±rasÄ±nda hata oluÅŸtu: ' + err.message);
      console.error('KullanÄ±cÄ± iÅŸlemi hatasÄ±:', err);
    }
  };

  // KullanÄ±cÄ± sil (API ile)
  const handleUserDelete = async (id) => {
    try {
      await deleteUser(id);
      setUsers(users.filter(u => u.id !== id));
    } catch (err) {
      setError('KullanÄ±cÄ± silinirken hata oluÅŸtu: ' + err.message);
      console.error('KullanÄ±cÄ± silme hatasÄ±:', err);
    }
  };
  // KullanÄ±cÄ± dÃ¼zenle
  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      username: user.username || '',
      email: user.email || '',
      role: user.role || 'member',
      birthDate: user.birthDate || '',
      address: user.address || '',
      publisherName: user.publisherName || '',
      website: user.website || '',
      password: '' // Åifre gÃ¼venlik nedeniyle boÅŸ bÄ±rakÄ±lÄ±r
    });
    setOpenUserDialog(true);
  };

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>

        {error && (
          <Alert severity="error" sx={{ mb: 2 }}>
            {error}
          </Alert>
        )}

        {loading ? (
          <Box sx={{ display: 'flex', justifyContent: 'center', py: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <>
            <Tabs value={tab} onChange={(_, v) => setTab(v)} centered sx={{ mb: 3 }}>
              <Tab label="Kitaplar" />
              <Tab label="KullanÄ±cÄ±lar" />
            </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 3 }}>
              <Button 
                variant="contained" 
                startIcon={<AddIcon />} 
                onClick={() => { setEditBook(null); setBookForm(emptyBook); setOpenBookDialog(true); }}
                sx={{ 
                  backgroundColor: '#2e7d32',
                  borderRadius: 3,
                  px: 4,
                  py: 1.5,
                  fontSize: 16,
                  fontWeight: 600,
                  textTransform: 'none',
                  boxShadow: '0 4px 12px rgba(46, 125, 50, 0.3)',
                  '&:hover': { 
                    backgroundColor: '#1b5e20',
                    boxShadow: '0 6px 16px rgba(46, 125, 50, 0.4)',
                    transform: 'translateY(-2px)'
                  },
                  transition: 'all 0.3s ease'
                }}
              >
                ğŸ“š Kitap Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>BaÅŸlÄ±k</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>YayÄ±n Tarihi</TableCell>
                    <TableCell>Sayfa</TableCell>
                    <TableCell>ISBN</TableCell>
                    <TableCell>YayÄ±nevi</TableCell>
                    <TableCell>Dil</TableCell>
                    <TableCell>Kapak</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell align="right">Ä°ÅŸlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {books.map(book => (
                    <TableRow key={book.ID}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{new Date(book.publishDate).toLocaleDateString('tr-TR')}</TableCell>
                      <TableCell>{book.pageCount}</TableCell>
                      <TableCell>{book.isbn || book.ISBN}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>{book.language}</TableCell>
                      <TableCell>
                        {book.cover && (
                          <img src={book.cover} alt="kapak" style={{ width: 40, height: 56, objectFit: 'contain', borderRadius: 4, boxShadow: '0 2px 8px #3a5ba022' }} />
                        )}
                      </TableCell>
                      <TableCell>
                        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map(cat => (
                            <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                          ))}
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleBookEdit(book)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleBookDelete(book.ID)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kitap Ekle/GÃ¼ncelle Dialog */}
            <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)}>
              <DialogTitle>{editBook ? 'Kitap GÃ¼ncelle' : 'Kitap Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <TextField label="BaÅŸlÄ±k" value={bookForm.title} onChange={e => setBookForm(f => ({ ...f, title: e.target.value }))} fullWidth required />
                  <TextField label="Yazar" value={bookForm.author} onChange={e => setBookForm(f => ({ ...f, author: e.target.value }))} fullWidth required />
                  <TextField 
                    label="YayÄ±n Tarihi" 
                    type="date" 
                    value={bookForm.publishDate ? new Date(bookForm.publishDate).toISOString().split('T')[0] : ''} 
                    onChange={e => setBookForm(f => ({ ...f, publishDate: e.target.value }))} 
                    fullWidth 
                    required 
                    InputLabelProps={{ shrink: true }}
                  />
                  <TextField label="Sayfa SayÄ±sÄ±" type="number" value={bookForm.pageCount} onChange={e => setBookForm(f => ({ ...f, pageCount: parseInt(e.target.value) }))} fullWidth required />
                  <TextField label="ISBN" value={bookForm.isbn} onChange={e => setBookForm(f => ({ ...f, isbn: e.target.value }))} fullWidth required />
                  <TextField label="YayÄ±nevi" value={bookForm.publisher} onChange={e => setBookForm(f => ({ ...f, publisher: e.target.value }))} fullWidth required />
                  <TextField label="Dil" value={bookForm.language} onChange={e => setBookForm(f => ({ ...f, language: e.target.value }))} fullWidth required />
                  <TextField label="Kapak URL" value={bookForm.cover || ''} onChange={e => setBookForm(f => ({ ...f, cover: e.target.value }))} fullWidth placeholder="https://..." />
                  <Autocomplete
                    multiple
                    options={allCategories}
                    value={bookForm.categories}
                    onChange={(_, newValue) => setBookForm(f => ({ ...f, categories: newValue }))}
                    renderInput={(params) => <TextField {...params} label="Kategoriler" placeholder="Kategori seÃ§" />}
                    fullWidth
                  />
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenBookDialog(false)}>Ä°ptal</Button>
                <Button onClick={handleBookSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
        {/* KullanÄ±cÄ±lar Sekmesi */}
        {tab === 1 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 3 }}>
              <Button 
                variant="contained" 
                startIcon={<AddIcon />} 
                onClick={() => { 
                  setEditUser(null); 
                  setUserForm({ ...emptyUser, role: 'member' }); 
                  setOpenUserDialog(true); 
                }}
                sx={{ 
                  backgroundColor: '#1976d2',
                  borderRadius: 3,
                  px: 4,
                  py: 1.5,
                  fontSize: 16,
                  fontWeight: 600,
                  textTransform: 'none',
                  boxShadow: '0 4px 12px rgba(25, 118, 210, 0.3)',
                  '&:hover': { 
                    backgroundColor: '#1565c0',
                    boxShadow: '0 6px 16px rgba(25, 118, 210, 0.4)',
                    transform: 'translateY(-2px)'
                  },
                  transition: 'all 0.3s ease'
                }}
              >
                ğŸ‘¤ Ãœye Ekle
              </Button>
            </Box>

            {/* Ãœyeler BÃ¶lÃ¼mÃ¼ */}
            <Paper sx={{ p: 3, mb: 3, borderRadius: 3, boxShadow: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ color: '#1976d2', fontWeight: 600, mb: 2 }}>
                ğŸ“š Ãœyeler ({users.filter(u => u.role === 'member').length})
              </Typography>
              <TableContainer component={Paper} sx={{ boxShadow: 1 }}>
                <Table size="small">
                  <TableHead>
                    <TableRow sx={{ backgroundColor: '#f5f5f5' }}>
                      <TableCell sx={{ fontWeight: 600 }}>Ad</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Soyad</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>KullanÄ±cÄ± AdÄ±</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>E-posta</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>KayÄ±t Tarihi</TableCell>
                      <TableCell align="right" sx={{ fontWeight: 600 }}>Ä°ÅŸlemler</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {users.filter(user => user.role === 'member').map(user => (
                      <TableRow key={user.id} sx={{ '&:hover': { backgroundColor: '#f8f9fa' } }}>
                        <TableCell>{user.firstName}</TableCell>
                        <TableCell>{user.lastName}</TableCell>
                        <TableCell>{user.username}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>{new Date(user.createdAt).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell align="right">
                          <IconButton color="info" onClick={() => handleUserEdit(user)} size="small"><EditIcon /></IconButton>
                          <IconButton color="error" onClick={() => handleUserDelete(user.id)} size="small"><DeleteIcon /></IconButton>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>

            {/* YayÄ±nevi Ekleme Butonu */}
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 3 }}>
              <Button 
                variant="contained" 
                startIcon={<AddIcon />} 
                onClick={() => { 
                  setEditUser(null); 
                  setUserForm({ ...emptyUser, role: 'publisher' }); 
                  setOpenUserDialog(true); 
                }}
                sx={{ 
                  backgroundColor: '#ed6c02',
                  borderRadius: 3,
                  px: 4,
                  py: 1.5,
                  fontSize: 16,
                  fontWeight: 600,
                  textTransform: 'none',
                  boxShadow: '0 4px 12px rgba(237, 108, 2, 0.3)',
                  '&:hover': { 
                    backgroundColor: '#d84315',
                    boxShadow: '0 6px 16px rgba(237, 108, 2, 0.4)',
                    transform: 'translateY(-2px)'
                  },
                  transition: 'all 0.3s ease'
                }}
              >
                ğŸ¢ YayÄ±nevi Ekle
              </Button>
            </Box>

            {/* YayÄ±nevleri BÃ¶lÃ¼mÃ¼ */}
            <Paper sx={{ p: 3, mb: 3, borderRadius: 3, boxShadow: 3 }}>
              <Typography variant="h6" gutterBottom sx={{ color: '#ed6c02', fontWeight: 600, mb: 2 }}>
                ğŸ¢ YayÄ±nevleri ({users.filter(u => u.role === 'publisher').length})
              </Typography>
              <TableContainer component={Paper} sx={{ boxShadow: 1 }}>
                <Table size="small">
                  <TableHead>
                    <TableRow sx={{ backgroundColor: '#fff3e0' }}>
                      <TableCell sx={{ fontWeight: 600 }}>YayÄ±nevi AdÄ±</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Yetkili KiÅŸi</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>KullanÄ±cÄ± AdÄ±</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>E-posta</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>Website</TableCell>
                      <TableCell sx={{ fontWeight: 600 }}>KayÄ±t Tarihi</TableCell>
                      <TableCell align="right" sx={{ fontWeight: 600 }}>Ä°ÅŸlemler</TableCell>
                    </TableRow>
                  </TableHead>
                  <TableBody>
                    {users.filter(user => user.role === 'publisher').map(user => (
                      <TableRow key={user.id} sx={{ '&:hover': { backgroundColor: '#fff8e1' } }}>
                        <TableCell sx={{ fontWeight: 500 }}>{user.publisherName || '-'}</TableCell>
                        <TableCell>{user.firstName} {user.lastName}</TableCell>
                        <TableCell>{user.username}</TableCell>
                        <TableCell>{user.email}</TableCell>
                        <TableCell>
                          {user.website ? (
                            <a href={user.website} target="_blank" rel="noopener noreferrer" style={{ color: '#1976d2', textDecoration: 'none' }}>
                              {user.website}
                            </a>
                          ) : '-'}
                        </TableCell>
                        <TableCell>{new Date(user.createdAt).toLocaleDateString('tr-TR')}</TableCell>
                        <TableCell align="right">
                          <IconButton color="info" onClick={() => handleUserEdit(user)} size="small"><EditIcon /></IconButton>
                          <IconButton color="error" onClick={() => handleUserDelete(user.id)} size="small"><DeleteIcon /></IconButton>
                        </TableCell>
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </TableContainer>
            </Paper>
            {/* KullanÄ±cÄ± Ekle/GÃ¼ncelle Dialog */}
            <Dialog open={openUserDialog} onClose={() => setOpenUserDialog(false)} maxWidth="md" fullWidth>
              <DialogTitle>
                {editUser ? 'KullanÄ±cÄ± GÃ¼ncelle' : (userForm.role === 'member' ? 'Ãœye Ekle' : 'YayÄ±nevi Ekle')}
              </DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <Grid container spacing={3}>
                    <Grid item xs={12}>
                      <Typography variant="h6" sx={{ 
                        color: userForm.role === 'member' ? '#1976d2' : '#ed6c02', 
                        fontWeight: 600, 
                        mb: 2,
                        textAlign: 'center',
                        py: 1,
                        borderRadius: 2,
                        backgroundColor: userForm.role === 'member' ? '#e3f2fd' : '#fff3e0'
                      }}>
                        {userForm.role === 'member' ? 'ğŸ‘¤ Ãœye Bilgileri' : 'ğŸ¢ YayÄ±nevi Bilgileri'}
                      </Typography>
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField 
                        label="DoÄŸum Tarihi" 
                        type="date" 
                        value={userForm.birthDate ? new Date(userForm.birthDate).toISOString().split('T')[0] : ''} 
                        onChange={e => setUserForm(f => ({ ...f, birthDate: e.target.value }))} 
                        fullWidth 
                        required 
                        InputLabelProps={{ shrink: true }}
                      />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="Ad" value={userForm.firstName} onChange={e => setUserForm(f => ({ ...f, firstName: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="Soyad" value={userForm.lastName} onChange={e => setUserForm(f => ({ ...f, lastName: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="KullanÄ±cÄ± AdÄ±" value={userForm.username} onChange={e => setUserForm(f => ({ ...f, username: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12} md={6}>
                      <TextField label="E-posta" value={userForm.email} onChange={e => setUserForm(f => ({ ...f, email: e.target.value }))} fullWidth required />
                    </Grid>
                    <Grid item xs={12}>
                      <TextField label="Adres" value={userForm.address} onChange={e => setUserForm(f => ({ ...f, address: e.target.value }))} fullWidth required multiline rows={2} />
                    </Grid>
                    {userForm.role === 'publisher' && (
                      <>
                        <Grid item xs={12}>
                          <Typography variant="subtitle1" sx={{ 
                            color: '#ed6c02', 
                            fontWeight: 600, 
                            mb: 1,
                            mt: 2,
                            borderLeft: '4px solid #ed6c02',
                            pl: 2
                          }}>
                            ğŸ¢ YayÄ±nevi Ã–zel Bilgileri
                          </Typography>
                        </Grid>
                        <Grid item xs={12} md={6}>
                          <TextField 
                            label="YayÄ±nevi AdÄ±" 
                            value={userForm.publisherName} 
                            onChange={e => setUserForm(f => ({ ...f, publisherName: e.target.value }))} 
                            fullWidth 
                            required
                            helperText="YayÄ±nevinin resmi adÄ±"
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                '&:hover fieldset': {
                                  borderColor: '#ed6c02',
                                },
                                '&.Mui-focused fieldset': {
                                  borderColor: '#ed6c02',
                                },
                              },
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} md={6}>
                          <TextField 
                            label="Website" 
                            value={userForm.website} 
                            onChange={e => setUserForm(f => ({ ...f, website: e.target.value }))} 
                            fullWidth 
                            placeholder="https://www.example.com"
                            helperText="YayÄ±nevinin web sitesi (opsiyonel)"
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                '&:hover fieldset': {
                                  borderColor: '#ed6c02',
                                },
                                '&.Mui-focused fieldset': {
                                  borderColor: '#ed6c02',
                                },
                              },
                            }}
                          />
                        </Grid>
                      </>
                    )}
                    {editUser && (
                      <Grid item xs={12}>
                        <TextField 
                          label="Yeni Åifre (BoÅŸ bÄ±rakÄ±lÄ±rsa deÄŸiÅŸmez)" 
                          type="password" 
                          value={userForm.password} 
                          onChange={e => setUserForm(f => ({ ...f, password: e.target.value }))} 
                          fullWidth 
                          helperText="Åifreyi deÄŸiÅŸtirmek istemiyorsanÄ±z boÅŸ bÄ±rakÄ±n"
                        />
                      </Grid>
                    )}
                  </Grid>
                </Stack>
              </DialogContent>
              <DialogActions sx={{ p: 3, pt: 1, gap: 2 }}>
                <Button 
                  onClick={() => setOpenUserDialog(false)}
                  variant="outlined"
                  sx={{ 
                    borderColor: '#666',
                    color: '#666',
                    borderRadius: 2,
                    px: 3,
                    py: 1.2,
                    fontSize: 14,
                    fontWeight: 500,
                    textTransform: 'none',
                    '&:hover': { 
                      borderColor: '#333',
                      backgroundColor: '#f5f5f5',
                      transform: 'translateY(-1px)'
                    },
                    transition: 'all 0.2s ease'
                  }}
                >
                  âŒ Ä°ptal
                </Button>
                <Button 
                  onClick={handleUserSave} 
                  variant="contained"
                  sx={{ 
                    backgroundColor: userForm.role === 'member' ? '#1976d2' : '#ed6c02',
                    borderRadius: 2,
                    px: 4,
                    py: 1.2,
                    fontSize: 14,
                    fontWeight: 600,
                    textTransform: 'none',
                    boxShadow: userForm.role === 'member' 
                      ? '0 3px 8px rgba(25, 118, 210, 0.3)' 
                      : '0 3px 8px rgba(237, 108, 2, 0.3)',
                    '&:hover': { 
                      backgroundColor: userForm.role === 'member' ? '#1565c0' : '#d84315',
                      boxShadow: userForm.role === 'member' 
                        ? '0 4px 12px rgba(25, 118, 210, 0.4)' 
                        : '0 4px 12px rgba(237, 108, 2, 0.4)',
                      transform: 'translateY(-1px)'
                    },
                    transition: 'all 0.2s ease'
                  }}
                >
                  {editUser ? 'âœ… GÃ¼ncelle' : 'ğŸ’¾ Kaydet'}
                </Button>
              </DialogActions>
            </Dialog>
          </>
        )}
          </>
        )}
      </Paper>
    </Box>
  );
};

export default AdminPanel;
