import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  Alert, 
  CircularProgress,
  Grid,
  Card,
  CardContent,
  Avatar,
  Divider,
  Chip,
  Stack
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { updateUser } from '../api';
import { LoginContext } from '../App';
import PersonIcon from '@mui/icons-material/Person';
import EmailIcon from '@mui/icons-material/Email';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import EditIcon from '@mui/icons-material/Edit';
import SaveIcon from '@mui/icons-material/Save';

const Profile = () => {
  const navigate = useNavigate();
  const { isLoggedIn } = useContext(LoginContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    birthDate: '',
    email: '',
    address: '',
    username: '',
    password: '',
    role: ''
  });

  useEffect(() => {
    // Kullanıcı giriş yapmamışsa ana sayfaya yönlendir
    if (!isLoggedIn) {
      navigate('/');
      return;
    }

    // localStorage'dan kullanıcı bilgilerini al
    const userData = JSON.parse(localStorage.getItem('user') || '{}');
    setUser(userData);
    
    // Form'u kullanıcı bilgileriyle doldur
    setForm({
      firstName: userData.firstName || '',
      lastName: userData.lastName || '',
      birthDate: userData.birthDate ? new Date(userData.birthDate).toISOString().split('T')[0] : '',
      email: userData.email || '',
      address: userData.address || '',
      username: userData.username || '',
      password: '', // Şifre boş bırakılır
      role: userData.role || ''
    });
  }, [isLoggedIn, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Şifre boşsa güncelleme verilerinden çıkar
      const updateData = { ...form };
      if (!updateData.password) {
        delete updateData.password;
      }

      const response = await updateUser(user.id, updateData);
      
      // localStorage'ı güncelle
      const updatedUser = response.data;
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      
      setSuccess('Profil bilgileriniz başarıyla güncellendi!');
      
    } catch (error) {
      setError(error.response?.data || 'Güncelleme sırasında bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return null;
  }

  const roleLabel = {
    'member': 'Üye',
    'publisher': 'Yayınevi',
    'admin': 'Admin'
  };

  const roleColor = {
    'member': '#4ecdc4',
    'publisher': '#ff6b6b',
    'admin': '#3a5ba0'
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Belirtilmemiş';
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: "linear-gradient(135deg, rgba(30,40,60,0.85) 0%, rgba(78,205,196,0.15) 100%), url('/library-bg.jpg') center center/cover no-repeat fixed",
        color: '#f7f4ed',
        py: 6,
        px: 2,
      }}
    >
      <Box sx={{ maxWidth: 1600, mx: 'auto' }}>
        <Typography 
          variant="h2" 
          align="center" 
          gutterBottom
          sx={{
            background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 6,
            textShadow: '0 4px 20px rgba(0,0,0,0.3)',
          }}
        >
          Profil Bilgilerim
        </Typography>

        <Grid container spacing={4} justifyContent="space-between">
          {/* Profil Kartı - En sol */}
          <Grid item xs={12} md={4}>
            <Card sx={{ 
              bgcolor: 'rgba(255,255,255,0.95)', 
              backdropFilter: 'blur(20px)',
              borderRadius: 4,
              boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
              border: '2px solid #4ecdc4',
              overflow: 'visible',
              position: 'relative',
              height: 'fit-content'
            }}>
              <Box sx={{
                position: 'absolute',
                top: -15,
                left: '50%',
                transform: 'translateX(-50%)',
                width: 100,
                height: 100,
                borderRadius: '50%',
                background: 'linear-gradient(135deg, #3a5ba0 0%, #4ecdc4 100%)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                boxShadow: '0 8px 25px rgba(0,0,0,0.3)',
                border: '3px solid white'
              }}>
                <PersonIcon sx={{ fontSize: 50, color: 'white' }} />
              </Box>
              
              <CardContent sx={{ textAlign: 'center', pt: 6, pb: 3 }}>
                <Typography variant="h5" gutterBottom sx={{ 
                  color: '#333', 
                  fontWeight: 700,
                  mb: 1
                }}>
                  {user?.firstName} {user?.lastName}
                </Typography>
                
                <Typography variant="body1" color="text.secondary" gutterBottom sx={{ mb: 2 }}>
                  @{user?.username}
                </Typography>
                
                <Chip 
                  label={roleLabel[user?.role]} 
                  sx={{ 
                    bgcolor: roleColor[user?.role],
                    color: 'white',
                    fontWeight: 600,
                    fontSize: '0.9rem',
                    px: 2,
                    mb: 2
                  }} 
                />
                
                <Divider sx={{ my: 2 }} />
                
                <Stack spacing={1.5} sx={{ textAlign: 'left' }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                    <EmailIcon sx={{ color: '#4ecdc4', fontSize: 16 }} />
                    <Box>
                      <Typography variant="caption" color="text.secondary" sx={{ fontWeight: 600 }}>
                        E-posta
                      </Typography>
                      <Typography variant="body2" sx={{ color: '#333', fontSize: '0.8rem' }}>
                        {user?.email}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                    <LocationOnIcon sx={{ color: '#4ecdc4', fontSize: 16 }} />
                    <Box>
                      <Typography variant="caption" color="text.secondary" sx={{ fontWeight: 600 }}>
                        Adres
                      </Typography>
                      <Typography variant="body2" sx={{ color: '#333', fontSize: '0.8rem' }}>
                        {user?.address || 'Belirtilmemiş'}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                    <CalendarTodayIcon sx={{ color: '#4ecdc4', fontSize: 16 }} />
                    <Box>
                      <Typography variant="caption" color="text.secondary" sx={{ fontWeight: 600 }}>
                        Doğum Tarihi
                      </Typography>
                      <Typography variant="body2" sx={{ color: '#333', fontSize: '0.8rem' }}>
                        {formatDate(user?.birthDate)}
                      </Typography>
                    </Box>
                  </Box>
                  
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5 }}>
                    <CalendarTodayIcon sx={{ color: '#4ecdc4', fontSize: 16 }} />
                    <Box>
                      <Typography variant="caption" color="text.secondary" sx={{ fontWeight: 600 }}>
                        Üyelik Tarihi
                      </Typography>
                      <Typography variant="body2" sx={{ color: '#333', fontSize: '0.8rem' }}>
                        {formatDate(user?.createdAt)}
                      </Typography>
                    </Box>
                  </Box>
                </Stack>
              </CardContent>
            </Card>
          </Grid>

          {/* Güncelleme Formu - Sağ taraf */}
          <Grid item xs={12} md={8}>
            <Paper sx={{ 
              p: 4, 
              bgcolor: 'rgba(255,255,255,0.95)', 
              backdropFilter: 'blur(20px)',
              borderRadius: 4,
              boxShadow: '0 20px 40px rgba(0,0,0,0.1)',
              border: '1px solid rgba(255,255,255,0.2)',
              position: 'relative'
            }}>
              {error && <Alert severity="error" sx={{ mb: 3, borderRadius: 2 }}>{error}</Alert>}
              {success && <Alert severity="success" sx={{ mb: 3, borderRadius: 2 }}>{success}</Alert>}
              
              <form onSubmit={handleSubmit}>
                <Grid container spacing={2}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Ad"
                      name="firstName"
                      variant="outlined"
                      fullWidth
                      value={form.firstName}
                      onChange={handleChange}
                      required
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Soyad"
                      name="lastName"
                      variant="outlined"
                      fullWidth
                      value={form.lastName}
                      onChange={handleChange}
                      required
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Doğum Tarihi"
                      name="birthDate"
                      type="date"
                      variant="outlined"
                      fullWidth
                      value={form.birthDate}
                      onChange={handleChange}
                      InputLabelProps={{ shrink: true }}
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="E-posta"
                      name="email"
                      type="email"
                      variant="outlined"
                      fullWidth
                      value={form.email}
                      onChange={handleChange}
                      required
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      label="Adres"
                      name="address"
                      variant="outlined"
                      fullWidth
                      value={form.address}
                      onChange={handleChange}
                      multiline
                      rows={2}
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Kullanıcı Adı"
                      name="username"
                      variant="outlined"
                      fullWidth
                      value={form.username}
                      onChange={handleChange}
                      required
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Yeni Şifre (Opsiyonel)"
                      name="password"
                      type="password"
                      variant="outlined"
                      fullWidth
                      value={form.password}
                      onChange={handleChange}
                      helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                      sx={{ 
                        bgcolor: 'rgba(255,255,255,0.8)', 
                        borderRadius: 2,
                        '& .MuiOutlinedInput-root': {
                          '&:hover fieldset': {
                            borderColor: '#4ecdc4',
                          },
                          '&.Mui-focused fieldset': {
                            borderColor: '#3a5ba0',
                          },
                        },
                      }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      fullWidth
                      disabled={loading}
                      startIcon={loading ? <CircularProgress size={20} color="inherit" /> : <SaveIcon />}
                      sx={{
                        py: 1.5,
                        fontWeight: 700,
                        fontSize: 16,
                        borderRadius: 3,
                        boxShadow: '0 8px 25px rgba(78,205,196,0.3)',
                        background: 'linear-gradient(135deg, #3a5ba0 0%, #4ecdc4 100%)',
                        backgroundSize: '200% 100%',
                        transition: 'all 0.3s ease',
                        '&:hover': {
                          background: 'linear-gradient(135deg, #4ecdc4 0%, #3a5ba0 100%)',
                          transform: 'translateY(-2px)',
                          boxShadow: '0 12px 35px rgba(78,205,196,0.4)',
                        },
                        '&:disabled': {
                          background: 'linear-gradient(135deg, #3a5ba0 0%, #4ecdc4 100%)',
                          transform: 'none',
                        }
                      }}
                    >
                      {loading ? 'Güncelleniyor...' : 'Bilgileri Güncelle'}
                    </Button>
                  </Grid>
                </Grid>
              </form>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default Profile;
