import React, { useEffect, useState } from 'react';
import { Container, Typography, Card, CardContent, CardActions, Button, Box, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Chip, Stack, Autocomplete, TextField, Paper, MenuItem } from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';

const categoryColors = {
