import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardActions, Button, Box, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Chip, Stack, Autocomplete, TextField } from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'Aşk', 'Distopya', 'Bilim Kurgu', 'Çocuk', 'Tarih', 'Polisiye'];

const dummyBooks = [
  {
    id: 1,
    title: 'Sefiller',
    author: 'Victor Hugo',
    summary: 'Fransız Devrimi sonrası Paris’te geçen, adalet, merhamet ve insanlık üzerine unutulmaz bir başyapıt.',
    year: 1862,
    pages: 1463,
    isbn: '9789750738606',
    publisher: 'Can Yayınları',
    language: 'Fransızca',
    categories: ['Roman', 'Klasik'],
  },
  {
    id: 2,
    title: 'Suç ve Ceza',
    author: 'Fyodor Dostoyevski',
    summary: 'Bir cinayetin psikolojik derinliklerinde kaybolan Raskolnikov’un vicdan ve kefaret savaşı.',
    year: 1866,
    pages: 687,
    isbn: '9786053609396',
    publisher: 'İş Bankası Kültür Yayınları',
    language: 'Rusça',
    categories: ['Roman', 'Psikoloji'],
  },
  {
    id: 3,
    title: 'Kürk Mantolu Madonna',
    author: 'Sabahattin Ali',
    summary: 'Raif Efendi’nin Berlin’de yaşadığı unutulmaz aşkın, yalnızlık ve içsel yolculukla harmanlanmış öyküsü.',
    year: 1943,
    pages: 160,
    isbn: '9789753638026',
    publisher: 'Yapı Kredi Yayınları',
    language: 'Türkçe',
    categories: ['Roman', 'Aşk'],
  },
  {
    id: 4,
    title: '1984',
    author: 'George Orwell',
    summary: 'Baskıcı bir rejimde özgürlük ve bireysellik için verilen mücadeleyi anlatan distopik bir klasik.',
    year: 1949,
    pages: 352,
    isbn: '9789750718530',
    publisher: 'Can Yayınları',
    language: 'İngilizce',
    categories: ['Distopya', 'Bilim Kurgu'],
  },
];

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);
  const [selectedBook, setSelectedBook] = useState(null);
  const [selectedCategory, setSelectedCategory] = useState('Tümü');

  useEffect(() => {
    setTimeout(() => {
      setBooks(dummyBooks);
      setLoading(false);
    }, 500);
  }, []);

  const filteredBooks = selectedCategory === 'Tümü'
    ? books
    : books.filter(book => book.categories && book.categories.includes(selectedCategory));

  if (loading) return <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><span>Yükleniyor...</span></Box>;

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Kitaplar</Typography>
      <Box sx={{ mb: 3, maxWidth: 320 }}>
        <Autocomplete
          options={['Tümü', ...allCategories]}
          value={selectedCategory}
          onChange={(_, newValue) => setSelectedCategory(newValue || 'Tümü')}
          renderInput={(params) => <TextField {...params} label="Kategoriye göre filtrele" variant="outlined" />}
        />
      </Box>
      <Grid container spacing={3}>
        {filteredBooks.map(book => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
            <Card
              sx={{
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-between',
                p: 2,
                position: 'relative',
                background: 'rgba(255,255,255,0.65)',
                boxShadow: '0 8px 32px 0 #3a5ba022',
                backdropFilter: 'blur(8px)',
                border: '1.5px solid rgba(58,91,160,0.10)',
                borderRadius: 5,
                transition: 'box-shadow 0.22s, transform 0.22s, border 0.22s',
                '&:hover': {
                  boxShadow: '0 16px 48px 0 #4ecdc422',
                  border: '2.5px solid #4ecdc4',
                  transform: 'scale(1.045)',
                  background: 'rgba(255,255,255,0.92)',
                },
              }}
            >
              <Box sx={{ position: 'absolute', top: 16, right: 16, bgcolor: 'secondary.main', color: 'white', borderRadius: '50%', p: 1, boxShadow: 2 }}>
                <MenuBookIcon fontSize="small" />
              </Box>
              <CardContent sx={{ flexGrow: 1, display: 'flex', flexDirection: 'column', gap: 1 }}>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 700 }}>{book.title}</Typography>
                <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                  {book.categories && book.categories.map(cat => (
                    <Chip key={cat} label={cat} size="small" sx={{ fontWeight: 600, fontSize: 13, background: 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)', color: '#fff', letterSpacing: '0.03em' }} />
                  ))}
                </Stack>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>{book.author}</Typography>
                <Typography variant="body2" color="text.secondary">{book.summary}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary" onClick={() => setSelectedBook(book)} sx={{ fontWeight: 700, borderRadius: 2 }}>Detay</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <Stack direction="row" spacing={1} sx={{ mb: 2, flexWrap: 'wrap' }}>
            {selectedBook?.categories?.map(cat => (
              <Chip key={cat} label={cat} size="small" sx={{ fontWeight: 600, fontSize: 13, background: 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)', color: '#fff', letterSpacing: '0.03em' }} />
            ))}
          </Stack>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Container>
  );
};

export default BookList;
