const Home = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [notFound, setNotFound] = useState(false);

  // Arama fonksiyonu
  const handleSearch = () => {
    const found = allBooks.find(b => b.title.toLowerCase() === (searchValue || inputValue).toLowerCase());
    if (found) {
      setSelectedBook(found);
      setNotFound(false);
    } else {
      setSelectedBook(null);
      setNotFound(true);
    }
  };

  return (
    <Box>
      {/* Hero Section */}
      <Box sx={{
        width: '100%',
        minHeight: 420,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'transparent', // Arka planı şeffaf bırak
        position: 'relative',
        overflow: 'hidden',
        borderRadius: 4,
        boxShadow: 3,
        mb: 8,
        py: { xs: 10, md: 14 },
        px: 2,
      }}>
        {/* SVG ve illüstrasyonları kaldırdık */}
        <Typography
          variant="h2"
          align="center"
          gutterBottom
          sx={{
            fontFamily: 'Playfair Display, Montserrat, serif',
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 2,
            color: '#fff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 1.5,
            zIndex: 2,
            fontSize: { xs: 32, sm: 48, md: 56 },
            textShadow: '0 4px 24px #1e283c99',
          }}
        >
          Kent Kütüphanesi
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ fontFamily: 'Montserrat, Playfair Display, serif', color: '#f7f4ed', opacity: 0.98, mb: 4, fontWeight: 500, zIndex: 2, textShadow: '0 2px 12px #1e283c88', fontStyle: 'italic', letterSpacing: '0.01em' }}>
          Gerçek bir kütüphane atmosferinde bilgiye ulaşın.
        </Typography>
        <Paper elevation={12} sx={{
          mt: 0.5,
          width: '0%',
          maxWidth: 420,
          minWidth: 0,
          overflow: 'hidden',
          p: { xs: 2, sm: 3 },
          borderRadius: 3,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 4px 18px 0 #1e283c33',
          backdropFilter: 'blur(8px)',
          background: 'rgba(30,40,60,0.82)',
          border: '1.5px solid #e0d7c6',
          zIndex: 3,
          transition: 'box-shadow 0.22s, border 0.22s, background 0.22s',
          '&:hover': {
            boxShadow: '0 8px 32px 0 #1e283c55',
            border: '2px solid #4ecdc4',
            background: 'rgba(30,40,60,0.92)',
          },
        }}>
          <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1, letterSpacing: '0.01em', opacity: 0.92, fontFamily: 'Montserrat, Playfair Display, serif' }}>
            Kütüphanede kitap ara
          </Typography>
          <Autocomplete
            freeSolo
            options={allBooks.map(b => b.title)}
            value={searchValue}
            onChange={(_, newValue) => setSearchValue(newValue || '')}
            inputValue={inputValue}
            onInputChange={(_, newInputValue) => setInputValue(newInputValue)}
            sx={{ width: '100%', mb: 2, maxWidth: 360, minWidth: 0, overflow: 'hidden', '& .MuiAutocomplete-listbox': { borderRadius: 2, boxShadow: 3, bgcolor: '#fff' } }}
            renderInput={(params) => (
              <TextField {...params} label="Kitap ara..." variant="outlined" sx={{ bgcolor: 'rgba(255,255,255,0.98)', borderRadius: 3, width: '100%', maxWidth: 360, minWidth: 0, overflow: 'hidden', border: '2px solid #e0d7c6', fontSize: 18, fontFamily: 'Montserrat, Playfair Display, serif', boxShadow: '0 2px 12px 0 #1e283c22', transition: 'border 0.18s, box-shadow 0.18s', '&:hover': { border: '2px solid #4ecdc4', boxShadow: '0 4px 16px 0 #4ecdc422' } }} />
            )}
            ListboxProps={{ style: { boxShadow: '0 4px 16px 0 #e0d7c622', borderRadius: 12 } }}
          />
          <Button variant="contained" color="primary" sx={{ fontWeight: 800, px: 5, width: '100%', py: 1.5, fontSize: 20, borderRadius: 3, boxShadow: '0 4px 16px 0 #3a5ba022', bgcolor: '#3a5ba0', letterSpacing: '0.04em', fontFamily: 'Montserrat, Playfair Display, serif', mt: 1, transition: 'all 0.18s', '&:hover': { bgcolor: '#4ecdc4', color: '#22335b', boxShadow: '0 8px 32px 0 #4ecdc422' } }} onClick={handleSearch}>
            Ara
          </Button>
          {notFound && (
            <Alert severity="warning" sx={{ mt: 2, width: '100%' }}>Aradığınız kitap bulunamadı.</Alert>
          )}
        </Paper>
      </Box>
      {/* Featured Books */}
      <Typography variant="h5" gutterBottom sx={{ mt: 4, mb: 2, fontWeight: 700, color: '#fff', letterSpacing: '0.03em', textShadow: '0 2px 8px #1e283c88' }}>Öne Çıkan Kitaplar</Typography>
      <Grid container spacing={3}>
        {featuredBooks.map(book => (
          <Grid item xs={12} sm={4} key={book.id}>
            <Paper
              elevation={6}
              sx={{
                p: 3,
                borderRadius: 5,
                bgcolor: 'rgba(30,40,60,0.82)',
                boxShadow: '0 8px 32px 0 #1e283c22',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'flex-start',
                gap: 1,
                position: 'relative',
                minHeight: 160,
                background: book.cover
                  ? `url('${book.cover}') center center/cover no-repeat`
                  : 'rgba(30,40,60,0.82)',
                overflow: 'hidden',
                transition: 'box-shadow 0.18s, background 0.18s',
                cursor: 'pointer',
                '&:hover': {
                  boxShadow: '0 16px 48px 0 #1e283c44',
                  bgcolor: 'rgba(30,40,60,0.92)',
                  background: book.cover
                    ? `url('${book.cover}') center center/cover no-repeat`
                    : 'rgba(30,40,60,0.92)',
                },
              }}
              onClick={() => setSelectedBook(book)}
            >
              {/* Arka plan görseli için overlay (blur ve opacity) */}
              {book.cover && (
                <Box sx={{
                  position: 'absolute',
                  inset: 0,
                  zIndex: 0,
                  background: 'rgba(30,40,60,0.62)',
                  transition: 'background 0.22s',
                  pointerEvents: 'none',
                }} />
              )}
              <Box sx={{ position: 'relative', zIndex: 1, width: '100%' }}>
                <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                  {book.categories && book.categories.map(cat => (
                    <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                  ))}
                </Stack>
                <Typography variant="h6" sx={{ fontWeight: 700, color: '#fff', textShadow: '0 2px 8px #1e283c88' }}>{book.title}</Typography>
                <Typography variant="subtitle2" color="#e0d7c6" gutterBottom>{book.author}</Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <Stack direction="row" spacing={1} sx={{ mb: 2, flexWrap: 'wrap' }}>
            {selectedBook?.categories?.map(cat => (
              <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
            ))}
          </Stack>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Home;
