import React, { useState, useEffect, useContext } from 'react';
import {
  Box,
  Typography,
  TextField,
  Button,
  Paper,
  Alert,
  CircularProgress,
  Grid,
  Avatar,
  Divider,
  Chip,
  Stack
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { updateUser } from '../api';
import { LoginContext } from '../App';
import PersonIcon from '@mui/icons-material/Person';
import BusinessIcon from '@mui/icons-material/Business';
import VerifiedUserIcon from '@mui/icons-material/VerifiedUser';
import EmailIcon from '@mui/icons-material/Email';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import LanguageIcon from '@mui/icons-material/Language';
import SaveIcon from '@mui/icons-material/Save';

const Profile = () => {
  const navigate = useNavigate();
  const { isLoggedIn } = useContext(LoginContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    birthDate: '',
    email: '',
    address: '',
    username: '',
    password: '',
    role: '',
    publisherName: '',
    website: ''
  });

  useEffect(() => {
    if (!isLoggedIn) {
      navigate('/');
      return;
    }
    const userData = JSON.parse(localStorage.getItem('user') || '{}');
    setUser(userData);
    setForm({
      firstName: userData.firstName || '',
      lastName: userData.lastName || '',
      birthDate: userData.birthDate ? new Date(userData.birthDate).toISOString().split('T')[0] : '',
      email: userData.email || '',
      address: userData.address || '',
      username: userData.username || '',
      password: '',
      role: userData.role || '',
      publisherName: userData.publisherName || '',
      website: userData.website || ''
    });
  }, [isLoggedIn, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');
    try {
      const updateData = { ...form };
      if (!updateData.password) delete updateData.password;
      const response = await updateUser(user.id, updateData);
      const updatedUser = response.data;
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      setSuccess('Profil bilgileriniz başarıyla güncellendi!');
    } catch (error) {
      setError(error.response?.data || 'Güncelleme sırasında bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) return null;

  const roleLabel = {
    'member': 'Üye',
    'publisher': 'Yayınevi',
    'admin': 'Admin'
  };
  const roleIcon = {
    'member': <PersonIcon fontSize="large" />,
    'publisher': <BusinessIcon fontSize="large" />,
    'admin': <VerifiedUserIcon fontSize="large" />
  };
  const roleColor = {
    'member': '#6c63ff',
    'publisher': '#43aa8b',
    'admin': '#e07a5f'
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Belirtilmemiş';
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const isPublisher = user?.role === 'publisher';

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f8fafc 0%, #e0e7ef 100%)',
        py: { xs: 2, md: 6 },
        px: { xs: 1, md: 4 }
      }}
    >
      <Grid container spacing={4} justifyContent="center">
        <Grid item xs={12} md={10} lg={8}>
          <Paper
            elevation={0}
            sx={{
              borderRadius: 5,
              p: { xs: 2, md: 5 },
              background: 'rgba(255,255,255,0.95)',
              boxShadow: '0 8px 32px 0 rgba(60,60,120,0.08)'
            }}
          >
            <Grid container spacing={4} alignItems="center">
              {/* Profil Kartı */}
              <Grid item xs={12} md={4}>
                <Box sx={{ textAlign: 'center' }}>
                  <Avatar
                    sx={{
                      width: 110,
                      height: 110,
                      mx: 'auto',
                      mb: 2,
                      bgcolor: roleColor[user?.role] || '#6c63ff',
                      boxShadow: '0 4px 24px 0 rgba(60,60,120,0.10)'
                    }}
                  >
                    {roleIcon[user?.role]}
                  </Avatar>
                  <Typography variant="h5" sx={{ fontWeight: 700, color: '#222', mb: 0.5 }}>
                    {isPublisher ? user?.publisherName : `${user?.firstName} ${user?.lastName}`}
                  </Typography>
                  {isPublisher && (
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                      Yetkili: {user?.firstName} {user?.lastName}
                    </Typography>
                  )}
                  {!isPublisher && (
                    <Typography variant="body2" color="text.secondary" sx={{ mb: 1 }}>
                      @{user?.username}
                    </Typography>
                  )}
                  <Chip
                    label={roleLabel[user?.role]}
                    sx={{
                      bgcolor: roleColor[user?.role] || '#6c63ff',
                      color: '#fff',
                      fontWeight: 600,
                      fontSize: '1rem',
                      px: 2,
                      mt: 1,
                      borderRadius: 2
                    }}
                  />
                  <Divider sx={{ my: 2 }} />
                  <Stack spacing={1.5} sx={{ textAlign: 'left', maxWidth: 260, mx: 'auto' }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <EmailIcon sx={{ color: '#6c63ff', fontSize: 18 }} />
                      <Typography variant="body2" color="text.secondary">
                        {user?.email}
                      </Typography>
                    </Box>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                      <LocationOnIcon sx={{ color: '#6c63ff', fontSize: 18 }} />
                      <Typography variant="body2" color="text.secondary">
                        {user?.address || 'Belirtilmemiş'}
                      </Typography>
                    </Box>
                    {isPublisher && user?.website && (
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <LanguageIcon sx={{ color: '#6c63ff', fontSize: 18 }} />
                        <Typography variant="body2" color="text.secondary">
                          {user.website}
                        </Typography>
                      </Box>
                    )}
                    {!isPublisher && (
                      <Box sx={{ display: 'flex', alignItems: 'center', gap: 1 }}>
                        <CalendarTodayIcon sx={{ color: '#6c63ff', fontSize: 18 }} />
                        <Typography variant="body2" color="text.secondary">
                          {formatDate(user?.birthDate)}
                        </Typography>
                      </Box>
                    )}
                  </Stack>
                </Box>
              </Grid>
              {/* Profil Formu */}
              <Grid item xs={12} md={8}>
                {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
                {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
                <form onSubmit={handleSubmit}>
                  <Grid container spacing={2}>
                    {isPublisher ? (
                      <>
                        <Grid item xs={12}>
                          <TextField
                            label="Yayınevi Adı"
                            name="publisherName"
                            variant="outlined"
                            fullWidth
                            value={form.publisherName}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12}>
                          <TextField
                            label="Yetkili Kişi Adı Soyadı"
                            name="firstName"
                            variant="outlined"
                            fullWidth
                            value={`${form.firstName} ${form.lastName}`.trim()}
                            onChange={e => {
                              const fullName = e.target.value;
                              const nameParts = fullName.split(' ');
                              const firstName = nameParts[0] || '';
                              const lastName = nameParts.slice(1).join(' ') || '';
                              setForm({ ...form, firstName, lastName });
                            }}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="E-posta"
                            name="email"
                            type="email"
                            variant="outlined"
                            fullWidth
                            value={form.email}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Web Sitesi (Opsiyonel)"
                            name="website"
                            variant="outlined"
                            fullWidth
                            value={form.website}
                            onChange={handleChange}
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12}>
                          <TextField
                            label="Adres"
                            name="address"
                            variant="outlined"
                            fullWidth
                            value={form.address}
                            onChange={handleChange}
                            multiline
                            rows={2}
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Kullanıcı Adı"
                            name="username"
                            variant="outlined"
                            fullWidth
                            value={form.username}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Yeni Şifre (Opsiyonel)"
                            name="password"
                            type="password"
                            variant="outlined"
                            fullWidth
                            value={form.password}
                            onChange={handleChange}
                            helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                      </>
                    ) : (
                      <>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Ad"
                            name="firstName"
                            variant="outlined"
                            fullWidth
                            value={form.firstName}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Soyad"
                            name="lastName"
                            variant="outlined"
                            fullWidth
                            value={form.lastName}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Doğum Tarihi"
                            name="birthDate"
                            type="date"
                            variant="outlined"
                            fullWidth
                            value={form.birthDate}
                            onChange={handleChange}
                            InputLabelProps={{ shrink: true }}
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="E-posta"
                            name="email"
                            type="email"
                            variant="outlined"
                            fullWidth
                            value={form.email}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12}>
                          <TextField
                            label="Adres"
                            name="address"
                            variant="outlined"
                            fullWidth
                            value={form.address}
                            onChange={handleChange}
                            multiline
                            rows={2}
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Kullanıcı Adı"
                            name="username"
                            variant="outlined"
                            fullWidth
                            value={form.username}
                            onChange={handleChange}
                            required
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                        <Grid item xs={12} sm={6}>
                          <TextField
                            label="Yeni Şifre (Opsiyonel)"
                            name="password"
                            type="password"
                            variant="outlined"
                            fullWidth
                            value={form.password}
                            onChange={handleChange}
                            helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                            sx={{
                              '& .MuiOutlinedInput-root': {
                                borderRadius: 2,
                                bgcolor: 'rgba(245,247,250,0.7)',
                                '&:hover': { bgcolor: 'rgba(245,247,250,0.95)' }
                              }
                            }}
                          />
                        </Grid>
                      </>
                    )}
                    <Grid item xs={12}>
                      <Button
                        type="submit"
                        variant="contained"
                        fullWidth
                        disabled={loading}
                        startIcon={loading ? <CircularProgress size={20} color="inherit" /> : <SaveIcon />}
                        sx={{
                          py: 2,
                          fontWeight: 700,
                          fontSize: 16,
                          borderRadius: 3,
                          background: 'linear-gradient(135deg, #667eea 0%, #43aa8b 100%)',
                          color: '#fff',
                          boxShadow: '0 4px 16px 0 rgba(102,126,234,0.10)',
                          transition: 'all 0.2s ease',
                          '&:hover': {
                            background: 'linear-gradient(135deg, #6c63ff 0%, #43aa8b 100%)',
                            boxShadow: '0 8px 24px 0 rgba(102,126,234,0.15)'
                          }
                        }}
                      >
                        {loading ? 'Güncelleniyor...' : 'Bilgileri Kaydet'}
                      </Button>
                    </Grid>
                  </Grid>
                </form>
              </Grid>
            </Grid>
          </Paper>
        </Grid>
      </Grid>
    </Box>
  );
};

export default Profile;
