import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  Alert, 
  CircularProgress,
  Grid,
  Card,
  CardContent,
  Avatar,
  Divider,
  Chip,
  Stack,
  IconButton,
  Tooltip,
  Fade,
  Slide,
  Container
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { updateUser } from '../api';
import { LoginContext } from '../App';
import PersonIcon from '@mui/icons-material/Person';
import BusinessIcon from '@mui/icons-material/Business';
import EmailIcon from '@mui/icons-material/Email';
import LocationOnIcon from '@mui/icons-material/LocationOn';
import CalendarTodayIcon from '@mui/icons-material/CalendarToday';
import LanguageIcon from '@mui/icons-material/Language';
import EditIcon from '@mui/icons-material/Edit';
import SaveIcon from '@mui/icons-material/Save';
import AccountCircleIcon from '@mui/icons-material/AccountCircle';
import SecurityIcon from '@mui/icons-material/Security';
import ContactSupportIcon from '@mui/icons-material/ContactSupport';
import VerifiedUserIcon from '@mui/icons-material/VerifiedUser';
import StarIcon from '@mui/icons-material/Star';
import BookmarkIcon from '@mui/icons-material/Bookmark';
import HistoryIcon from '@mui/icons-material/History';

const Profile = () => {
  const navigate = useNavigate();
  const { isLoggedIn } = useContext(LoginContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    birthDate: '',
    email: '',
    address: '',
    username: '',
    password: '',
    role: '',
    publisherName: '',
    website: ''
  });

  useEffect(() => {
    if (!isLoggedIn) {
      navigate('/');
      return;
    }

    const userData = JSON.parse(localStorage.getItem('user') || '{}');
    setUser(userData);
    
    setForm({
      firstName: userData.firstName || '',
      lastName: userData.lastName || '',
      birthDate: userData.birthDate ? new Date(userData.birthDate).toISOString().split('T')[0] : '',
      email: userData.email || '',
      address: userData.address || '',
      username: userData.username || '',
      password: '',
      role: userData.role || '',
      publisherName: userData.publisherName || '',
      website: userData.website || ''
    });
  }, [isLoggedIn, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const updateData = { ...form };
      if (!updateData.password) {
        delete updateData.password;
      }

      const response = await updateUser(user.id, updateData);
      
      const updatedUser = response.data;
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      
      setSuccess('Profil bilgileriniz başarıyla güncellendi!');
      setIsEditing(false);
      
    } catch (error) {
      setError(error.response?.data || 'Güncelleme sırasında bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return null;
  }

  const roleLabel = {
    'member': 'Üye',
    'publisher': 'Yayınevi',
    'admin': 'Admin'
  };

  const roleColor = {
    'member': '#4ecdc4',
    'publisher': '#ff6b6b',
    'admin': '#3a5ba0'
  };

  const roleIcon = {
    'member': <PersonIcon />,
    'publisher': <BusinessIcon />,
    'admin': <VerifiedUserIcon />
  };

  const formatDate = (dateString) => {
    if (!dateString) return 'Belirtilmemiş';
    return new Date(dateString).toLocaleDateString('tr-TR', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const isPublisher = user?.role === 'publisher';

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        py: 4,
        px: 2,
        position: 'relative',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'url("/library-bg.jpg") center center/cover no-repeat fixed',
          opacity: 0.1,
          zIndex: 0
        }
      }}
    >
      <Container maxWidth="lg" sx={{ position: 'relative', zIndex: 1 }}>
        {/* Header Section */}
        <Fade in timeout={800}>
          <Box sx={{ textAlign: 'center', mb: 6 }}>
            <Typography 
              variant="h2" 
              sx={{
                color: 'white',
                fontWeight: 900,
                letterSpacing: '0.04em',
                mb: 2,
                textShadow: '0 4px 20px rgba(0,0,0,0.3)',
                fontSize: { xs: '2.5rem', md: '3.5rem' }
              }}
            >
              👤 Profil Merkezi
            </Typography>
            <Typography 
              variant="h6" 
              sx={{
                color: 'rgba(255,255,255,0.9)',
                fontWeight: 300,
                fontSize: { xs: '1rem', md: '1.2rem' }
              }}
            >
              Kişisel bilgilerinizi yönetin ve hesabınızı güvenli tutun
            </Typography>
          </Box>
        </Fade>

        {/* Alerts */}
        {error && (
          <Slide direction="down" in={!!error} timeout={500}>
            <Alert severity="error" sx={{ mb: 3, borderRadius: 3, boxShadow: '0 8px 32px rgba(0,0,0,0.1)' }}>
              {error}
            </Alert>
          </Slide>
        )}
        {success && (
          <Slide direction="down" in={!!success} timeout={500}>
            <Alert severity="success" sx={{ mb: 3, borderRadius: 3, boxShadow: '0 8px 32px rgba(0,0,0,0.1)' }}>
              {success}
            </Alert>
          </Slide>
        )}

        <Grid container spacing={4}>
          {/* Profile Card - Left Side */}
          <Grid item xs={12} lg={4}>
            <Slide direction="right" in timeout={1000}>
              <Card sx={{ 
                bgcolor: 'rgba(255,255,255,0.95)', 
                backdropFilter: 'blur(20px)',
                borderRadius: 4,
                boxShadow: '0 20px 60px rgba(0,0,0,0.15)',
                border: '1px solid rgba(255,255,255,0.2)',
                overflow: 'visible',
                position: 'relative',
                height: 'fit-content'
              }}>
                {/* Profile Avatar */}
                <Box sx={{
                  position: 'relative',
                  textAlign: 'center',
                  pt: 4,
                  pb: 2
                }}>
                  <Avatar
                    sx={{
                      width: 120,
                      height: 120,
                      mx: 'auto',
                      mb: 2,
                      background: `linear-gradient(135deg, ${roleColor[user?.role]} 0%, ${roleColor[user?.role]}dd 100%)`,
                      boxShadow: '0 8px 32px rgba(0,0,0,0.2)',
                      border: '4px solid white',
                      fontSize: '3rem'
                    }}
                  >
                    {roleIcon[user?.role]}
                  </Avatar>
                  
                  <Typography variant="h4" sx={{ 
                    color: '#2c3e50', 
                    fontWeight: 800,
                    mb: 1
                  }}>
                    {isPublisher ? user?.publisherName : `${user?.firstName} ${user?.lastName}`}
                  </Typography>
                  
                  <Chip 
                    icon={roleIcon[user?.role]}
                    label={roleLabel[user?.role]} 
                    sx={{ 
                      bgcolor: roleColor[user?.role],
                      color: 'white',
                      fontWeight: 700,
                      fontSize: '1rem',
                      px: 2,
                      py: 1,
                      mb: 2,
                      boxShadow: '0 4px 12px rgba(0,0,0,0.15)'
                    }} 
                  />

                  {isPublisher && (
                    <Typography variant="body1" color="text.secondary" sx={{ mb: 2, fontStyle: 'italic' }}>
                      Yetkili: {user?.firstName} {user?.lastName}
                    </Typography>
                  )}
                </Box>

                <Divider sx={{ mx: 3, mb: 3 }} />

                {/* Quick Stats */}
                <Box sx={{ px: 3, pb: 3 }}>
                  <Typography variant="h6" sx={{ mb: 2, fontWeight: 700, color: '#2c3e50' }}>
                    📊 Hesap İstatistikleri
                  </Typography>
                  
                  <Stack spacing={2}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Box sx={{
                        p: 1,
                        borderRadius: 2,
                        bgcolor: 'rgba(78, 205, 196, 0.1)',
                        color: '#4ecdc4'
                      }}>
                        <StarIcon />
                      </Box>
                      <Box>
                        <Typography variant="body2" color="text.secondary">Üyelik Durumu</Typography>
                        <Typography variant="body1" sx={{ fontWeight: 600 }}>
                          Aktif
                        </Typography>
                      </Box>
                    </Box>

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Box sx={{
                        p: 1,
                        borderRadius: 2,
                        bgcolor: 'rgba(255, 107, 107, 0.1)',
                        color: '#ff6b6b'
                      }}>
                        <BookmarkIcon />
                      </Box>
                      <Box>
                        <Typography variant="body2" color="text.secondary">Favori Kitaplar</Typography>
                        <Typography variant="body1" sx={{ fontWeight: 600 }}>
                          12 Kitap
                        </Typography>
                      </Box>
                    </Box>

                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Box sx={{
                        p: 1,
                        borderRadius: 2,
                        bgcolor: 'rgba(58, 91, 160, 0.1)',
                        color: '#3a5ba0'
                      }}>
                        <HistoryIcon />
                      </Box>
                      <Box>
                        <Typography variant="body2" color="text.secondary">Ödünç Alınan</Typography>
                        <Typography variant="body1" sx={{ fontWeight: 600 }}>
                          8 Kitap
                        </Typography>
                      </Box>
                    </Box>
                  </Stack>
                </Box>
              </Card>
            </Slide>
          </Grid>

          {/* Profile Form - Right Side */}
          <Grid item xs={12} lg={8}>
            <Slide direction="left" in timeout={1200}>
              <Card sx={{ 
                bgcolor: 'rgba(255,255,255,0.95)', 
                backdropFilter: 'blur(20px)',
                borderRadius: 4,
                boxShadow: '0 20px 60px rgba(0,0,0,0.15)',
                border: '1px solid rgba(255,255,255,0.2)',
                overflow: 'hidden'
              }}>
                {/* Form Header */}
                <Box sx={{
                  p: 3,
                  background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                  color: 'white',
                  position: 'relative',
                  overflow: 'hidden'
                }}>
                  <Box sx={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                    <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                      <Box sx={{
                        p: 1.5,
                        borderRadius: 3,
                        bgcolor: 'rgba(255,255,255,0.2)',
                        backdropFilter: 'blur(10px)'
                      }}>
                        <ContactSupportIcon />
                      </Box>
                      <Box>
                        <Typography variant="h5" sx={{ fontWeight: 800, mb: 0.5 }}>
                          {isEditing ? '📝 Profil Düzenle' : '👤 Kişisel Bilgiler'}
                        </Typography>
                        <Typography variant="body2" sx={{ opacity: 0.9 }}>
                          {isEditing ? 'Bilgilerinizi güncelleyin' : 'Hesap bilgilerinizi görüntüleyin'}
                        </Typography>
                      </Box>
                    </Box>
                    
                    {!isEditing && (
                      <Tooltip title="Profili Düzenle">
                        <IconButton
                          onClick={() => setIsEditing(true)}
                          sx={{
                            color: 'white',
                            bgcolor: 'rgba(255,255,255,0.2)',
                            '&:hover': { bgcolor: 'rgba(255,255,255,0.3)' }
                          }}
                        >
                          <EditIcon />
                        </IconButton>
                      </Tooltip>
                    )}
                  </Box>
                </Box>

                {/* Form Content */}
                <Box sx={{ p: 4 }}>
                  <form onSubmit={handleSubmit}>
                    <Grid container spacing={3}>
                      {isPublisher ? (
                        // Publisher Form Fields
                        <>
                          <Grid item xs={12}>
                            <TextField
                              label="🏢 Yayınevi Adı"
                              name="publisherName"
                              variant="outlined"
                              fullWidth
                              value={form.publisherName}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>
                          
                          <Grid item xs={12}>
                            <TextField
                              label="👤 Yetkili Kişi Adı Soyadı"
                              name="firstName"
                              variant="outlined"
                              fullWidth
                              value={`${form.firstName} ${form.lastName}`.trim()}
                              onChange={(e) => {
                                const fullName = e.target.value;
                                const nameParts = fullName.split(' ');
                                const firstName = nameParts[0] || '';
                                const lastName = nameParts.slice(1).join(' ') || '';
                                setForm({ ...form, firstName, lastName });
                              }}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="📧 E-posta"
                              name="email"
                              type="email"
                              variant="outlined"
                              fullWidth
                              value={form.email}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="🌐 Web Sitesi (Opsiyonel)"
                              name="website"
                              variant="outlined"
                              fullWidth
                              value={form.website}
                              onChange={handleChange}
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12}>
                            <TextField
                              label="📍 Adres"
                              name="address"
                              variant="outlined"
                              fullWidth
                              value={form.address}
                              onChange={handleChange}
                              multiline
                              rows={2}
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="👤 Kullanıcı Adı"
                              name="username"
                              variant="outlined"
                              fullWidth
                              value={form.username}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="🔒 Yeni Şifre (Opsiyonel)"
                              name="password"
                              type="password"
                              variant="outlined"
                              fullWidth
                              value={form.password}
                              onChange={handleChange}
                              disabled={!isEditing}
                              helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>
                        </>
                      ) : (
                        // Member Form Fields
                        <>
                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="👤 Ad"
                              name="firstName"
                              variant="outlined"
                              fullWidth
                              value={form.firstName}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="👤 Soyad"
                              name="lastName"
                              variant="outlined"
                              fullWidth
                              value={form.lastName}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="📅 Doğum Tarihi"
                              name="birthDate"
                              type="date"
                              variant="outlined"
                              fullWidth
                              value={form.birthDate}
                              onChange={handleChange}
                              InputLabelProps={{ shrink: true }}
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="📧 E-posta"
                              name="email"
                              type="email"
                              variant="outlined"
                              fullWidth
                              value={form.email}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12}>
                            <TextField
                              label="📍 Adres"
                              name="address"
                              variant="outlined"
                              fullWidth
                              value={form.address}
                              onChange={handleChange}
                              multiline
                              rows={2}
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="👤 Kullanıcı Adı"
                              name="username"
                              variant="outlined"
                              fullWidth
                              value={form.username}
                              onChange={handleChange}
                              required
                              disabled={!isEditing}
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>

                          <Grid item xs={12} sm={6}>
                            <TextField
                              label="🔒 Yeni Şifre (Opsiyonel)"
                              name="password"
                              type="password"
                              variant="outlined"
                              fullWidth
                              value={form.password}
                              onChange={handleChange}
                              disabled={!isEditing}
                              helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                              sx={{ 
                                '& .MuiOutlinedInput-root': {
                                  borderRadius: 2,
                                  bgcolor: isEditing ? 'rgba(255,255,255,0.8)' : 'rgba(248,249,250,0.8)',
                                  '&:hover fieldset': {
                                    borderColor: '#667eea',
                                  },
                                  '&.Mui-focused fieldset': {
                                    borderColor: '#667eea',
                                  },
                                },
                              }}
                            />
                          </Grid>
                        </>
                      )}
                      
                      {/* Action Buttons */}
                      {isEditing && (
                        <Grid item xs={12}>
                          <Box sx={{ display: 'flex', gap: 2, justifyContent: 'flex-end', pt: 2 }}>
                            <Button
                              onClick={() => setIsEditing(false)}
                              variant="outlined"
                              sx={{
                                borderRadius: 3,
                                px: 4,
                                py: 1.5,
                                fontWeight: 600,
                                fontSize: '1rem',
                                borderColor: '#ddd',
                                color: '#666',
                                '&:hover': {
                                  borderColor: '#999',
                                  bgcolor: 'rgba(0,0,0,0.05)'
                                }
                              }}
                            >
                              ❌ İptal
                            </Button>
                            <Button
                              type="submit"
                              variant="contained"
                              disabled={loading}
                              startIcon={loading ? <CircularProgress size={20} color="inherit" /> : <SaveIcon />}
                              sx={{
                                borderRadius: 3,
                                px: 4,
                                py: 1.5,
                                fontWeight: 700,
                                fontSize: '1rem',
                                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                                boxShadow: '0 8px 25px rgba(102, 126, 234, 0.3)',
                                '&:hover': {
                                  background: 'linear-gradient(135deg, #5a6fd8 0%, #6a4c93 100%)',
                                  transform: 'translateY(-2px)',
                                  boxShadow: '0 12px 35px rgba(102, 126, 234, 0.4)',
                                },
                                '&:disabled': {
                                  background: 'linear-gradient(135deg, #ccc 0%, #999 100%)',
                                  transform: 'none',
                                }
                              }}
                            >
                              {loading ? 'Güncelleniyor...' : '💾 Kaydet'}
                            </Button>
                          </Box>
                        </Grid>
                      )}
                    </Grid>
                  </form>
                </Box>
              </Card>
            </Slide>
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Profile;
