import React, { useState, useEffect, useContext } from 'react';
import { 
  Box, 
  Typography, 
  TextField, 
  Button, 
  Paper, 
  Alert, 
  CircularProgress,
  Grid,
  Card,
  CardContent,
  Avatar,
  Divider
} from '@mui/material';
import { useNavigate } from 'react-router-dom';
import { updateUser } from '../api';
import { LoginContext } from '../App';
import PersonIcon from '@mui/icons-material/Person';

const Profile = () => {
  const navigate = useNavigate();
  const { isLoggedIn } = useContext(LoginContext);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [user, setUser] = useState(null);
  const [form, setForm] = useState({
    firstName: '',
    lastName: '',
    birthDate: '',
    email: '',
    address: '',
    username: '',
    password: '',
    role: ''
  });

  useEffect(() => {
    // Kullanıcı giriş yapmamışsa ana sayfaya yönlendir
    if (!isLoggedIn) {
      navigate('/');
      return;
    }

    // localStorage'dan kullanıcı bilgilerini al
    const userData = JSON.parse(localStorage.getItem('user') || '{}');
    setUser(userData);
    
    // Form'u kullanıcı bilgileriyle doldur
    setForm({
      firstName: userData.firstName || '',
      lastName: userData.lastName || '',
      birthDate: userData.birthDate ? new Date(userData.birthDate).toISOString().split('T')[0] : '',
      email: userData.email || '',
      address: userData.address || '',
      username: userData.username || '',
      password: '', // Şifre boş bırakılır
      role: userData.role || ''
    });
  }, [isLoggedIn, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      // Şifre boşsa güncelleme verilerinden çıkar
      const updateData = { ...form };
      if (!updateData.password) {
        delete updateData.password;
      }

      const response = await updateUser(user.id, updateData);
      
      // localStorage'ı güncelle
      const updatedUser = response.data;
      localStorage.setItem('user', JSON.stringify(updatedUser));
      setUser(updatedUser);
      
      setSuccess('Profil bilgileriniz başarıyla güncellendi!');
      
    } catch (error) {
      setError(error.response?.data || 'Güncelleme sırasında bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  };

  if (!isLoggedIn) {
    return null;
  }

  const roleLabel = {
    'member': 'Üye',
    'publisher': 'Yayınevi',
    'admin': 'Admin'
  };

  return (
    <Box
      sx={{
        minHeight: '100vh',
        background: "linear-gradient(rgba(30,40,60,0.72), rgba(30,40,60,0.62)), url('/library-bg.jpg') center center/cover no-repeat fixed",
        color: '#f7f4ed',
        py: 6,
        px: 2,
      }}
    >
      <Box sx={{ maxWidth: 1200, mx: 'auto' }}>
        <Typography 
          variant="h3" 
          align="center" 
          gutterBottom
          sx={{
            background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
            WebkitBackgroundClip: 'text',
            WebkitTextFillColor: 'transparent',
            fontWeight: 800,
            letterSpacing: '0.04em',
            mb: 4,
          }}
        >
          Profil Bilgilerim
        </Typography>

        <Grid container spacing={4}>
          {/* Profil Kartı */}
          <Grid item xs={12} md={4}>
            <Card sx={{ 
              bgcolor: 'rgba(255,255,255,0.9)', 
              backdropFilter: 'blur(10px)',
              borderRadius: 3,
              boxShadow: 6
            }}>
              <CardContent sx={{ textAlign: 'center', py: 4 }}>
                <Avatar 
                  sx={{ 
                    width: 120, 
                    height: 120, 
                    mx: 'auto', 
                    mb: 2,
                    bgcolor: 'primary.main',
                    fontSize: '3rem'
                  }}
                >
                  <PersonIcon sx={{ fontSize: 60 }} />
                </Avatar>
                <Typography variant="h5" gutterBottom sx={{ color: '#333', fontWeight: 600 }}>
                  {user?.firstName} {user?.lastName}
                </Typography>
                <Typography variant="body1" color="text.secondary" gutterBottom>
                  @{user?.username}
                </Typography>
                <Divider sx={{ my: 2 }} />
                <Typography variant="body2" color="text.secondary">
                  <strong>E-posta:</strong> {user?.email}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  <strong>Kullanıcı Türü:</strong> {roleLabel[user?.role]}
                </Typography>
                <Typography variant="body2" color="text.secondary" sx={{ mt: 1 }}>
                  <strong>Üyelik Tarihi:</strong> {user?.birthDate ? new Date(user?.birthDate).toLocaleDateString('tr-TR') : 'Belirtilmemiş'}
                </Typography>
              </CardContent>
            </Card>
          </Grid>

          {/* Güncelleme Formu */}
          <Grid item xs={12} md={8}>
            <Paper sx={{ 
              p: 4, 
              bgcolor: 'rgba(255,255,255,0.9)', 
              backdropFilter: 'blur(10px)',
              borderRadius: 3,
              boxShadow: 6
            }}>
              <Typography variant="h5" gutterBottom sx={{ color: '#333', fontWeight: 600, mb: 3 }}>
                Bilgilerimi Güncelle
              </Typography>
              
              {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
              {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}
              
              <form onSubmit={handleSubmit}>
                <Grid container spacing={3}>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Ad"
                      name="firstName"
                      variant="outlined"
                      fullWidth
                      value={form.firstName}
                      onChange={handleChange}
                      required
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Soyad"
                      name="lastName"
                      variant="outlined"
                      fullWidth
                      value={form.lastName}
                      onChange={handleChange}
                      required
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Doğum Tarihi"
                      name="birthDate"
                      type="date"
                      variant="outlined"
                      fullWidth
                      value={form.birthDate}
                      onChange={handleChange}
                      InputLabelProps={{ shrink: true }}
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="E-posta"
                      name="email"
                      type="email"
                      variant="outlined"
                      fullWidth
                      value={form.email}
                      onChange={handleChange}
                      required
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <TextField
                      label="Adres"
                      name="address"
                      variant="outlined"
                      fullWidth
                      value={form.address}
                      onChange={handleChange}
                      multiline
                      rows={3}
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Kullanıcı Adı"
                      name="username"
                      variant="outlined"
                      fullWidth
                      value={form.username}
                      onChange={handleChange}
                      required
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12} sm={6}>
                    <TextField
                      label="Yeni Şifre (Opsiyonel)"
                      name="password"
                      type="password"
                      variant="outlined"
                      fullWidth
                      value={form.password}
                      onChange={handleChange}
                      helperText="Şifrenizi değiştirmek istemiyorsanız boş bırakın"
                      sx={{ bgcolor: 'rgba(255,255,255,0.8)', borderRadius: 1 }}
                    />
                  </Grid>
                  <Grid item xs={12}>
                    <Button
                      type="submit"
                      variant="contained"
                      color="primary"
                      fullWidth
                      disabled={loading}
                      sx={{
                        py: 1.5,
                        fontWeight: 700,
                        fontSize: 18,
                        borderRadius: 2,
                        boxShadow: '0 4px 12px 0 #4ecdc422',
                        background: 'linear-gradient(90deg, #3a5ba0 0%, #4ecdc4 100%)',
                        '&:hover': {
                          background: 'linear-gradient(90deg, #4ecdc4 0%, #3a5ba0 100%)',
                        },
                      }}
                    >
                      {loading ? <CircularProgress size={24} color="inherit" /> : 'Bilgileri Güncelle'}
                    </Button>
                  </Grid>
                </Grid>
              </form>
            </Paper>
          </Grid>
        </Grid>
      </Box>
    </Box>
  );
};

export default Profile;
