import React, { useState } from 'react';
import { Box, Typography, TextField, Button, Grid, Dialog, DialogTitle, DialogContent, DialogContentText, DialogActions, Autocomplete, Paper, Alert, Stack, Chip } from '@mui/material';
import MenuBookIcon from '@mui/icons-material/MenuBook';
import AutoStoriesIcon from '@mui/icons-material/AutoStories';

const featuredBooks = [
  {
    id: 1,
    title: 'Sefiller',
    author: 'Victor Hugo',
    year: 1862,
    pages: 1463,
    isbn: '9789750738606',
    publisher: 'Can Yayınları',
    language: 'Fransızca',
    categories: ['Roman', 'Klasik'],
  },
  {
    id: 2,
    title: 'Suç ve Ceza',
    author: 'Fyodor Dostoyevski',
    year: 1866,
    pages: 687,
    isbn: '9786053609396',
    publisher: 'İş Bankası Kültür Yayınları',
    language: 'Rusça',
    categories: ['Roman', 'Psikoloji'],
  },
  {
    id: 3,
    title: 'Kürk Mantolu Madonna',
    author: 'Sabahattin Ali',
    year: 1943,
    pages: 160,
    isbn: '9789753638026',
    publisher: 'Yapı Kredi Yayınları',
    language: 'Türkçe',
    categories: ['Roman', 'Aşk'],
  },
];

const allBooks = [
  ...featuredBooks,
  // Buraya daha fazla kitap eklenebilir
];

const BookShelfSVG = () => (
  <svg width="100%" height="180" viewBox="0 0 800 180" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ position: 'absolute', bottom: 0, left: 0, zIndex: 0, opacity: 0.22, filter: 'blur(1.5px)' }}>
    <rect x="0" y="120" width="800" height="20" rx="10" fill="#b08968" />
    <rect x="40" y="60" width="30" height="60" rx="6" fill="#3a5ba0" />
    <rect x="80" y="80" width="24" height="40" rx="5" fill="#ffd166" />
    <rect x="120" y="70" width="28" height="50" rx="5" fill="#ffb6b9" />
    <rect x="170" y="90" width="20" height="30" rx="4" fill="#4ecdc4" />
    <rect x="210" y="60" width="32" height="60" rx="6" fill="#6c63ff" />
    <rect x="260" y="80" width="24" height="40" rx="5" fill="#a3a380" />
    <rect x="300" y="70" width="28" height="50" rx="5" fill="#b5838d" />
    <rect x="350" y="90" width="20" height="30" rx="4" fill="#22223b" />
    <rect x="390" y="60" width="32" height="60" rx="6" fill="#ffb86b" />
    <rect x="440" y="80" width="24" height="40" rx="5" fill="#3a5ba0" />
    <rect x="480" y="70" width="28" height="50" rx="5" fill="#ffd166" />
    <rect x="530" y="90" width="20" height="30" rx="4" fill="#ffb6b9" />
    <rect x="570" y="60" width="32" height="60" rx="6" fill="#4ecdc4" />
    <rect x="620" y="80" width="24" height="40" rx="5" fill="#b08968" />
    <rect x="660" y="70" width="28" height="50" rx="5" fill="#6c63ff" />
    <rect x="710" y="90" width="20" height="30" rx="4" fill="#a3a380" />
  </svg>
);

// Kitaplarla ilgili estetik cümleler
const bookQuotes = [
  '“Bir kitap bir insanı değiştirebilir.”',
  '“Okumak, ruhun kanatlarını açmaktır.”',
  '“Kitaplar, sessiz dostlarımızdır.”',
  '“Her kitap yeni bir dünyadır.”',
  '“Kütüphaneler, medeniyetin hafızasıdır.”',
  '“Bir kitap, bin hayat yaşatır.”',
  '“Okuyan insan, düşünen insandır.”',
  '“Kitaplar, geçmişin fısıltısıdır.”',
  '“Hayat kısa, kitaplar sonsuz.”',
  '“Bir kütüphane, bir hazine odasıdır.”',
  '“Kitaplar, hayal gücünün anahtarıdır.”',
  '“Okumak, insanı özgürleştirir.”',
  '“Bir kitap, en iyi yol arkadaşıdır.”',
  '“Kitaplar, zamanın ötesine geçer.”',
  '“Her kitap, yeni bir yolculuktur.”',
];

// Ekrana random 5 cümle yerleştir
function getRandomQuotes(quotes, count = 5) {
  const shuffled = [...quotes].sort(() => 0.5 - Math.random());
  return shuffled.slice(0, count);
}

// Her cümle için random konum (yüzdelik)
function getRandomPositions(count) {
  const positions = [];
  for (let i = 0; i < count; i++) {
    // Y: 10-70 arası, X: 5-80 arası (responsive overlap az)
    const top = 10 + Math.random() * 60;
    const left = 5 + Math.random() * 75;
    positions.push({ top: `${top}%`, left: `${left}%` });
  }
  return positions;
}

// Estetik fontlar
const quoteFonts = [
  'Playfair Display, serif',
  'Dancing Script, cursive',
  'Montserrat, sans-serif',
  'Merriweather, serif',
  'Caveat, cursive',
  'Quicksand, sans-serif',
  'Lobster, cursive',
];

// Her cümleye random font ve renk seç
function getQuoteStyles(count) {
  const palette = [
    '#fff', '#e0d7c6', '#4ecdc4', '#ffd166', '#b08968', '#3a5ba0', '#ffb6b9', '#90caf9', '#b5838d', '#6c63ff', '#ffb86b', '#a3a380', '#22223b'
  ];
  const styles = [];
  for (let i = 0; i < count; i++) {
    const font = quoteFonts[Math.floor(Math.random() * quoteFonts.length)];
    const color = palette[Math.floor(Math.random() * palette.length)];
    const fontSize = 18 + Math.random() * 22; // px, 18-40 arası
    const rotate = -8 + Math.random() * 16; // -8 ile +8 derece arası
    styles.push({ font, color, fontSize, rotate });
  }
  return styles;
}

const Home = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [notFound, setNotFound] = useState(false);
  // Random cümleler ve pozisyonlar (her renderda değişmesin diye)
  const [quotes] = useState(() => getRandomQuotes(bookQuotes, 7));
  const [positions] = useState(() => getRandomPositions(7));
  const [quoteStyles] = useState(() => getQuoteStyles(7));

  // Arama fonksiyonu
  const handleSearch = () => {
    const found = allBooks.find(b => b.title.toLowerCase() === (searchValue || inputValue).toLowerCase());
    if (found) {
      setSelectedBook(found);
      setNotFound(false);
    } else {
      setSelectedBook(null);
      setNotFound(true);
    }
  };

  return (
    <Box sx={{ position: 'relative', width: '100%', minHeight: '100vh' }}>
      {/* Arka plan üzerinde random cümleler */}
      {quotes.map((q, i) => (
        <Box
          key={i}
          sx={{
            position: 'absolute',
            top: positions[i].top,
            left: positions[i].left,
            zIndex: 2,
            pointerEvents: 'none',
            opacity: 0.82,
            color: quoteStyles[i].color,
            fontSize: { xs: quoteStyles[i].fontSize * 0.8, sm: quoteStyles[i].fontSize, md: quoteStyles[i].fontSize * 1.1 },
            fontWeight: 700,
            fontFamily: quoteStyles[i].font,
            textShadow: `0 4px 24px #1e283c99, 0 1px 0 #fff2`,
            maxWidth: { xs: 180, sm: 260, md: 340 },
            lineHeight: 1.35,
            filter: 'drop-shadow(0 2px 8px #1e283c44)',
            userSelect: 'none',
            transition: 'opacity 0.3s',
            transform: `rotate(${quoteStyles[i].rotate}deg)`,
            letterSpacing: '0.01em',
            boxShadow: '0 2px 16px 0 #1e283c22',
            p: 1.2,
            borderRadius: 3,
            background: 'rgba(30,40,60,0.13)',
            border: '1.5px solid rgba(255,255,255,0.08)',
            backdropFilter: 'blur(2.5px)',
          }}
        >
          {q}
        </Box>
      ))}
      {/* Hero Section */}
      <Box sx={{
        width: '100%',
        minHeight: 420,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: 'transparent', // Arka planı şeffaf bırak
        position: 'relative',
        overflow: 'hidden',
        borderRadius: 4,
        boxShadow: 3,
        mb: 8,
        py: { xs: 10, md: 14 },
        px: 2,
      }}>
        {/* SVG ve illüstrasyonları kaldırdık */}
        <Typography
          variant="h2"
          align="center"
          gutterBottom
          sx={{
            fontFamily: 'Playfair Display, Montserrat, serif',
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 2,
            color: '#fff',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 1.5,
            zIndex: 2,
            fontSize: { xs: 32, sm: 48, md: 56 },
            textShadow: '0 4px 24px #1e283c99',
          }}
        >
          Kent Kütüphanesi
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ fontFamily: 'Montserrat, Playfair Display, serif', color: '#f7f4ed', opacity: 0.98, mb: 4, fontWeight: 500, zIndex: 2, textShadow: '0 2px 12px #1e283c88', fontStyle: 'italic', letterSpacing: '0.01em' }}>
          Gerçek bir kütüphane atmosferinde bilgiye ulaşın.
        </Typography>
        <Paper elevation={12} sx={{
          mt: 0.5,
          width: '100%',
          maxWidth: 420,
          minWidth: 0,
          overflow: 'hidden',
          p: { xs: 2, sm: 3 },
          borderRadius: 3,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 4px 18px 0 #1e283c33',
          backdropFilter: 'blur(8px)',
          background: 'rgba(30,40,60,0.82)',
          border: '1.5px solid #e0d7c6',
          zIndex: 3,
          transition: 'box-shadow 0.22s, border 0.22s, background 0.22s',
          '&:hover': {
            boxShadow: '0 8px 32px 0 #1e283c55',
            border: '2px solid #4ecdc4',
            background: 'rgba(30,40,60,0.92)',
          },
        }}>
          <Typography variant="subtitle1" sx={{ color: '#fff', fontWeight: 600, mb: 1, letterSpacing: '0.01em', opacity: 0.92, fontFamily: 'Montserrat, Playfair Display, serif' }}>
            Kütüphanede kitap ara
          </Typography>
          <Autocomplete
            freeSolo
            options={allBooks.map(b => b.title)}
            value={searchValue}
            onChange={(_, newValue) => setSearchValue(newValue || '')}
            inputValue={inputValue}
            onInputChange={(_, newInputValue) => setInputValue(newInputValue)}
            sx={{ width: '100%', mb: 2, maxWidth: 360, minWidth: 0, overflow: 'hidden', '& .MuiAutocomplete-listbox': { borderRadius: 2, boxShadow: 3, bgcolor: '#fff' } }}
            renderInput={(params) => (
              <TextField {...params} placeholder="Kitap ara..." variant="outlined" sx={{ bgcolor: 'rgba(255,255,255,0.98)', borderRadius: 3, width: '100%', maxWidth: 360, minWidth: 0, overflow: 'hidden', border: '2px solid #e0d7c6', fontSize: 18, fontFamily: 'Montserrat, Playfair Display, serif', boxShadow: '0 2px 12px 0 #1e283c22', transition: 'border 0.18s, box-shadow 0.18s', '&:hover': { border: '2px solid #4ecdc4', boxShadow: '0 4px 16px 0 #4ecdc422' } }} />
            )}
            ListboxProps={{ style: { boxShadow: '0 4px 16px 0 #e0d7c622', borderRadius: 12 } }}
          />
          <Button variant="contained" color="primary" sx={{ fontWeight: 800, px: 5, width: '100%', py: 1.5, fontSize: 20, borderRadius: 3, boxShadow: '0 4px 16px 0 #3a5ba022', bgcolor: '#3a5ba0', letterSpacing: '0.04em', fontFamily: 'Montserrat, Playfair Display, serif', mt: 1, transition: 'all 0.18s', '&:hover': { bgcolor: '#4ecdc4', color: '#22335b', boxShadow: '0 8px 32px 0 #4ecdc422' } }} onClick={handleSearch}>
            Ara
          </Button>
          {notFound && (
            <Alert severity="warning" sx={{ mt: 2, width: '100%' }}>Aradığınız kitap bulunamadı.</Alert>
          )}
        </Paper>
      </Box>
      {/* Featured Books */}
      <Typography variant="h5" gutterBottom sx={{ mt: 4, mb: 2, fontWeight: 700, color: '#fff', letterSpacing: '0.03em', textShadow: '0 2px 8px #1e283c88' }}>Öne Çıkan Kitaplar</Typography>
      <Grid container spacing={3}>
        {featuredBooks.map(book => (
          <Grid item xs={12} sm={4} key={book.id}>
            <Paper
              elevation={6}
              sx={{
                p: 3,
                borderRadius: 5,
                bgcolor: 'rgba(30,40,60,0.82)',
                boxShadow: '0 8px 32px 0 #1e283c22',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'flex-start',
                gap: 1,
                position: 'relative',
                minHeight: 160,
                background: book.cover
                  ? `url('${book.cover}') center center/cover no-repeat`
                  : 'rgba(30,40,60,0.82)',
                overflow: 'hidden',
                transition: 'box-shadow 0.18s, background 0.18s',
                cursor: 'pointer',
                '&:hover': {
                  boxShadow: '0 16px 48px 0 #1e283c44',
                  bgcolor: 'rgba(30,40,60,0.92)',
                  background: book.cover
                    ? `url('${book.cover}') center center/cover no-repeat`
                    : 'rgba(30,40,60,0.92)',
                },
              }}
              onClick={() => setSelectedBook(book)}
            >
              {/* Arka plan görseli için overlay (blur ve opacity) */}
              {book.cover && (
                <Box sx={{
                  position: 'absolute',
                  inset: 0,
                  zIndex: 0,
                  background: 'rgba(30,40,60,0.62)',
                  transition: 'background 0.22s',
                  pointerEvents: 'none',
                }} />
              )}
              <Box sx={{ position: 'relative', zIndex: 1, width: '100%' }}>
                <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                  {book.categories && book.categories.map(cat => (
                    <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                  ))}
                </Stack>
                <Typography variant="h6" sx={{ fontWeight: 700, color: '#fff', textShadow: '0 2px 8px #1e283c88' }}>{book.title}</Typography>
                <Typography variant="subtitle2" color="#e0d7c6" gutterBottom>{book.author}</Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <Stack direction="row" spacing={1} sx={{ mb: 2, flexWrap: 'wrap' }}>
            {selectedBook?.categories?.map(cat => (
              <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
            ))}
          </Stack>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Home;
