import axios from 'axios';

const api = axios.create({
  baseURL: 'https://localhost:7238/api', // .NET Core Web API adresin burası olacak
  headers: {
    'Content-Type': 'application/json',
  },
});

export const fetchCategories = () => api.get('/category');

export default api;
