import React, { useEffect, useState } from 'react';
import { Container, Typography, Grid, Card, CardContent, CardActions, Button, Box } from '@mui/material';

const dummyBooks = [
  {
    id: 1,
    title: 'Sefiller',
    author: 'Victor Hugo',
    summary: 'Fransız Devrimi sonrası Paris’te geçen, adalet, merhamet ve insanlık üzerine unutulmaz bir başyapıt.'
  },
  {
    id: 2,
    title: 'Suç ve Ceza',
    author: 'Fyodor Dostoyevski',
    summary: 'Bir cinayetin psikolojik derinliklerinde kaybolan Raskolnikov’un vicdan ve kefaret savaşı.'
  },
  {
    id: 3,
    title: 'Kürk Mantolu Madonna',
    author: 'Sabahattin Ali',
    summary: 'Raif Efendi’nin Berlin’de yaşadığı unutulmaz aşkın, yalnızlık ve içsel yolculukla harmanlanmış öyküsü.'
  },
  {
    id: 4,
    title: '1984',
    author: 'George Orwell',
    summary: 'Baskıcı bir rejimde özgürlük ve bireysellik için verilen mücadeleyi anlatan distopik bir klasik.'
  },
];

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    setTimeout(() => {
      setBooks(dummyBooks);
      setLoading(false);
    }, 500);
  }, []);

  if (loading) return <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}><span>Yükleniyor...</span></Box>;

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Kitaplar</Typography>
      <Grid container spacing={3}>
        {books.map(book => (
          <Grid item xs={12} sm={6} md={4} lg={3} key={book.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column', justifyContent: 'space-between' }}>
              <CardContent>
                <Typography variant="h6" gutterBottom>{book.title}</Typography>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>{book.author}</Typography>
                <Typography variant="body2" color="text.secondary">{book.summary}</Typography>
              </CardContent>
              <CardActions>
                <Button size="small" color="primary">Detay</Button>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Container>
  );
};

export default BookList;
