import React, { useState } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';

const allCategories = ['Roman', 'Klasik', 'Psikoloji', 'Aşk', 'Distopya', 'Bilim Kurgu', 'Çocuk', 'Tarih', 'Polisiye'];

const dummyBooks = [
  { id: 1, title: 'Sefiller', author: 'Victor Hugo', year: 1862, pages: 1463, isbn: '9789750738606', publisher: 'Can Yayınları', language: 'Fransızca', categories: ['Roman', 'Klasik'] },
  { id: 2, title: 'Suç ve Ceza', author: 'Fyodor Dostoyevski', year: 1866, pages: 687, isbn: '9786053609396', publisher: 'İş Bankası Kültür Yayınları', language: 'Rusça', categories: ['Roman', 'Psikoloji'] },
  { id: 3, title: 'Kürk Mantolu Madonna', author: 'Sabahattin Ali', year: 1943, pages: 160, isbn: '9789753638026', publisher: 'Yapı Kredi Yayınları', language: 'Türkçe', categories: ['Roman', 'Aşk'] },
];
const dummyUsers = [
  { id: 1, type: 'Üye', name: 'Ali Veli', username: 'aliveli', email: 'ali@site.com' },
  { id: 2, type: 'Yayınevi', name: 'Can Yayınları', username: 'canyay', email: 'info@can.com' },
];

const emptyBook = { title: '', author: '', year: '', pages: '', isbn: '', publisher: '', language: '', categories: [] };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState(dummyBooks);
  const [users, setUsers] = useState(dummyUsers);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);

  // Kitap ekle/güncelle
  const handleBookSave = () => {
    if (editBook) {
      setBooks(books.map(b => b.id === editBook.id ? { ...editBook, ...bookForm } : b));
    } else {
      setBooks([...books, { id: Date.now(), ...bookForm }]);
    }
    setOpenBookDialog(false);
    setEditBook(null);
    setBookForm(emptyBook);
  };
  // Kitap sil
  const handleBookDelete = (id) => {
    setBooks(books.filter(b => b.id !== id));
  };
  // Kitap düzenle
  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      year: book.year,
      pages: book.pages,
      isbn: book.isbn,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
    });
    setOpenBookDialog(true);
  };

  // Kullanıcı ekle/güncelle/sil için benzer fonksiyonlar eklenebilir

  return (
    <Box sx={{ p: { xs: 1, sm: 3 }, maxWidth: 1200, mx: 'auto', mt: 4 }}>
      <Paper sx={{ p: { xs: 2, sm: 4 }, borderRadius: 4, boxShadow: 6 }}>
        <Typography variant="h4" gutterBottom align="center">Admin Paneli</Typography>
        <Tabs value={tab} onChange={(_, v) => setTab(v)} centered sx={{ mb: 3 }}>
          <Tab label="Kitaplar" />
          <Tab label="Kullanıcılar" />
        </Tabs>
        {/* Kitaplar Sekmesi */}
        {tab === 0 && (
          <>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', mb: 2 }}>
              <Button variant="contained" startIcon={<AddIcon />} onClick={() => { setEditBook(null); setBookForm(emptyBook); setOpenBookDialog(true); }}>
                Kitap Ekle
              </Button>
            </Box>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table size="small">
                <TableHead>
                  <TableRow>
                    <TableCell>Başlık</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>Yıl</TableCell>
                    <TableCell>Sayfa</TableCell>
                    <TableCell>ISBN</TableCell>
                    <TableCell>Yayınevi</TableCell>
                    <TableCell>Dil</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {books.map(book => (
                    <TableRow key={book.id}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{book.year}</TableCell>
                      <TableCell>{book.pages}</TableCell>
                      <TableCell>{book.isbn}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>{book.language}</TableCell>
                      <TableCell>
                        <Stack direction="row" spacing={1} sx={{ flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map(cat => (
                            <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                          ))}
                        </Stack>
                      </TableCell>
                      <TableCell align="right">
                        <IconButton color="info" onClick={() => handleBookEdit(book)}><EditIcon /></IconButton>
                        <IconButton color="error" onClick={() => handleBookDelete(book.id)}><DeleteIcon /></IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            {/* Kitap Ekle/Güncelle Dialog */}
            <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)}>
              <DialogTitle>{editBook ? 'Kitap Güncelle' : 'Kitap Ekle'}</DialogTitle>
              <DialogContent>
                <Stack spacing={2} sx={{ mt: 1 }}>
                  <TextField label="Başlık" value={bookForm.title} onChange={e => setBookForm(f => ({ ...f, title: e.target.value }))} fullWidth required />
                  <TextField label="Yazar" value={bookForm.author} onChange={e => setBookForm(f => ({ ...f, author: e.target.value }))} fullWidth required />
                  <TextField label="Yıl" value={bookForm.year} onChange={e => setBookForm(f => ({ ...f, year: e.target.value }))} fullWidth required />
                  <TextField label="Sayfa" value={bookForm.pages} onChange={e => setBookForm(f => ({ ...f, pages: e.target.value }))} fullWidth required />
                  <TextField label="ISBN" value={bookForm.isbn} onChange={e => setBookForm(f => ({ ...f, isbn: e.target.value }))} fullWidth required />
                  <TextField label="Yayınevi" value={bookForm.publisher} onChange={e => setBookForm(f => ({ ...f, publisher: e.target.value }))} fullWidth required />
                  <TextField label="Dil" value={bookForm.language} onChange={e => setBookForm(f => ({ ...f, language: e.target.value }))} fullWidth required />
                  <Autocomplete
                    multiple
                    options={allCategories}
                    value={bookForm.categories}
                    onChange={(_, newValue) => setBookForm(f => ({ ...f, categories: newValue }))}
                    renderInput={(params) => <TextField {...params} label="Kategoriler" placeholder="Kategori seç" />}
                    fullWidth
                  />
                </Stack>
              </DialogContent>
              <DialogActions>
                <Button onClick={() => setOpenBookDialog(false)}>İptal</Button>
                <Button onClick={handleBookSave} variant="contained">Kaydet</Button>
              </DialogActions>
            </Dialog>
          </>
        )}
        {/* Kullanıcılar Sekmesi */}
        {tab === 1 && (
          <>
            <TableContainer component={Paper} sx={{ boxShadow: 2 }}>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Tür</TableCell>
                    <TableCell>Ad / Yayınevi</TableCell>
                    <TableCell>Kullanıcı Adı</TableCell>
                    <TableCell>E-posta</TableCell>
                    <TableCell align="right">İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {users.map(user => (
                    <TableRow key={user.id}>
                      <TableCell>{user.type}</TableCell>
                      <TableCell>{user.name}</TableCell>
                      <TableCell>{user.username}</TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell align="right">
                        <IconButton color="error"><DeleteIcon /></IconButton>
                        {/* Güncelleme/ekleme fonksiyonları eklenebilir */}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
          </>
        )}
      </Paper>
    </Box>
  );
};

export default AdminPanel;
