import React, { useState, useEffect } from 'react';
import { Box, Typography, Tabs, Tab, Paper, Button, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, IconButton, Dialog, DialogTitle, DialogContent, DialogActions, TextField, Stack, Chip, Autocomplete, MenuItem, Alert, CircularProgress, Grid, InputAdornment, Snackbar } from '@mui/material';
import EditIcon from '@mui/icons-material/Edit';
import DeleteIcon from '@mui/icons-material/Delete';
import AddIcon from '@mui/icons-material/Add';
import SearchIcon from '@mui/icons-material/Search';
import ClearIcon from '@mui/icons-material/Clear';
import { fetchBooks, getAllUsers, updateUser, deleteUser, registerUser, createBook, updateBook, deleteBook, fetchCategories, createCategory, updateCategory, deleteCategory, getAllBorrowRequests, updateBorrowRequestStatus } from '../api';

const categoryColors = {
  Roman: '#3a5ba0',
  Klasik: '#e07a5f',
  Psikoloji: '#43aa8b',
  Aşk: '#f76d6d',
  Distopya: '#6c63ff',
  'Bilim Kurgu' : '#ffd166',
  Çocuk: '#f9c74f',
  Tarih: '#b5838d',
  Polisiye: '#22223b',
  Felsefe: '#3d405b',
  Macera: '#f3722c',
  Biyografi: '#277da1',
  Şiir: '#9d4edd',
  Mitoloji: '#ffb4a2',
  Korku: '#720026',
  Mizah: '#f9844a',
  Sanat: '#577590',
  Din: '#b5ead7',
  Bilim: '#00b4d8',
  Eğitim: '#f7b801',
  Sağlık: '#43bccd',
  Spor: '#f94144',
  Gezi: '#90be6d',
  Yemek: '#f8961e',
  Diğer: '#adb5bd',
};

const emptyBook = { title: '', author: '', summary: '', publishDate: '', pageCount: '', isbn: '', publisher: '', language: '', categories: [], cover: '' };
const emptyUser = { firstName: '', lastName: '', username: '', email: '', role: 'member', birthDate: '', address: '', publisherName: '', website: '', password: '' };

const AdminPanel = () => {
  const [tab, setTab] = useState(0);
  const [books, setBooks] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [openBookDialog, setOpenBookDialog] = useState(false);
  const [editBook, setEditBook] = useState(null);
  const [bookForm, setBookForm] = useState(emptyBook);
  const [openUserDialog, setOpenUserDialog] = useState(false);
  const [editUser, setEditUser] = useState(null);
  const [userForm, setUserForm] = useState(emptyUser);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredUsers, setFilteredUsers] = useState([]);
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [categories, setCategories] = useState([]);
  const [filteredCategories, setFilteredCategories] = useState([]);
  const [openCategoryDialog, setOpenCategoryDialog] = useState(false);
  const [editCategory, setEditCategory] = useState(null);
  const [categoryForm, setCategoryForm] = useState({ name: '' });
  const [borrowRequests, setBorrowRequests] = useState([]);
  const [filteredBorrowRequests, setFilteredBorrowRequests] = useState([]);

  const [showMoreBooks, setShowMoreBooks] = useState(false);
  const [showMoreUsers, setShowMoreUsers] = useState(false);
  const [showMoreCategories, setShowMoreCategories] = useState(false);
  const [showMoreBorrowRequests, setShowMoreBorrowRequests] = useState(false);

  const ITEMS_PER_PAGE = 5;

  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');
  const [snackbarSeverity, setSnackbarSeverity] = useState('info');

  const handleSearch = (searchValue) => {
    setSearchTerm(searchValue);
    
    if (!searchValue.trim()) {
      setFilteredUsers(users);
      setFilteredBooks(books);
      setFilteredCategories(categories);
      return;
    }

    const lowerSearch = searchValue.toLowerCase();

    const filteredUsersResult = users.filter(user => {
      const fullName = `${user.firstName} ${user.lastName}`.toLowerCase();
      const username = user.username.toLowerCase();
      const email = user.email.toLowerCase();
      const publisherName = (user.publisherName || '').toLowerCase();
      const website = (user.website || '').toLowerCase();
      
      return fullName.includes(lowerSearch) || 
             username.includes(lowerSearch) || 
             email.includes(lowerSearch) ||
             publisherName.includes(lowerSearch) ||
             website.includes(lowerSearch);
    });

    const filteredBooksResult = books.filter(book => {
      const title = book.title.toLowerCase();
      const author = book.author.toLowerCase();
      const publisher = (book.publisher || '').toLowerCase();
      const isbn = (book.isbn || book.ISBN || '').toLowerCase();
      
      return title.includes(lowerSearch) || 
             author.includes(lowerSearch) || 
             publisher.includes(lowerSearch) ||
             isbn.includes(lowerSearch);
    });

    const filteredCategoriesResult = categories.filter(category => {
      const name = category.name.toLowerCase();
      const id = category.id.toString().toLowerCase();
      
      return name.includes(lowerSearch) || id.includes(lowerSearch);
    });

    const filteredBorrowRequestsResult = borrowRequests.filter(request => {
      const bookTitle = (request.bookTitle || '').toLowerCase();
      const userName = (request.userName || '').toLowerCase();
      const status = (request.status || '').toLowerCase();
      const publisherName = (request.publisherName || '').toLowerCase();
      
      return bookTitle.includes(lowerSearch) || 
             userName.includes(lowerSearch) || 
             status.includes(lowerSearch) ||
             publisherName.includes(lowerSearch);
    });

    setFilteredUsers(filteredUsersResult);
    setFilteredBooks(filteredBooksResult);
    setFilteredCategories(filteredCategoriesResult);
    setFilteredBorrowRequests(filteredBorrowRequestsResult);
  };

  const clearSearch = () => {
    setSearchTerm('');
    setFilteredUsers(users);
    setFilteredBooks(books);
    setFilteredCategories(categories);
    setFilteredBorrowRequests(borrowRequests);
  };

  useEffect(() => {
    const loadData = async () => {
      try {
        setLoading(true);
        const [booksResponse, usersResponse, categoriesResponse, borrowRequestsResponse] = await Promise.all([
          fetchBooks(),
          getAllUsers(),
          fetchCategories(),
          getAllBorrowRequests()
        ]);
        setBooks(booksResponse.data);
        setUsers(usersResponse.data);
        setCategories(categoriesResponse.data);
        setBorrowRequests(borrowRequestsResponse.data);
        setFilteredUsers(usersResponse.data);
        setFilteredBooks(booksResponse.data);
        setFilteredCategories(categoriesResponse.data);
        setFilteredBorrowRequests(borrowRequestsResponse.data);
        setError(null);
      } catch (err) {
        setError('Veriler yüklenirken hata oluştu: ' + err.message);
        console.error('Veri yükleme hatası:', err);
      } finally {
        setLoading(false);
      }
    };

    loadData();
  }, []);

  const handleBookSave = async () => {
    try {
      const bookData = {
        title: bookForm.title,
        author: bookForm.author,
        summary: bookForm.summary || 'Özet bilgisi girilmemiş.',
        publishDate: bookForm.publishDate,
        pageCount: parseInt(bookForm.pageCount) || 0,
        ISBN: bookForm.isbn,
        publisher: bookForm.publisher,
        language: bookForm.language,
        cover: bookForm.cover || '',
        categoryIDs: bookForm.categories && bookForm.categories.length > 0 ? 
          bookForm.categories.map(cat => {
            const category = categories.find(c => c.name === cat);
            return category ? category.id : null;
          }).filter(id => id !== null) : []
      };
      
      if (editBook) {
        await updateBook(editBook.id || editBook.ID, bookData);
      } else {
        const response = await createBook(bookData);
      }
      
      setOpenBookDialog(false);
      setEditBook(null);
      setBookForm(emptyBook);
      
      setSnackbarMessage(editBook ? 'Kitap başarıyla güncellendi!' : 'Kitap başarıyla eklendi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Kitap işlemi hatası detayları:', {
        message: err.message,
        response: err.response,
        status: err.response?.status,
        data: err.response?.data,
        config: err.config
      });
      
      let errorMessage = 'Kitap işlemi sırasında hata oluştu: ';
      if (err.response?.data) {
        if (typeof err.response.data === 'object') {
          errorMessage += JSON.stringify(err.response.data);
        } else {
          errorMessage += err.response.data;
        }
      } else if (err.message) {
        errorMessage += err.message;
      } else {
        errorMessage += 'Bilinmeyen hata';
      }
      
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };
  
  const handleBookDelete = async (id) => {
    try {
      const response = await deleteBook(id);
      
      setSnackbarMessage('Kitap başarıyla silindi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Kitap silme hatası detayları:', {
        message: err.message,
        response: err.response,
        status: err.response?.status,
        data: err.response?.data,
        config: err.config
      });
      
      let errorMessage = 'Kitap silinirken hata oluştu: ';
      if (err.response?.data) {
        errorMessage += err.response.data;
      } else if (err.message) {
        errorMessage += err.message;
      } else {
        errorMessage += 'Bilinmeyen hata';
      }
      
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleBookEdit = (book) => {
    setEditBook(book);
    setBookForm({
      title: book.title,
      author: book.author,
      summary: book.summary || '',
      publishDate: book.publishDate,
      pageCount: book.pageCount,
      isbn: book.ISBN || book.isbn,
      publisher: book.publisher,
      language: book.language,
      categories: book.categories || [],
      cover: book.cover || '',
    });
    setOpenBookDialog(true);
  };

  const handleUserSave = async () => {
    try {
      if (editUser) {
        await updateUser(editUser.id, userForm);
        const updatedUsers = users.map(u => u.id === editUser.id ? { ...u, ...userForm } : u);
        setUsers(updatedUsers);
      } else {
        const response = await registerUser(userForm);
        const newUser = response.data;
        setUsers([...users, newUser]);
      }
      setOpenUserDialog(false);
      setEditUser(null);
      setUserForm(emptyUser);
      
      setSnackbarMessage(editUser ? 'Kullanıcı başarıyla güncellendi!' : 'Kullanıcı başarıyla eklendi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
    } catch (err) {
      console.error('Kullanıcı işlemi hatası detayları:', {
        message: err.message,
        response: err.response,
        status: err.response?.status,
        data: err.response?.data
      });
      
      let errorMessage = 'Kullanıcı işlemi sırasında hata oluştu: ';
      if (err.response?.data) {
        if (typeof err.response.data === 'object') {
          errorMessage += JSON.stringify(err.response.data);
        } else {
          errorMessage += err.response.data;
        }
      } else if (err.message) {
        errorMessage += err.message;
      } else {
        errorMessage += 'Bilinmeyen hata';
      }
      
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleUserDelete = async (id) => {
    try {
      await deleteUser(id);
      
      setSnackbarMessage('Kullanıcı başarıyla silindi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Kullanıcı silme hatası detayları:', {
        message: err.message,
        response: err.response,
        status: err.response?.status,
        data: err.response?.data
      });
      
      let errorMessage = 'Kullanıcı silinirken hata oluştu: ';
      if (err.response?.data) {
        if (typeof err.response.data === 'object') {
          errorMessage += JSON.stringify(err.response.data);
        } else {
          errorMessage += err.response.data;
        }
      } else if (err.message) {
        errorMessage += err.message;
      } else {
        errorMessage += 'Bilinmeyen hata';
      }
      
      setSnackbarMessage(errorMessage);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleUserEdit = (user) => {
    setEditUser(user);
    setUserForm({
      firstName: user.firstName || '',
      lastName: user.lastName || '',
      username: user.username || '',
      email: user.email || '',
      role: user.role || 'member',
      birthDate: user.birthDate || '',
      address: user.address || '',
      publisherName: user.publisherName || '',
      website: user.website || '',
      password: ''
    });
    setOpenUserDialog(true);
  };

  const handleCategorySave = async () => {
    try {
      if (editCategory) {
        await updateCategory(editCategory.id, categoryForm);
      } else {
        await createCategory(categoryForm);
      }
      
      setOpenCategoryDialog(false);
      setEditCategory(null);
      setCategoryForm({ name: '' });
      
      setSnackbarMessage(editCategory ? 'Kategori başarıyla güncellendi!' : 'Kategori başarıyla eklendi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Kategori işlemi hatası:', err);
      setSnackbarMessage('Kategori işlemi sırasında hata oluştu: ' + err.message);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleCategoryDelete = async (id) => {
    try {
      await deleteCategory(id);
      
      setSnackbarMessage('Kategori başarıyla silindi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Kategori silme hatası:', err);
      setSnackbarMessage('Kategori silinirken hata oluştu: ' + err.message);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleCategoryEdit = (category) => {
    setEditCategory(category);
    setCategoryForm({ name: category.name });
    setOpenCategoryDialog(true);
  };

  const handleApproveRequest = async (requestId) => {
    try {
      await updateBorrowRequestStatus(requestId, { status: 'Approved' });
      
      setSnackbarMessage('Ödünç talebi onaylandı!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Onaylama hatası:', err);
      setSnackbarMessage('Onaylama sırasında hata oluştu: ' + err.message);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const handleRejectRequest = async (requestId) => {
    try {
      await updateBorrowRequestStatus(requestId, { status: 'Rejected' });
      
      setSnackbarMessage('Ödünç talebi reddedildi!');
      setSnackbarSeverity('success');
      setSnackbarOpen(true);
      
      setTimeout(() => {
        window.location.reload();
      }, 500);
      
    } catch (err) {
      console.error('Reddetme hatası:', err);
      setSnackbarMessage('Reddetme sırasında hata oluştu: ' + err.message);
      setSnackbarSeverity('error');
      setSnackbarOpen(true);
    }
  };

  const formatDate = (dateString) => {
    if (!dateString) return '-';
    return new Date(dateString).toLocaleDateString('tr-TR');
  };

  const handleTabChange = (event, newValue) => {
    setTab(newValue);
    setShowMoreBooks(false);
    setShowMoreUsers(false);
    setShowMoreCategories(false);
    setShowMoreBorrowRequests(false);
  };

  if (loading) {
    return (
      <Box sx={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '50vh' }}>
        <CircularProgress size={60} />
      </Box>
    );
  }

  if (error) {
    return (
      <Box sx={{ p: 3 }}>
        <Alert severity="error">{error}</Alert>
      </Box>
    );
  }

  const displayedBooks = showMoreBooks ? filteredBooks : filteredBooks.slice(0, ITEMS_PER_PAGE);
  const displayedUsers = showMoreUsers ? filteredUsers : filteredUsers.slice(0, ITEMS_PER_PAGE);
  const displayedCategories = showMoreCategories ? filteredCategories : filteredCategories.slice(0, ITEMS_PER_PAGE);
  const displayedBorrowRequests = showMoreBorrowRequests ? filteredBorrowRequests : filteredBorrowRequests.slice(0, ITEMS_PER_PAGE);

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom sx={{ mb: 3, fontWeight: 700, color: '#2c3e50' }}>
        Admin Paneli
      </Typography>

      <Paper sx={{ p: 3, mb: 3 }}>
        <Box sx={{ mb: 2, display: 'flex', alignItems: 'center', gap: 2 }}>
          <TextField
            placeholder="Ara..."
            value={searchTerm}
            onChange={(e) => handleSearch(e.target.value)}
            sx={{ flexGrow: 1 }}
            InputProps={{
              startAdornment: (
                <InputAdornment position="start">
                  <SearchIcon />
                </InputAdornment>
              ),
              endAdornment: searchTerm && (
                <InputAdornment position="end">
                  <IconButton onClick={clearSearch} size="small">
                    <ClearIcon />
                  </IconButton>
                </InputAdornment>
              )
            }}
          />
        </Box>

        <Tabs value={tab} onChange={handleTabChange} sx={{ mb: 3 }}>
          <Tab label="Kitaplar" />
          <Tab label="Kullanıcılar" />
          <Tab label="Kategoriler" />
          <Tab label="Ödünç Talepleri" />
        </Tabs>

        {tab === 0 && (
          <>
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6">Kitaplar ({filteredBooks.length})</Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => {
                  setEditBook(null);
                  setBookForm(emptyBook);
                  setOpenBookDialog(true);
                }}
                sx={{ borderRadius: 2 }}
              >
                Kitap Ekle
              </Button>
            </Box>

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Başlık</TableCell>
                    <TableCell>Yazar</TableCell>
                    <TableCell>Yayınevi</TableCell>
                    <TableCell>Kategoriler</TableCell>
                    <TableCell>İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {displayedBooks.map((book) => (
                    <TableRow key={book.ID || book.id}>
                      <TableCell>{book.title}</TableCell>
                      <TableCell>{book.author}</TableCell>
                      <TableCell>{book.publisher}</TableCell>
                      <TableCell>
                        <Box sx={{ display: 'flex', gap: 0.5, flexWrap: 'wrap' }}>
                          {book.categories && book.categories.map((category, index) => (
                            <Chip
                              key={index}
                              label={category}
                              size="small"
                              sx={{
                                backgroundColor: categoryColors[category] || '#ccc',
                                color: 'white',
                                fontSize: '0.7rem'
                              }}
                            />
                          ))}
                        </Box>
                      </TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleBookEdit(book)} color="primary" size="small">
                          <EditIcon />
                        </IconButton>
                        <IconButton onClick={() => handleBookDelete(book.ID || book.id)} color="error" size="small">
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            
            {filteredBooks.length > ITEMS_PER_PAGE && (
              <Box sx={{ mt: 2, textAlign: 'center' }}>
                <Button
                  variant="outlined"
                  onClick={() => setShowMoreBooks(!showMoreBooks)}
                  sx={{
                    borderRadius: 3,
                    px: 4,
                    py: 1.5,
                    fontWeight: 600,
                    borderColor: '#3a5ba0',
                    color: '#fff',
                    backgroundColor: '#3a5ba0',
                    '&:hover': {
                      borderColor: '#22335b',
                      backgroundColor: '#22335b',
                      transform: 'translateY(-1px)',
                      boxShadow: '0 4px 12px rgba(58, 91, 160, 0.15)'
                    },
                    transition: 'all 0.3s ease'
                  }}
                >
                  {showMoreBooks ? 'Daha Az Göster' : 'Daha Fazla Göster'}
                </Button>
              </Box>
            )}
          </>
        )}

        {tab === 1 && (
          <>
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6">Kullanıcılar ({filteredUsers.length})</Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => {
                  setEditUser(null);
                  setUserForm(emptyUser);
                  setOpenUserDialog(true);
                }}
                sx={{ borderRadius: 2 }}
              >
                Kullanıcı Ekle
              </Button>
            </Box>

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Ad Soyad</TableCell>
                    <TableCell>Kullanıcı Adı</TableCell>
                    <TableCell>E-posta</TableCell>
                    <TableCell>Rol</TableCell>
                    <TableCell>İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {displayedUsers.map((user) => (
                    <TableRow key={user.ID || user.id}>
                      <TableCell>{`${user.firstName} ${user.lastName}`}</TableCell>
                      <TableCell>{user.username}</TableCell>
                      <TableCell>{user.email}</TableCell>
                      <TableCell>
                        <Chip
                          label={user.role === 'member' ? 'Üye' : user.role === 'publisher' ? 'Yayınevi' : 'Admin'}
                          color={user.role === 'admin' ? 'error' : user.role === 'publisher' ? 'warning' : 'primary'}
                          size="small"
                        />
                      </TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleUserEdit(user)} color="primary" size="small">
                          <EditIcon />
                        </IconButton>
                        <IconButton onClick={() => handleUserDelete(user.ID || user.id)} color="error" size="small">
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            
            {filteredUsers.length > ITEMS_PER_PAGE && (
              <Box sx={{ mt: 2, textAlign: 'center' }}>
                <Button
                  variant="outlined"
                  onClick={() => setShowMoreUsers(!showMoreUsers)}
                  sx={{
                    borderRadius: 3,
                    px: 4,
                    py: 1.5,
                    fontWeight: 600,
                    borderColor: '#43aa8b',
                    color: '#fff',
                    backgroundColor: '#43aa8b',
                    '&:hover': {
                      borderColor: '#2d7a5f',
                      backgroundColor: '#2d7a5f',
                      transform: 'translateY(-1px)',
                      boxShadow: '0 4px 12px rgba(67, 170, 139, 0.15)'
                    },
                    transition: 'all 0.3s ease'
                  }}
                >
                  {showMoreUsers ? 'Daha Az Göster' : 'Daha Fazla Göster'}
                </Button>
              </Box>
            )}
          </>
        )}

        {tab === 2 && (
          <>
            <Box sx={{ mb: 2, display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
              <Typography variant="h6">Kategoriler ({filteredCategories.length})</Typography>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => {
                  setEditCategory(null);
                  setCategoryForm({ name: '' });
                  setOpenCategoryDialog(true);
                }}
                sx={{ borderRadius: 2 }}
              >
                Kategori Ekle
              </Button>
            </Box>

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>ID</TableCell>
                    <TableCell>Kategori Adı</TableCell>
                    <TableCell>İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {displayedCategories.map((category) => (
                    <TableRow key={category.id}>
                      <TableCell>{category.id}</TableCell>
                      <TableCell>{category.name}</TableCell>
                      <TableCell>
                        <IconButton onClick={() => handleCategoryEdit(category)} color="primary" size="small">
                          <EditIcon />
                        </IconButton>
                        <IconButton onClick={() => handleCategoryDelete(category.id)} color="error" size="small">
                          <DeleteIcon />
                        </IconButton>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            
            {filteredCategories.length > ITEMS_PER_PAGE && (
              <Box sx={{ mt: 2, textAlign: 'center' }}>
                <Button
                  variant="outlined"
                  onClick={() => setShowMoreCategories(!showMoreCategories)}
                  sx={{
                    borderRadius: 3,
                    px: 4,
                    py: 1.5,
                    fontWeight: 600,
                    borderColor: '#e07a5f',
                    color: '#fff',
                    backgroundColor: '#e07a5f',
                    '&:hover': {
                      borderColor: '#c45a3f',
                      backgroundColor: '#c45a3f',
                      transform: 'translateY(-1px)',
                      boxShadow: '0 4px 12px rgba(224, 122, 95, 0.15)'
                    },
                    transition: 'all 0.3s ease'
                  }}
                >
                  {showMoreCategories ? 'Daha Az Göster' : 'Daha Fazla Göster'}
                </Button>
              </Box>
            )}
          </>
        )}

        {tab === 3 && (
          <>
            <Box sx={{ mb: 2 }}>
              <Typography variant="h6">Ödünç Talepleri ({filteredBorrowRequests.length})</Typography>
            </Box>

            <TableContainer>
              <Table>
                <TableHead>
                  <TableRow>
                    <TableCell>Kitap</TableCell>
                    <TableCell>Kullanıcı</TableCell>
                    <TableCell>Yayınevi</TableCell>
                    <TableCell>Durum</TableCell>
                    <TableCell>Tarih</TableCell>
                    <TableCell>İşlemler</TableCell>
                  </TableRow>
                </TableHead>
                <TableBody>
                  {displayedBorrowRequests.map((request) => (
                    <TableRow key={request.id}>
                      <TableCell>{request.bookTitle}</TableCell>
                      <TableCell>{request.userName}</TableCell>
                      <TableCell>{request.publisherName}</TableCell>
                      <TableCell>
                        <Chip
                          label={request.status === 'Pending' ? 'Beklemede' : 
                                 request.status === 'Approved' ? 'Onaylandı' : 
                                 request.status === 'Rejected' ? 'Reddedildi' : 'İade Edildi'}
                          color={request.status === 'Pending' ? 'warning' : 
                                 request.status === 'Approved' ? 'success' : 
                                 request.status === 'Rejected' ? 'error' : 'info'}
                          size="small"
                        />
                      </TableCell>
                      <TableCell>{formatDate(request.requestDate)}</TableCell>
                      <TableCell>
                        {request.status === 'Pending' && (
                          <Box sx={{ display: 'flex', gap: 1 }}>
                            <Button
                              variant="contained"
                              size="small"
                              color="success"
                              onClick={() => handleApproveRequest(request.id)}
                              sx={{ 
                                minWidth: 'auto', 
                                px: 1.5, 
                                py: 0.5,
                                fontSize: '0.75rem',
                                fontWeight: 600
                              }}
                            >
                              Onayla
                            </Button>
                            <Button
                              variant="contained"
                              size="small"
                              color="error"
                              onClick={() => handleRejectRequest(request.id)}
                              sx={{ 
                                minWidth: 'auto', 
                                px: 1.5, 
                                py: 0.5,
                                fontSize: '0.75rem',
                                fontWeight: 600
                              }}
                            >
                              Reddet
                            </Button>
                          </Box>
                        )}
                        {request.status !== 'Pending' && (
                          <Typography variant="caption" sx={{ color: '#999', fontStyle: 'italic' }}>
                            İşlem tamamlandı
                          </Typography>
                        )}
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </TableContainer>
            
            {filteredBorrowRequests.length > ITEMS_PER_PAGE && (
              <Box sx={{ mt: 2, textAlign: 'center' }}>
                <Button
                  variant="outlined"
                  onClick={() => setShowMoreBorrowRequests(!showMoreBorrowRequests)}
                  sx={{
                    borderRadius: 3,
                    px: 4,
                    py: 1.5,
                    fontWeight: 600,
                    borderColor: '#9c27b0',
                    color: '#fff',
                    backgroundColor: '#9c27b0',
                    '&:hover': {
                      borderColor: '#7b1fa2',
                      backgroundColor: '#7b1fa2',
                      transform: 'translateY(-1px)',
                      boxShadow: '0 4px 12px rgba(156, 39, 176, 0.15)'
                    },
                    transition: 'all 0.3s ease'
                  }}
                >
                  {showMoreBorrowRequests ? 'Daha Az Göster' : 'Daha Fazla Göster'}
                </Button>
              </Box>
            )}
          </>
        )}
      </Paper>

      <Dialog open={openBookDialog} onClose={() => setOpenBookDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle>{editBook ? 'Kitap Düzenle' : 'Yeni Kitap Ekle'}</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Başlık"
                fullWidth
                value={bookForm.title}
                onChange={(e) => setBookForm({ ...bookForm, title: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Yazar"
                fullWidth
                value={bookForm.author}
                onChange={(e) => setBookForm({ ...bookForm, author: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Özet"
                fullWidth
                multiline
                rows={3}
                value={bookForm.summary}
                onChange={(e) => setBookForm({ ...bookForm, summary: e.target.value })}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Yayın Tarihi"
                type="date"
                fullWidth
                value={bookForm.publishDate}
                onChange={(e) => setBookForm({ ...bookForm, publishDate: e.target.value })}
                InputLabelProps={{ shrink: true }}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Sayfa Sayısı"
                type="number"
                fullWidth
                value={bookForm.pageCount}
                onChange={(e) => setBookForm({ ...bookForm, pageCount: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="ISBN"
                fullWidth
                value={bookForm.isbn}
                onChange={(e) => setBookForm({ ...bookForm, isbn: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Yayınevi"
                fullWidth
                value={bookForm.publisher}
                onChange={(e) => setBookForm({ ...bookForm, publisher: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Dil"
                fullWidth
                value={bookForm.language}
                onChange={(e) => setBookForm({ ...bookForm, language: e.target.value })}
                required={!editBook}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Kapak URL"
                fullWidth
                value={bookForm.cover}
                onChange={(e) => setBookForm({ ...bookForm, cover: e.target.value })}
                placeholder="https://www.example.com"
              />
            </Grid>
            <Grid item xs={12}>
              <Autocomplete
                multiple
                options={categories.map(cat => cat.name)}
                value={bookForm.categories}
                onChange={(event, newValue) => setBookForm({ ...bookForm, categories: newValue })}
                renderInput={(params) => (
                  <TextField
                    {...params}
                    label="Kategoriler"
                    placeholder="Kategori seçin"
                  />
                )}
                renderTags={(value, getTagProps) =>
                  value.map((option, index) => (
                    <Chip
                      {...getTagProps({ index })}
                      key={option}
                      label={option}
                      size="small"
                      sx={{
                        backgroundColor: categoryColors[option] || '#ccc',
                        color: 'white'
                      }}
                    />
                  ))
                }
              />
            </Grid>
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenBookDialog(false)}>İptal</Button>
          <Button onClick={handleBookSave} variant="contained">Kaydet</Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openUserDialog} onClose={() => setOpenUserDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle>{editUser ? 'Kullanıcı Düzenle' : 'Yeni Kullanıcı Ekle'}</DialogTitle>
        <DialogContent>
          <Grid container spacing={2} sx={{ mt: 1 }}>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Ad"
                fullWidth
                value={userForm.firstName}
                onChange={(e) => setUserForm({ ...userForm, firstName: e.target.value })}
                required={!editUser}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Soyad"
                fullWidth
                value={userForm.lastName}
                onChange={(e) => setUserForm({ ...userForm, lastName: e.target.value })}
                required={!editUser}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Kullanıcı Adı"
                fullWidth
                value={userForm.username}
                onChange={(e) => setUserForm({ ...userForm, username: e.target.value })}
                required={!editUser}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="E-posta"
                type="email"
                fullWidth
                value={userForm.email}
                onChange={(e) => setUserForm({ ...userForm, email: e.target.value })}
                required={!editUser}
              />
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                select
                label="Rol"
                fullWidth
                value={userForm.role}
                onChange={(e) => setUserForm({ ...userForm, role: e.target.value })}
                required={!editUser}
              >
                <MenuItem value="member">Üye</MenuItem>
                <MenuItem value="publisher">Yayınevi</MenuItem>
                <MenuItem value="admin">Admin</MenuItem>
              </TextField>
            </Grid>
            <Grid item xs={12} sm={6}>
              <TextField
                label="Doğum Tarihi"
                type="date"
                fullWidth
                value={userForm.birthDate}
                onChange={(e) => setUserForm({ ...userForm, birthDate: e.target.value })}
                InputLabelProps={{ shrink: true }}
                required={!editUser}
              />
            </Grid>
            <Grid item xs={12}>
              <TextField
                label="Adres"
                fullWidth
                multiline
                rows={2}
                value={userForm.address}
                onChange={(e) => setUserForm({ ...userForm, address: e.target.value })}
                required={!editUser}
              />
            </Grid>
            {userForm.role === 'publisher' && (
              <>
                <Grid item xs={12} sm={6}>
                  <TextField
                    label="Yayınevi Adı"
                    fullWidth
                    value={userForm.publisherName}
                    onChange={(e) => setUserForm({ ...userForm, publisherName: e.target.value })}
                    required={!editUser}
                  />
                </Grid>
                <Grid item xs={12} sm={6}>
                  <TextField
                    label="Web Sitesi"
                    fullWidth
                    value={userForm.website}
                    onChange={(e) => setUserForm({ ...userForm, website: e.target.value })}
                  />
                </Grid>
              </>
            )}
            {!editUser && (
              <Grid item xs={12}>
                <TextField
                  label="Şifre"
                  type="password"
                  fullWidth
                  value={userForm.password}
                  onChange={(e) => setUserForm({ ...userForm, password: e.target.value })}
                  required
                />
              </Grid>
            )}
          </Grid>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenUserDialog(false)}>İptal</Button>
          <Button onClick={handleUserSave} variant="contained">Kaydet</Button>
        </DialogActions>
      </Dialog>

      <Dialog open={openCategoryDialog} onClose={() => setOpenCategoryDialog(false)} maxWidth="sm" fullWidth>
        <DialogTitle>{editCategory ? 'Kategori Düzenle' : 'Yeni Kategori Ekle'}</DialogTitle>
        <DialogContent>
          <TextField
            label="Kategori Adı"
            fullWidth
            value={categoryForm.name}
            onChange={(e) => setCategoryForm({ name: e.target.value })}
            sx={{ mt: 1 }}
            required
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setOpenCategoryDialog(false)}>İptal</Button>
          <Button onClick={handleCategorySave} variant="contained">Kaydet</Button>
        </DialogActions>
      </Dialog>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={4000}
        onClose={() => setSnackbarOpen(false)}
        anchorOrigin={{ vertical: 'bottom', horizontal: 'center' }}
      >
        <Alert
          onClose={() => setSnackbarOpen(false)}
          severity={snackbarSeverity}
          sx={{
            width: '100%',
            borderRadius: 2,
            fontWeight: 600,
            '& .MuiAlert-icon': {
              fontSize: '1.2rem'
            }
          }}
        >
          {snackbarMessage}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default AdminPanel;
