const allBooks = [
  ...featuredBooks,
  // Buraya daha fazla kitap eklenebilir
];

const BookShelfSVG = () => (
  <svg width="100%" height="180" viewBox="0 0 800 180" fill="none" xmlns="http://www.w3.org/2000/svg" style={{ position: 'absolute', bottom: 0, left: 0, zIndex: 0, opacity: 0.22, filter: 'blur(1.5px)' }}>
    <rect x="0" y="120" width="800" height="20" rx="10" fill="#b08968" />
    <rect x="40" y="60" width="30" height="60" rx="6" fill="#3a5ba0" />
    <rect x="80" y="80" width="24" height="40" rx="5" fill="#ffd166" />
    <rect x="120" y="70" width="28" height="50" rx="5" fill="#ffb6b9" />
    <rect x="170" y="90" width="20" height="30" rx="4" fill="#4ecdc4" />
    <rect x="210" y="60" width="32" height="60" rx="6" fill="#6c63ff" />
    <rect x="260" y="80" width="24" height="40" rx="5" fill="#a3a380" />
    <rect x="300" y="70" width="28" height="50" rx="5" fill="#b5838d" />
    <rect x="350" y="90" width="20" height="30" rx="4" fill="#22223b" />
    <rect x="390" y="60" width="32" height="60" rx="6" fill="#ffb86b" />
    <rect x="440" y="80" width="24" height="40" rx="5" fill="#3a5ba0" />
    <rect x="480" y="70" width="28" height="50" rx="5" fill="#ffd166" />
    <rect x="530" y="90" width="20" height="30" rx="4" fill="#ffb6b9" />
    <rect x="570" y="60" width="32" height="60" rx="6" fill="#4ecdc4" />
    <rect x="620" y="80" width="24" height="40" rx="5" fill="#b08968" />
    <rect x="660" y="70" width="28" height="50" rx="5" fill="#6c63ff" />
    <rect x="710" y="90" width="20" height="30" rx="4" fill="#a3a380" />
  </svg>
);

const Home = () => {
  const [selectedBook, setSelectedBook] = useState(null);
  const [searchValue, setSearchValue] = useState('');
  const [inputValue, setInputValue] = useState('');
  const [notFound, setNotFound] = useState(false);

  // Arama fonksiyonu
  const handleSearch = () => {
    const found = allBooks.find(b => b.title.toLowerCase() === (searchValue || inputValue).toLowerCase());
    if (found) {
      setSelectedBook(found);
      setNotFound(false);
    } else {
      setSelectedBook(null);
      setNotFound(true);
    }
  };

  return (
    <Box>
      {/* Hero Section */}
      <Box sx={{
        width: '100%',
        minHeight: 420,
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
        background: `linear-gradient(135deg, #f7f4ed 0%, #e0e7ef 100%), url('https://images.unsplash.com/photo-1519681393784-d120267933ba?auto=format&fit=crop&w=1200&q=80') center/cover no-repeat`,
        position: 'relative',
        overflow: 'hidden',
        borderRadius: 4,
        boxShadow: 3,
        mb: 8,
        py: { xs: 10, md: 14 },
        px: 2,
      }}>
        <Box sx={{ position: 'absolute', inset: 0, zIndex: 0, background: 'rgba(255,255,255,0.7)', backdropFilter: 'blur(2.5px)' }} />
        {/* Kitap rafı SVG arka plan */}
        <BookShelfSVG />
        {/* Kitap yığını illüstrasyonu başlık üstünde */}
        <Box sx={{ position: 'absolute', top: 24, left: '50%', transform: 'translateX(-50%)', zIndex: 1 }}>
          <AutoStoriesIcon sx={{ fontSize: 80, color: '#3a5ba0', opacity: 0.13 }} />
        </Box>
        <Typography
          variant="h2"
          align="center"
          gutterBottom
          sx={{
            fontWeight: 900,
            letterSpacing: '0.04em',
            mb: 2,
            color: '#3a5ba0',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            gap: 1.5,
            zIndex: 2,
            fontSize: { xs: 32, sm: 48, md: 56 },
          }}
        >
          <AutoStoriesIcon sx={{ fontSize: 48, color: '#4ecdc4', mb: '-4px' }} />
          Kent Kütüphanesi
        </Typography>
        <Typography variant="h5" align="center" gutterBottom sx={{ color: '#22335b', opacity: 0.92, mb: 4, fontWeight: 500, zIndex: 2 }}>
          Bilgiye açılan kapınız, binlerce kitapla burada!
        </Typography>
        <Paper elevation={8} sx={{
          mt: 2,
          width: '100%',
          maxWidth: 520,
          p: { xs: 2, sm: 3 },
          borderRadius: 3.5,
          display: 'flex',
          flexDirection: 'column',
          alignItems: 'center',
          boxShadow: '0 4px 16px 0 #3a5ba022',
          backdropFilter: 'blur(6px)',
          background: 'rgba(255,255,255,0.97)',
          border: '1.5px solid #e0d7c6',
          zIndex: 3,
        }}>
          <Autocomplete
            freeSolo
            options={allBooks.map(b => b.title)}
            value={searchValue}
            onChange={(_, newValue) => setSearchValue(newValue || '')}
            inputValue={inputValue}
            onInputChange={(_, newInputValue) => setInputValue(newInputValue)}
            sx={{ width: '100%', mb: 2, '& .MuiAutocomplete-listbox': { borderRadius: 2, boxShadow: 3, bgcolor: '#fff' } }}
            renderInput={(params) => (
              <TextField {...params} label="Kitap ara..." variant="outlined" sx={{ bgcolor: 'rgba(255,255,255,0.98)', borderRadius: 2, width: '100%', border: '1.5px solid #e0d7c6' }} />
            )}
            ListboxProps={{ style: { boxShadow: '0 4px 16px 0 #e0d7c622', borderRadius: 12 } }}
          />
          <Button variant="contained" color="primary" sx={{ fontWeight: 700, px: 4, width: '100%', py: 1.2, fontSize: 17, borderRadius: 3, boxShadow: '0 2px 12px 0 #3a5ba022', bgcolor: '#3a5ba0' }} onClick={handleSearch}>
            Ara
          </Button>
          {notFound && (
            <Alert severity="warning" sx={{ mt: 2, width: '100%' }}>Aradığınız kitap bulunamadı.</Alert>
          )}
        </Paper>
      </Box>
      {/* Featured Books */}
      <Typography variant="h5" gutterBottom sx={{ mt: 4, mb: 2, fontWeight: 700, color: 'primary.dark', letterSpacing: '0.03em' }}>Öne Çıkan Kitaplar</Typography>
      <Grid container spacing={3}>
        {featuredBooks.map(book => (
          <Grid item xs={12} sm={4} key={book.id}>
            <Paper
              elevation={6}
              sx={{
                p: 3,
                borderRadius: 5,
                bgcolor: 'transparent',
                boxShadow: '0 8px 32px 0 #3a5ba022',
                display: 'flex',
                flexDirection: 'column',
                alignItems: 'flex-start',
                gap: 1,
                position: 'relative',
                minHeight: 160,
                background: book.cover
                  ? `url('${book.cover}') center center/cover no-repeat`
                  : 'linear-gradient(135deg, #f7f4ed 0%, #e0e7ef 100%)',
                overflow: 'hidden',
                transition: 'box-shadow 0.18s, background 0.18s',
                cursor: 'pointer',
                '&:hover': {
                  boxShadow: '0 16px 48px 0 #4ecdc422',
                  bgcolor: 'rgba(255,255,255,0.97)',
                  background: book.cover
                    ? `url('${book.cover}') center center/cover no-repeat`
                    : 'linear-gradient(135deg, #e0e7ef 0%, #f7f4ed 100%)',
                },
              }}
              onClick={() => setSelectedBook(book)}
            >
              {/* Arka plan görseli için overlay (blur ve opacity) */}
              {book.cover && (
                <Box sx={{
                  position: 'absolute',
                  inset: 0,
                  zIndex: 0,
                  background: 'rgba(255,255,255,0.82)',
                  transition: 'background 0.22s',
                  pointerEvents: 'none',
                }} />
              )}
              <Box sx={{ position: 'relative', zIndex: 1, width: '100%' }}>
                <Stack direction="row" spacing={1} sx={{ mb: 1, flexWrap: 'wrap' }}>
                  {book.categories && book.categories.map(cat => (
                    <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
                  ))}
                </Stack>
                <Typography variant="h6" sx={{ fontWeight: 700 }}>{book.title}</Typography>
                <Typography variant="subtitle2" color="text.secondary" gutterBottom>{book.author}</Typography>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
      <Dialog open={!!selectedBook} onClose={() => setSelectedBook(null)}>
        <DialogTitle>{selectedBook?.title} - {selectedBook?.author}</DialogTitle>
        <DialogContent>
          <Stack direction="row" spacing={1} sx={{ mb: 2, flexWrap: 'wrap' }}>
            {selectedBook?.categories?.map(cat => (
              <Chip key={cat} label={cat} size="small" color="info" sx={{ fontWeight: 600, fontSize: 13 }} />
            ))}
          </Stack>
          <DialogContentText>
            <strong>İlk Basım Yılı:</strong> {selectedBook?.year}<br />
            <strong>Sayfa Sayısı:</strong> {selectedBook?.pages}<br />
            <strong>ISBN:</strong> {selectedBook?.isbn}<br />
            <strong>Yayınevi:</strong> {selectedBook?.publisher}<br />
            <strong>Dil:</strong> {selectedBook?.language}
          </DialogContentText>
        </DialogContent>
        <DialogActions>
          <Button onClick={() => setSelectedBook(null)}>Kapat</Button>
        </DialogActions>
      </Dialog>
    </Box>
  );
};

export default Home;
