import React, { useState } from 'react';
import { AppBar, Toolbar, Typography, Button, Box, Menu, MenuItem } from '@mui/material';
import LibraryBooksIcon from '@mui/icons-material/LibraryBooks';
import { Link, useLocation } from 'react-router-dom';

const navLinks = [
  { label: 'Ana Sayfa', to: '/' },
  { label: 'Kitaplar', to: '/books' },
  { label: 'Kayıt Ol', to: '/register' },
];

const Navbar = () => {
  const [anchorEl, setAnchorEl] = useState(null);
  const handleMenuOpen = (event) => setAnchorEl(event.currentTarget);
  const handleMenuClose = () => setAnchorEl(null);
  const location = useLocation();

  return (
    <AppBar position="static" elevation={0} sx={{
      background: 'linear-gradient(90deg, #4b6cb7 0%, #182848 100%)',
      bgcolor: 'transparent',
      opacity: 0.97,
      boxShadow: '0 2px 16px 0 rgba(75,108,183,0.10)',
      borderBottomLeftRadius: 24,
      borderBottomRightRadius: 24,
    }}>
      <Toolbar sx={{ minHeight: 80, px: { xs: 1, sm: 4 } }}>
        <Box sx={{ display: 'flex', alignItems: 'center', flexGrow: 1 }}>
          <LibraryBooksIcon sx={{ mr: 1, fontSize: 36 }} />
          <Typography variant="h6" component={Link} to="/" sx={{ color: 'inherit', textDecoration: 'none', fontWeight: 800, letterSpacing: '0.04em', fontSize: 26 }}>
            Kent Kütüphanesi
          </Typography>
        </Box>
        <Box sx={{ display: 'flex', gap: 2 }}>
          {navLinks.map(link => (
            <Button
              key={link.to}
              color="inherit"
              component={Link}
              to={link.to}
              sx={{
                fontWeight: 700,
                fontSize: 17,
                px: 2.5,
                borderRadius: 2,
                position: 'relative',
                transition: 'color 0.2s',
                color: location.pathname === link.to ? '#ffb347' : 'white',
                '&:after': {
                  content: '""',
                  display: 'block',
                  position: 'absolute',
                  left: 8,
                  right: 8,
                  bottom: 4,
                  height: 3,
                  borderRadius: 2,
                  background: location.pathname === link.to ? '#ffb347' : 'transparent',
                  transition: 'background 0.2s',
                },
                '&:hover:after': {
                  background: '#ffb347',
                },
              }}
            >
              {link.label}
            </Button>
          ))}
          <Button color="inherit" onClick={handleMenuOpen} sx={{ fontWeight: 700, fontSize: 17, px: 2.5, borderRadius: 2 }}>Kullanıcı Girişi</Button>
          <Menu anchorEl={anchorEl} open={Boolean(anchorEl)} onClose={handleMenuClose}>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/member">Üye Girişi</MenuItem>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/publisher">Yayınevi Girişi</MenuItem>
            <MenuItem onClick={handleMenuClose} component={Link} to="/login/admin">Admin Girişi</MenuItem>
          </Menu>
        </Box>
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
