import React, { useEffect, useState } from 'react';
import { Container, Typography, List, ListItem, ListItemText, CircularProgress } from '@mui/material';

const dummyBooks = [
  { id: 1, title: 'Sefiller', author: 'Victor Hugo' },
  { id: 2, title: 'Suç ve Ceza', author: 'Fyodor Dostoyevski' },
  { id: 3, title: 'Kürk Mantolu Madonna', author: 'Sabahattin Ali' },
  { id: 4, title: '1984', author: 'George Orwell' },
];

const BookList = () => {
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // API entegrasyonu yoksa dummy veri kullan
    setTimeout(() => {
      setBooks(dummyBooks);
      setLoading(false);
    }, 500);
  }, []);

  if (loading) return <CircularProgress />;

  return (
    <Container>
      <Typography variant="h4" gutterBottom>Kitaplar</Typography>
      <List>
        {books.map(book => (
          <ListItem key={book.id} divider>
            <ListItemText primary={book.title} secondary={book.author} />
          </ListItem>
        ))}
      </List>
    </Container>
  );
};

export default BookList;
