import React, { useState, useEffect, useContext } from 'react';
import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  CircularProgress,
  Chip,
  IconButton,
  Snackbar,
  MenuItem,
  Avatar,
  Divider,
  Stack
} from '@mui/material';
import { 
  Add as AddIcon, 
  Edit as EditIcon, 
  Delete as DeleteIcon,
  Business as BusinessIcon,
  Person as PersonIcon,
  Email as EmailIcon,
  LocationOn as LocationIcon,
  Language as LanguageIcon,
  Book as BookIcon
} from '@mui/icons-material';
import { LoginContext } from '../App';
import { fetchCategories, fetchBooks } from '../api';

// Open Library'den kitap adına göre kapak url'si bul (cache'li)
async function fetchCoverByTitleCached(title) {
  const cacheKey = 'cover_' + title;
  const cached = localStorage.getItem(cacheKey);
  if (cached) return cached;
  const url = `https://openlibrary.org/search.json?title=${encodeURIComponent(title)}&limit=1`;
  try {
    const res = await fetch(url);
    const data = await res.json();
    if (data.docs && data.docs.length > 0) {
      const doc = data.docs[0];
      let coverUrl = null;
      if (doc.cover_i) {
        coverUrl = `https://covers.openlibrary.org/b/id/${doc.cover_i}-L.jpg`;
      } else if (doc.isbn && doc.isbn.length > 0) {
        coverUrl = `https://covers.openlibrary.org/b/isbn/${doc.isbn[0]}-L.jpg`;
      }
      if (coverUrl) {
        localStorage.setItem(cacheKey, coverUrl);
        return coverUrl;
      }
    }
  } catch (e) {}
  localStorage.setItem(cacheKey, '');
  return null;
}

const MyPublisher = () => {
  const { isLoggedIn } = useContext(LoginContext);
  const [user, setUser] = useState(null);
  const [categories, setCategories] = useState([]);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [form, setForm] = useState({
    title: '',
    author: '',
    summary: '',
    publishDate: '',
    pageCount: '',
    isbn: '',
    publisher: '',
    language: 'Türkçe',
    categoryIDs: []
  });

  useEffect(() => {
    if (isLoggedIn) {
      const userData = JSON.parse(localStorage.getItem('user'));
      setUser(userData);
      fetchCategoriesData();
      fetchBooksData();
    }
  }, [isLoggedIn]);

  const fetchCategoriesData = async () => {
    try {
      const response = await fetchCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Kategoriler yüklenirken hata:', error);
    }
  };

  const fetchBooksData = async () => {
    try {
      const response = await fetchBooks();
      const userData = JSON.parse(localStorage.getItem('user'));
      const publisherBooks = response.data.filter(book => 
        book.publisher === userData.publisherName
      );
      
      // Her kitap için cover resmini yükle
      const booksWithCovers = await Promise.all(
        publisherBooks.map(async (book) => {
          let coverUrl = book.cover;
          if (!coverUrl) {
            coverUrl = await fetchCoverByTitleCached(book.title);
          }
          return { ...book, cover: coverUrl };
        })
      );
      
      setBooks(booksWithCovers);
    } catch (error) {
      console.error('Kitaplar yüklenirken hata:', error);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCategoryChange = (event) => {
    const value = event.target.value;
    setForm({ ...form, categoryIDs: typeof value === 'string' ? value.split(',') : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const userData = JSON.parse(localStorage.getItem('user'));
      const bookData = {
        ...form,
        publisher: userData.publisherName,
        pageCount: parseInt(form.pageCount) || 0,
        categoryIDs: form.categoryIDs.map(id => parseInt(id))
      };

      if (editingBook) {
        const response = await fetch(`https://localhost:7238/api/book/${editingBook.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(bookData)
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla güncellendi!');
        } else {
          throw new Error('Kitap güncellenirken hata oluştu');
        }
      } else {
        const response = await fetch('https://localhost:7238/api/book', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(bookData)
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla eklendi!');
        } else {
          throw new Error('Kitap eklenirken hata oluştu');
        }
      }

      setOpenDialog(false);
      setEditingBook(null);
      resetForm();
      fetchBooksData();

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (book) => {
    setEditingBook(book);
    setForm({
      title: book.title,
      author: book.author,
      summary: book.summary,
      publishDate: book.publishDate ? book.publishDate.split('T')[0] : '',
      pageCount: book.pageCount?.toString() || '',
      isbn: book.isbn || '',
      publisher: book.publisher,
      language: book.language || 'Türkçe',
      categoryIDs: book.categoryIDs || []
    });
    setOpenDialog(true);
  };

  const handleDelete = async (bookId) => {
    if (window.confirm('Bu kitabı silmek istediğinizden emin misiniz?')) {
      try {
        const response = await fetch(`https://localhost:7238/api/book/${bookId}`, {
          method: 'DELETE'
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla silindi!');
          fetchBooksData();
        } else {
          throw new Error('Kitap silinirken hata oluştu');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  const resetForm = () => {
    setForm({
      title: '',
      author: '',
      summary: '',
      publishDate: '',
      pageCount: '',
      isbn: '',
      publisher: '',
      language: 'Türkçe',
      categoryIDs: []
    });
  };

  if (!isLoggedIn || !user || user.role !== 'publisher') {
    return (
      <Box sx={{ 
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center'
      }}>
        <Paper sx={{ 
          p: 4, 
          textAlign: 'center',
          bgcolor: 'rgba(255,255,255,0.95)',
          borderRadius: 3,
          boxShadow: '0 8px 32px rgba(0,0,0,0.1)'
        }}>
          <Typography variant="h6" color="error" sx={{ fontWeight: 'bold' }}>
            Bu sayfaya erişim yetkiniz yok.
          </Typography>
        </Paper>
      </Box>
    );
  }

  return (
    <Box sx={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
      py: 4,
      px: 2
    }}>
      {/* Header Section */}
      <Box sx={{ mb: 4, textAlign: 'center' }}>
        <Typography 
          variant="h3" 
          sx={{ 
            color: 'white',
            fontWeight: 800,
            textShadow: '2px 2px 4px rgba(0,0,0,0.3)',
            mb: 1
          }}
        >
          🏢 {user.publisherName}
        </Typography>
        <Typography 
          variant="h6" 
          sx={{ 
            color: 'rgba(255,255,255,0.9)',
            fontWeight: 300
          }}
        >
          Yayınevi Yönetim Paneli
        </Typography>
      </Box>

      {error && (
        <Alert severity="error" sx={{ mb: 3, mx: 'auto', maxWidth: 600 }}>
          {error}
        </Alert>
      )}
      {success && (
        <Alert severity="success" sx={{ mb: 3, mx: 'auto', maxWidth: 600 }}>
          {success}
        </Alert>
      )}

      <Grid container spacing={4} sx={{ maxWidth: 1400, mx: 'auto' }}>
        {/* Publisher Info Card */}
        <Grid item xs={12} md={4}>
          <Paper sx={{ 
            p: 3,
            bgcolor: 'rgba(255,255,255,0.95)',
            borderRadius: 3,
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            backdropFilter: 'blur(10px)',
            height: 'fit-content'
          }}>
            <Box sx={{ textAlign: 'center', mb: 3 }}>
              <Avatar sx={{ 
                width: 80, 
                height: 80, 
                mx: 'auto', 
                mb: 2,
                bgcolor: '#667eea',
                fontSize: '2rem'
              }}>
                <BusinessIcon sx={{ fontSize: 40 }} />
              </Avatar>
              <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#2c3e50' }}>
                Yayınevi Bilgileri
              </Typography>
            </Box>
            
            <Stack spacing={2}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <BusinessIcon sx={{ color: '#667eea' }} />
                <Box>
                  <Typography variant="body2" color="text.secondary">Yayınevi Adı</Typography>
                  <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                    {user.publisherName || 'Belirtilmemiş'}
                  </Typography>
                </Box>
              </Box>
              
              <Divider />
              
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <PersonIcon sx={{ color: '#667eea' }} />
                <Box>
                  <Typography variant="body2" color="text.secondary">Yetkili Kişi</Typography>
                  <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                    {`${user.firstName || ''} ${user.lastName || ''}`.trim() || 'Belirtilmemiş'}
                  </Typography>
                </Box>
              </Box>
              
              <Divider />
              
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <EmailIcon sx={{ color: '#667eea' }} />
                <Box>
                  <Typography variant="body2" color="text.secondary">E-posta</Typography>
                  <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                    {user.email || 'Belirtilmemiş'}
                  </Typography>
                </Box>
              </Box>
              
              <Divider />
              
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <LocationIcon sx={{ color: '#667eea' }} />
                <Box>
                  <Typography variant="body2" color="text.secondary">Adres</Typography>
                  <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                    {user.address || 'Belirtilmemiş'}
                  </Typography>
                </Box>
              </Box>
              
              {user.website && (
                <>
                  <Divider />
                  <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                    <LanguageIcon sx={{ color: '#667eea' }} />
                    <Box>
                      <Typography variant="body2" color="text.secondary">Web Sitesi</Typography>
                      <Typography variant="body1" sx={{ fontWeight: 'bold' }}>
                        {user.website}
                      </Typography>
                    </Box>
                  </Box>
                </>
              )}
            </Stack>
          </Paper>
        </Grid>

        {/* Books Section */}
        <Grid item xs={12} md={8}>
          <Paper sx={{ 
            p: 3,
            bgcolor: 'rgba(255,255,255,0.95)',
            borderRadius: 3,
            boxShadow: '0 8px 32px rgba(0,0,0,0.1)',
            backdropFilter: 'blur(10px)'
          }}>
            <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
              <Box sx={{ display: 'flex', alignItems: 'center', gap: 2 }}>
                <BookIcon sx={{ color: '#667eea', fontSize: 32 }} />
                <Box>
                  <Typography variant="h5" sx={{ fontWeight: 'bold', color: '#2c3e50' }}>
                    Kitaplarım
                  </Typography>
                  <Typography variant="body2" color="text.secondary">
                    {books.length} kitap bulundu
                  </Typography>
                </Box>
              </Box>
              <Button
                variant="contained"
                startIcon={<AddIcon />}
                onClick={() => {
                  setEditingBook(null);
                  resetForm();
                  setOpenDialog(true);
                }}
                sx={{
                  bgcolor: '#667eea',
                  '&:hover': { bgcolor: '#5a6fd8' },
                  borderRadius: 2,
                  px: 3,
                  py: 1.5,
                  fontWeight: 'bold',
                  boxShadow: '0 4px 12px rgba(102, 126, 234, 0.4)'
                }}
              >
                Yeni Kitap Ekle
              </Button>
            </Box>

            {books.length === 0 ? (
              <Box sx={{ 
                textAlign: 'center', 
                py: 8,
                bgcolor: 'rgba(102, 126, 234, 0.05)',
                borderRadius: 2
              }}>
                <BookIcon sx={{ fontSize: 64, color: '#667eea', mb: 2, opacity: 0.5 }} />
                <Typography variant="h6" color="text.secondary" sx={{ mb: 1 }}>
                  Henüz kitap eklenmemiş
                </Typography>
                <Typography variant="body2" color="text.secondary">
                  İlk kitabınızı eklemek için "Yeni Kitap Ekle" butonuna tıklayın.
                </Typography>
              </Box>
            ) : (
              <Grid container spacing={3}>
                {books.map((book) => (
                  <Grid item xs={12} sm={6} lg={4} key={book.id}>
                    <Card sx={{ 
                      height: '100%',
                      display: 'flex',
                      flexDirection: 'column',
                      borderRadius: 2,
                      boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-4px)',
                        boxShadow: '0 8px 24px rgba(0,0,0,0.15)'
                      }
                    }}>
                      {/* Kitap Kapağı */}
                      <Box sx={{ 
                        height: 200, 
                        overflow: 'hidden',
                        borderRadius: '8px 8px 0 0',
                        position: 'relative',
                        bgcolor: '#f5f5f5'
                      }}>
                        {book.cover ? (
                          <img 
                            src={book.cover} 
                            alt={book.title}
                            style={{ 
                              width: '100%', 
                              height: '100%', 
                              objectFit: 'cover' 
                            }}
                            onError={(e) => {
                              e.target.style.display = 'none';
                              e.target.nextSibling.style.display = 'flex';
                            }}
                          />
                        ) : null}
                        <Box sx={{ 
                          display: book.cover ? 'none' : 'flex',
                          width: '100%', 
                          height: '100%', 
                          alignItems: 'center', 
                          justifyContent: 'center',
                          bgcolor: '#667eea',
                          color: 'white'
                        }}>
                          <BookIcon sx={{ fontSize: 48, opacity: 0.7 }} />
                        </Box>
                      </Box>
                      
                      <CardContent sx={{ flexGrow: 1, p: 2.5 }}>
                        <Typography variant="h6" gutterBottom sx={{ 
                          fontWeight: 'bold',
                          color: '#2c3e50',
                          lineHeight: 1.3
                        }}>
                          {book.title}
                        </Typography>
                        
                        <Stack spacing={1} sx={{ mb: 2 }}>
                          <Typography variant="body2" color="text.secondary">
                            <strong>Yazar:</strong> {book.author}
                          </Typography>
                          <Typography variant="body2" color="text.secondary">
                            <strong>Yayınevi:</strong> {book.publisher}
                          </Typography>
                        </Stack>
                        
                        {book.categories && book.categories.length > 0 && (
                          <Box sx={{ mt: 2 }}>
                            {book.categories.map((category, index) => (
                              <Chip
                                key={index}
                                label={category}
                                size="small"
                                sx={{ 
                                  mr: 0.5, 
                                  mb: 0.5, 
                                  bgcolor: '#667eea', 
                                  color: 'white',
                                  fontWeight: 'bold',
                                  fontSize: '0.75rem'
                                }}
                              />
                            ))}
                          </Box>
                        )}
                      </CardContent>
                      
                      <CardActions sx={{ 
                        p: 2, 
                        pt: 0,
                        justifyContent: 'space-between'
                      }}>
                        <Box>
                          <IconButton
                            size="small"
                            onClick={() => handleEdit(book)}
                            sx={{ 
                              color: '#f39c12',
                              '&:hover': { bgcolor: 'rgba(243, 156, 18, 0.1)' }
                            }}
                          >
                            <EditIcon />
                          </IconButton>
                          <IconButton
                            size="small"
                            onClick={() => handleDelete(book.id)}
                            sx={{ 
                              color: '#e74c3c',
                              '&:hover': { bgcolor: 'rgba(231, 76, 60, 0.1)' }
                            }}
                          >
                            <DeleteIcon />
                          </IconButton>
                        </Box>
                      </CardActions>
                    </Card>
                  </Grid>
                ))}
              </Grid>
            )}
          </Paper>
        </Grid>
      </Grid>

      {/* Kitap Ekleme/Düzenleme Dialog */}
      <Dialog 
        open={openDialog} 
        onClose={() => setOpenDialog(false)} 
        maxWidth="md" 
        fullWidth
        PaperProps={{
          sx: {
            borderRadius: 4,
            boxShadow: '0 24px 64px rgba(0,0,0,0.3)',
            background: 'linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%)',
            border: '1px solid rgba(102, 126, 234, 0.1)'
          }
        }}
      >
        <DialogTitle sx={{ 
          background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
          color: 'white',
          borderRadius: '16px 16px 0 0',
          py: 4,
          px: 4,
          position: 'relative',
          overflow: 'hidden'
        }}>
          <Box sx={{ 
            display: 'flex', 
            alignItems: 'center', 
            gap: 3,
            position: 'relative',
            zIndex: 2
          }}>
            <Box sx={{
              p: 1.5,
              borderRadius: 3,
              bgcolor: 'rgba(255,255,255,0.2)',
              backdropFilter: 'blur(10px)'
            }}>
              <BookIcon sx={{ fontSize: 28 }} />
            </Box>
            <Box>
              <Typography variant="h5" sx={{ 
                fontWeight: 800,
                mb: 0.5,
                textShadow: '0 2px 4px rgba(0,0,0,0.2)'
              }}>
                {editingBook ? '📝 Kitap Düzenle' : '📚 Yeni Kitap Ekle'}
              </Typography>
              <Typography variant="body2" sx={{ 
                opacity: 0.9,
                fontWeight: 300
              }}>
                {editingBook ? 'Kitap bilgilerini güncelleyin' : 'Yeni kitap bilgilerini girin'}
              </Typography>
            </Box>
          </Box>
          {/* Dekoratif arka plan elementleri */}
          <Box sx={{
            position: 'absolute',
            top: -20,
            right: -20,
            width: 100,
            height: 100,
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.1)',
            zIndex: 1
          }} />
          <Box sx={{
            position: 'absolute',
            bottom: -30,
            left: -30,
            width: 80,
            height: 80,
            borderRadius: '50%',
            background: 'rgba(255,255,255,0.05)',
            zIndex: 1
          }} />
        </DialogTitle>
        
        <form onSubmit={handleSubmit}>
          <DialogContent sx={{ 
            pt: 4, 
            pb: 3,
            px: 4,
            background: 'linear-gradient(135deg, #ffffff 0%, #f8f9fa 100%)'
          }}>
            <Grid container spacing={3}>
              <Grid item xs={12} md={6}>
                <TextField
                  name="title"
                  label="📖 Kitap Adı"
                  value={form.title}
                  onChange={handleChange}
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="author"
                  label="✍️ Yazar"
                  value={form.author}
                  onChange={handleChange}
                  fullWidth
                  required
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="summary"
                  label="📝 Özet"
                  value={form.summary}
                  onChange={handleChange}
                  fullWidth
                  multiline
                  rows={4}
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="publishDate"
                  label="📅 Yayın Tarihi"
                  type="date"
                  value={form.publishDate}
                  onChange={handleChange}
                  fullWidth
                  InputLabelProps={{ shrink: true }}
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="pageCount"
                  label="📄 Sayfa Sayısı"
                  type="number"
                  value={form.pageCount}
                  onChange={handleChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="isbn"
                  label="🏷️ ISBN"
                  value={form.isbn}
                  onChange={handleChange}
                  fullWidth
                  sx={{
                    '& .MuiOutlinedInput-root': {
                      borderRadius: 3,
                      backgroundColor: 'rgba(255,255,255,0.8)',
                      backdropFilter: 'blur(10px)',
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        backgroundColor: 'rgba(255,255,255,0.9)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                      },
                      '&.Mui-focused': {
                        backgroundColor: 'rgba(255,255,255,1)',
                        transform: 'translateY(-2px)',
                        boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                      }
                    },
                    '& .MuiInputLabel-root': {
                      fontWeight: 600,
                      color: '#2c3e50'
                    },
                    '& .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.3)',
                      borderWidth: '2px'
                    },
                    '&:hover .MuiOutlinedInput-notchedOutline': {
                      borderColor: 'rgba(102, 126, 234, 0.5)'
                    },
                    '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                      borderColor: '#667eea',
                      borderWidth: '2px'
                    }
                  }}
                />
              </Grid>
                             <Grid item xs={12} md={6}>
                 <TextField
                   name="language"
                   label="🌍 Dil"
                   value={form.language}
                   onChange={handleChange}
                   fullWidth
                   sx={{
                     '& .MuiOutlinedInput-root': {
                       borderRadius: 3,
                       backgroundColor: 'rgba(255,255,255,0.8)',
                       backdropFilter: 'blur(10px)',
                       transition: 'all 0.3s ease',
                       '&:hover': {
                         backgroundColor: 'rgba(255,255,255,0.9)',
                         transform: 'translateY(-2px)',
                         boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                       },
                       '&.Mui-focused': {
                         backgroundColor: 'rgba(255,255,255,1)',
                         transform: 'translateY(-2px)',
                         boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                       }
                     },
                     '& .MuiInputLabel-root': {
                       fontWeight: 600,
                       color: '#2c3e50'
                     },
                     '& .MuiOutlinedInput-notchedOutline': {
                       borderColor: 'rgba(102, 126, 234, 0.3)',
                       borderWidth: '2px'
                     },
                     '&:hover .MuiOutlinedInput-notchedOutline': {
                       borderColor: 'rgba(102, 126, 234, 0.5)'
                     },
                     '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                       borderColor: '#667eea',
                       borderWidth: '2px'
                     }
                   }}
                 />
               </Grid>
               <Grid item xs={12} md={6}>
                 <TextField
                   name="publisher"
                   label="🏢 Yayın Evi"
                   value={user?.publisherName || ''}
                   fullWidth
                   disabled
                   sx={{ 
                     '& .MuiOutlinedInput-root': {
                       borderRadius: 3,
                       backgroundColor: 'rgba(102, 126, 234, 0.1)',
                       backdropFilter: 'blur(10px)',
                       border: '2px solid rgba(102, 126, 234, 0.3)'
                     },
                     '& .MuiInputBase-input': {
                       color: '#2c3e50',
                       fontWeight: 'bold',
                       fontSize: '1rem'
                     },
                     '& .MuiInputLabel-root': {
                       fontWeight: 600,
                       color: '#667eea'
                     }
                   }}
                 />
               </Grid>
                               <Grid item xs={12}>
                  <TextField
                    select
                    name="categoryIDs"
                    label="🏷️ Kategoriler (Birden fazla seçebilirsiniz)"
                    value={form.categoryIDs}
                    onChange={handleCategoryChange}
                    fullWidth
                    SelectProps={{ 
                      multiple: true,
                      renderValue: (selected) => (
                         <Box sx={{ display: 'flex', flexWrap: 'wrap', gap: 0.5 }}>
                           {selected.map((value) => {
                             const category = categories.find(c => c.id === value);
                             return (
                               <Chip
                                 key={value}
                                 label={category ? category.name : value}
                                 size="small"
                                 sx={{ 
                                   bgcolor: '#667eea', 
                                   color: 'white',
                                   fontWeight: 'bold',
                                   fontSize: '0.75rem',
                                   borderRadius: 2,
                                   '& .MuiChip-label': {
                                     px: 1.5,
                                     whiteSpace: 'nowrap',
                                     overflow: 'visible'
                                   }
                                 }}
                               />
                             );
                           })}
                         </Box>
                       )
                    }}
                    sx={{
                      '& .MuiOutlinedInput-root': {
                        borderRadius: 3,
                        backgroundColor: 'rgba(255,255,255,0.8)',
                        backdropFilter: 'blur(10px)',
                        transition: 'all 0.3s ease',
                        '&:hover': {
                          backgroundColor: 'rgba(255,255,255,0.9)',
                          transform: 'translateY(-2px)',
                          boxShadow: '0 8px 24px rgba(102, 126, 234, 0.15)'
                        },
                        '&.Mui-focused': {
                          backgroundColor: 'rgba(255,255,255,1)',
                          transform: 'translateY(-2px)',
                          boxShadow: '0 8px 24px rgba(102, 126, 234, 0.2)'
                        }
                      },
                      '& .MuiSelect-select': {
                        color: '#2c3e50',
                        fontWeight: '500',
                        py: 1.5
                      },
                      '& .MuiInputLabel-root': {
                        color: '#2c3e50',
                        fontWeight: '600'
                      },
                      '& .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'rgba(102, 126, 234, 0.3)',
                        borderWidth: '2px'
                      },
                      '&:hover .MuiOutlinedInput-notchedOutline': {
                        borderColor: 'rgba(102, 126, 234, 0.5)'
                      },
                      '&.Mui-focused .MuiOutlinedInput-notchedOutline': {
                        borderColor: '#667eea',
                        borderWidth: '2px'
                      }
                    }}
                  >
                                       {categories.map((category) => (
                      <MenuItem 
                        key={category.id} 
                        value={category.id} 
                        sx={{ 
                          py: 1.5, 
                          minHeight: 'auto',
                          '&:hover': {
                            backgroundColor: 'rgba(102, 126, 234, 0.1)'
                          },
                          '&.Mui-selected': {
                            backgroundColor: 'rgba(102, 126, 234, 0.2)',
                            '&:hover': {
                              backgroundColor: 'rgba(102, 126, 234, 0.3)'
                            }
                          }
                        }}
                      >
                        <Box sx={{ display: 'flex', alignItems: 'center', gap: 1.5, width: '100%' }}>
                          <Box sx={{ 
                            width: 14, 
                            height: 14, 
                            borderRadius: '50%', 
                            bgcolor: '#667eea',
                            flexShrink: 0,
                            border: '2px solid #667eea'
                          }} />
                          <Typography sx={{ 
                            fontSize: '0.9rem',
                            fontWeight: '500',
                            color: '#2c3e50',
                            whiteSpace: 'nowrap',
                            overflow: 'hidden',
                            textOverflow: 'ellipsis',
                            width: '100%'
                          }}>
                            {category.name}
                          </Typography>
                        </Box>
                      </MenuItem>
                    ))}
                 </TextField>
               </Grid>
            </Grid>
          </DialogContent>
          
          <DialogActions sx={{ 
            p: 4, 
            pt: 2,
            background: 'linear-gradient(135deg, #f8f9fa 0%, #ffffff 100%)',
            borderTop: '1px solid rgba(102, 126, 234, 0.1)'
          }}>
            <Button 
              onClick={() => setOpenDialog(false)}
              sx={{ 
                borderRadius: 3,
                px: 4,
                py: 1.5,
                fontWeight: 600,
                fontSize: '1rem',
                color: '#666',
                border: '2px solid #ddd',
                transition: 'all 0.3s ease',
                '&:hover': {
                  borderColor: '#999',
                  backgroundColor: 'rgba(0,0,0,0.05)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 4px 12px rgba(0,0,0,0.1)'
                }
              }}
            >
              ❌ İptal
            </Button>
            <Button
              type="submit"
              variant="contained"
              disabled={loading}
              sx={{ 
                background: 'linear-gradient(135deg, #667eea 0%, #764ba2 100%)',
                '&:hover': { 
                  background: 'linear-gradient(135deg, #5a6fd8 0%, #6a4c93 100%)',
                  transform: 'translateY(-2px)',
                  boxShadow: '0 8px 24px rgba(102, 126, 234, 0.4)'
                },
                '&:disabled': {
                  background: 'linear-gradient(135deg, #ccc 0%, #999 100%)',
                  transform: 'none',
                  boxShadow: 'none'
                },
                borderRadius: 3,
                px: 4,
                py: 1.5,
                fontWeight: 700,
                fontSize: '1rem',
                textTransform: 'none',
                boxShadow: '0 4px 12px rgba(102, 126, 234, 0.3)',
                transition: 'all 0.3s ease'
              }}
            >
              {loading ? (
                <CircularProgress size={24} sx={{ color: 'white' }} />
              ) : (
                editingBook ? '✅ Güncelle' : '📚 Ekle'
              )}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <Snackbar
        open={!!success || !!error}
        autoHideDuration={6000}
        onClose={() => { setSuccess(''); setError(''); }}
        anchorOrigin={{ vertical: 'top', horizontal: 'center' }}
      >
        <Alert 
          severity={success ? 'success' : 'error'} 
          onClose={() => { setSuccess(''); setError(''); }}
          sx={{ borderRadius: 2 }}
        >
          {success || error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default MyPublisher;
