import React, { useState, useEffect, useContext } from 'react';
import {
  Box,
  Typography,
  Paper,
  TextField,
  Button,
  Grid,
  Card,
  CardContent,
  CardActions,
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Alert,
  CircularProgress,
  Chip,
  IconButton,
  Snackbar,
  MenuItem
} from '@mui/material';
import { Add as AddIcon, Edit as EditIcon, Delete as DeleteIcon } from '@mui/icons-material';
import { LoginContext } from '../App';
import { fetchCategories, fetchBooks } from '../api';

const MyPublisher = () => {
  const { isLoggedIn } = useContext(LoginContext);
  const [user, setUser] = useState(null);
  const [categories, setCategories] = useState([]);
  const [books, setBooks] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [openDialog, setOpenDialog] = useState(false);
  const [editingBook, setEditingBook] = useState(null);
  const [form, setForm] = useState({
    title: '',
    author: '',
    summary: '',
    publishDate: '',
    pageCount: '',
    isbn: '',
    publisher: '',
    language: 'Türkçe',
    categoryIDs: []
  });

  useEffect(() => {
    if (isLoggedIn) {
      const userData = JSON.parse(localStorage.getItem('user'));
      console.log('MyPublisher - User Data:', userData); // Debug için
      setUser(userData);
      fetchCategoriesData();
      fetchBooksData();
    }
  }, [isLoggedIn]);

  const fetchCategoriesData = async () => {
    try {
      const response = await fetchCategories();
      setCategories(response.data);
    } catch (error) {
      console.error('Kategoriler yüklenirken hata:', error);
    }
  };

  const fetchBooksData = async () => {
    try {
      const response = await fetchBooks();
      // Sadece bu yayın evinin kitaplarını filtrele
      const userData = JSON.parse(localStorage.getItem('user'));
      const publisherBooks = response.data.filter(book => 
        book.publisher === userData.publisherName
      );
      setBooks(publisherBooks);
    } catch (error) {
      console.error('Kitaplar yüklenirken hata:', error);
    }
  };

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleCategoryChange = (event) => {
    const value = event.target.value;
    setForm({ ...form, categoryIDs: typeof value === 'string' ? value.split(',') : value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    setError('');
    setSuccess('');

    try {
      const userData = JSON.parse(localStorage.getItem('user'));
      const bookData = {
        ...form,
        publisher: userData.publisherName, // Yayın evi adı otomatik
        pageCount: parseInt(form.pageCount) || 0,
        categoryIDs: form.categoryIDs.map(id => parseInt(id))
      };

      if (editingBook) {
        // Kitap güncelleme
        const response = await fetch(`https://localhost:7238/api/book/${editingBook.id}`, {
          method: 'PUT',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(bookData)
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla güncellendi!');
        } else {
          throw new Error('Kitap güncellenirken hata oluştu');
        }
      } else {
        // Yeni kitap ekleme
        const response = await fetch('https://localhost:7238/api/book', {
          method: 'POST',
          headers: { 'Content-Type': 'application/json' },
          body: JSON.stringify(bookData)
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla eklendi!');
        } else {
          throw new Error('Kitap eklenirken hata oluştu');
        }
      }

      setOpenDialog(false);
      setEditingBook(null);
      resetForm();
      fetchBooksData(); // Kitapları yenile

    } catch (error) {
      setError(error.message);
    } finally {
      setLoading(false);
    }
  };

  const handleEdit = (book) => {
    setEditingBook(book);
    setForm({
      title: book.title,
      author: book.author,
      summary: book.summary,
      publishDate: book.publishDate ? book.publishDate.split('T')[0] : '',
      pageCount: book.pageCount?.toString() || '',
      isbn: book.isbn || '',
      publisher: book.publisher,
      language: book.language || 'Türkçe',
      categoryIDs: book.categoryIDs || []
    });
    setOpenDialog(true);
  };

  const handleDelete = async (bookId) => {
    if (window.confirm('Bu kitabı silmek istediğinizden emin misiniz?')) {
      try {
        const response = await fetch(`https://localhost:7238/api/book/${bookId}`, {
          method: 'DELETE'
        });
        if (response.ok) {
          setSuccess('Kitap başarıyla silindi!');
          fetchBooksData();
        } else {
          throw new Error('Kitap silinirken hata oluştu');
        }
      } catch (error) {
        setError(error.message);
      }
    }
  };

  const resetForm = () => {
    setForm({
      title: '',
      author: '',
      summary: '',
      publishDate: '',
      pageCount: '',
      isbn: '',
      publisher: '',
      language: 'Türkçe',
      categoryIDs: []
    });
  };

  const getCategoryNames = (categoryIDs) => {
    if (!categoryIDs || !Array.isArray(categoryIDs)) return [];
    return categoryIDs.map(id => {
      const category = categories.find(c => c.id === id);
      return category ? category.name : '';
    }).filter(name => name);
  };

  if (!isLoggedIn || !user || user.role !== 'publisher') {
    return (
      <Box sx={{ p: 3, textAlign: 'center' }}>
        <Typography variant="h6" color="error">
          Bu sayfaya erişim yetkiniz yok.
        </Typography>
      </Box>
    );
  }

  return (
    <Box sx={{ p: 3, minHeight: '100vh', bgcolor: '#f5f5f5' }}>
      <Typography variant="h4" gutterBottom sx={{ color: '#2c3e50', fontWeight: 'bold', mb: 3 }}>
        🏢 {user.publisherName} - Yayınevim
      </Typography>

      {error && <Alert severity="error" sx={{ mb: 2 }}>{error}</Alert>}
      {success && <Alert severity="success" sx={{ mb: 2 }}>{success}</Alert>}

      <Paper sx={{ p: 3, mb: 3, bgcolor: 'rgba(255,255,255,0.9)', borderRadius: 3 }}>
        <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 2 }}>
          <Typography variant="h6" sx={{ color: '#34495e' }}>
            Yayın Evi Bilgileri
          </Typography>
        </Box>
        <Grid container spacing={2}>
          <Grid item xs={12} md={6}>
            <Typography><strong>Yayın Evi Adı:</strong> {user.publisherName}</Typography>
            <Typography><strong>Yetkili Kişi:</strong> {user.firstName}</Typography>
          </Grid>
          <Grid item xs={12} md={6}>
            <Typography><strong>E-posta:</strong> {user.email}</Typography>
            <Typography><strong>Adres:</strong> {user.address}</Typography>
            {user.website && (
              <Typography><strong>Web Sitesi:</strong> {user.website}</Typography>
            )}
          </Grid>
        </Grid>
      </Paper>

      <Box sx={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', mb: 3 }}>
        <Typography variant="h5" sx={{ color: '#2c3e50', fontWeight: 'bold' }}>
          Kitaplarım ({books.length})
        </Typography>
        <Button
          variant="contained"
          startIcon={<AddIcon />}
          onClick={() => {
            setEditingBook(null);
            resetForm();
            setOpenDialog(true);
          }}
          sx={{
            bgcolor: '#27ae60',
            '&:hover': { bgcolor: '#229954' }
          }}
        >
          Yeni Kitap Ekle
        </Button>
      </Box>

      <Grid container spacing={3}>
        {books.map((book) => (
          <Grid item xs={12} sm={6} md={4} key={book.id}>
            <Card sx={{ height: '100%', display: 'flex', flexDirection: 'column' }}>
              <CardContent sx={{ flexGrow: 1 }}>
                <Typography variant="h6" gutterBottom sx={{ fontWeight: 'bold' }}>
                  {book.title}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  <strong>Yazar:</strong> {book.author}
                </Typography>
                <Typography variant="body2" color="text.secondary" gutterBottom>
                  <strong>Yayın Evi:</strong> {book.publisher}
                </Typography>
                {book.categories && book.categories.length > 0 && (
                  <Box sx={{ mt: 1 }}>
                    {book.categories.map((category, index) => (
                      <Chip
                        key={index}
                        label={category}
                        size="small"
                        sx={{ mr: 0.5, mb: 0.5, bgcolor: '#3498db', color: 'white' }}
                      />
                    ))}
                  </Box>
                )}
              </CardContent>
              <CardActions>
                <IconButton
                  size="small"
                  onClick={() => handleEdit(book)}
                  sx={{ color: '#f39c12' }}
                >
                  <EditIcon />
                </IconButton>
                <IconButton
                  size="small"
                  onClick={() => handleDelete(book.id)}
                  sx={{ color: '#e74c3c' }}
                >
                  <DeleteIcon />
                </IconButton>
              </CardActions>
            </Card>
          </Grid>
        ))}
      </Grid>

      {books.length === 0 && (
        <Paper sx={{ p: 4, textAlign: 'center', bgcolor: 'rgba(255,255,255,0.9)' }}>
          <Typography variant="h6" color="text.secondary">
            Henüz kitap eklenmemiş. İlk kitabınızı eklemek için "Yeni Kitap Ekle" butonuna tıklayın.
          </Typography>
        </Paper>
      )}

      {/* Kitap Ekleme/Düzenleme Dialog */}
      <Dialog open={openDialog} onClose={() => setOpenDialog(false)} maxWidth="md" fullWidth>
        <DialogTitle sx={{ bgcolor: '#3498db', color: 'white' }}>
          {editingBook ? 'Kitap Düzenle' : 'Yeni Kitap Ekle'}
        </DialogTitle>
        <form onSubmit={handleSubmit}>
          <DialogContent sx={{ pt: 2 }}>
            <Grid container spacing={2}>
              <Grid item xs={12} md={6}>
                <TextField
                  name="title"
                  label="Kitap Adı"
                  value={form.title}
                  onChange={handleChange}
                  fullWidth
                  required
                  margin="dense"
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="author"
                  label="Yazar"
                  value={form.author}
                  onChange={handleChange}
                  fullWidth
                  required
                  margin="dense"
                />
              </Grid>
              <Grid item xs={12}>
                <TextField
                  name="summary"
                  label="Özet"
                  value={form.summary}
                  onChange={handleChange}
                  fullWidth
                  multiline
                  rows={3}
                  margin="dense"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="publishDate"
                  label="Yayın Tarihi"
                  type="date"
                  value={form.publishDate}
                  onChange={handleChange}
                  fullWidth
                  margin="dense"
                  InputLabelProps={{ shrink: true }}
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="pageCount"
                  label="Sayfa Sayısı"
                  type="number"
                  value={form.pageCount}
                  onChange={handleChange}
                  fullWidth
                  margin="dense"
                />
              </Grid>
              <Grid item xs={12} md={4}>
                <TextField
                  name="isbn"
                  label="ISBN"
                  value={form.isbn}
                  onChange={handleChange}
                  fullWidth
                  margin="dense"
                />
              </Grid>
              <Grid item xs={12} md={6}>
                <TextField
                  name="language"
                  label="Dil"
                  value={form.language}
                  onChange={handleChange}
                  fullWidth
                  margin="dense"
                />
              </Grid>
                             <Grid item xs={12} md={6}>
                 <TextField
                   name="publisher"
                   label="Yayın Evi"
                   value={user?.publisherName || ''}
                   fullWidth
                   margin="dense"
                   disabled
                   sx={{ 
                     bgcolor: '#f5f5f5',
                     '& .MuiInputBase-input': {
                       color: '#2c3e50',
                       fontWeight: 'bold'
                     }
                   }}
                 />
               </Grid>
              <Grid item xs={12}>
                <TextField
                  select
                  name="categoryIDs"
                  label="Kategoriler"
                  value={form.categoryIDs}
                  onChange={handleCategoryChange}
                  fullWidth
                  margin="dense"
                  SelectProps={{ multiple: true }}
                >
                  {categories.map((category) => (
                    <MenuItem key={category.id} value={category.id}>
                      {category.name}
                    </MenuItem>
                  ))}
                </TextField>
              </Grid>
            </Grid>
          </DialogContent>
          <DialogActions sx={{ p: 2 }}>
            <Button onClick={() => setOpenDialog(false)}>İptal</Button>
            <Button
              type="submit"
              variant="contained"
              disabled={loading}
              sx={{ bgcolor: '#27ae60' }}
            >
              {loading ? <CircularProgress size={24} /> : (editingBook ? 'Güncelle' : 'Ekle')}
            </Button>
          </DialogActions>
        </form>
      </Dialog>

      <Snackbar
        open={!!success || !!error}
        autoHideDuration={6000}
        onClose={() => { setSuccess(''); setError(''); }}
      >
        <Alert severity={success ? 'success' : 'error'} onClose={() => { setSuccess(''); setError(''); }}>
          {success || error}
        </Alert>
      </Snackbar>
    </Box>
  );
};

export default MyPublisher;
